mapper.select:
select ID, PID, NAME, STATUS from DS_COMMON_ORG

	
		 and PID=#{pid} 
		 and PID is null 
	
	 and STATUS=#{status} 

order by STATUS desc, SEQ asc

select f2.ID, f2.DEPLOYID, f2.NAME from DS_FLOW f1
left join DS_FLOW f2 on f2.ALIAS=f1.ALIAS and f2.VNUM>0
where f1.STATUS=1 and f1.VNUM=0 and f1.ALIAS=#{alias}
