INSERT INTO DS_CMS_CATEGORY (ID, PID, SITEID, STATUS, NAME, FOLDER, SCOPE, URL, SEQ, VIEWSITE, PAGEVIEWSITE, METAKEYWORDS, METADESCRIPTION, SUMMARY, RELEASETIME, RELEASESOURCE, RELEASEUSER, IMG, CONTENT) VALUES (14, 10, 5, 1, '领导分工', 'ldfg', 1, '/a/ldfg/index.html', '0', 'show.jsp', '', '', '', NULL, NULL, NULL, NULL, '', '<h2><span style=\"font-family:\'微软雅黑\',sans-serif;\">&nbsp;</span></h2><table cellspacing=\"0\" cellpadding=\"0\" width=\"95%\" border=\"0\"><tbody><tr><td><table cellspacing=\"0\" cellpadding=\"0\" width=\"100%\" border=\"0\"><tbody><tr><td style=\"WIDTH: 127.5pt\"><p><span style=\"font-family:\'??\',serif;color:black;FONT-SIZE: 9pt\"><img height=\"199\" src=\"http://www.gzly.gov.cn/f/img/201608/1472116464327.jpg\" width=\"140\" alt=\"\" /></span></p></td><td style=\"WIDTH: 267.05pt\"><table style=\"WIDTH: 547px; HEIGHT: 33px\" cellspacing=\"0\" cellpadding=\"0\" width=\"547\" border=\"0\"><tbody><tr><td><p><span style=\"font-family:宋体;color:#0066cc;FONT-SIZE: 9pt\">&nbsp; &nbsp; 党委书记、局长：</span><span style=\"font-family: 宋体; font-size: 9pt;\"><span style=\"color:#ff0000;\">刘瑜梅</span></span></p></td></tr><tr><td><p><span style=\"font-family:宋体;color:black;FONT-SIZE: 9pt\">&nbsp; &nbsp; 主持局党委和行政业务全面工作。</span></p></td></tr></tbody></table></td></tr></tbody></table></td></tr></tbody></table><p><span style=\"font-family:\'??\',serif;color:black;FONT-SIZE: 9pt\">&nbsp;</span></p><br /><table cellspacing=\"0\" cellpadding=\"0\" width=\"95%\" border=\"0\"><tbody><tr><td><table cellspacing=\"0\" cellpadding=\"0\" width=\"100%\" border=\"0\"><tbody><tr><td style=\"WIDTH: 127.5pt\"><p><span style=\"font-family: ??, serif; font-size: 9pt;\"><img height=\"194\" alt=\"\" src=\"http://www.gzly.gov.cn/f/img/201505/1432704333959.jpg\" width=\"144\" /></span></p></td><td><table cellspacing=\"0\" cellpadding=\"0\" width=\"100%\" border=\"0\"><tbody><tr><td><p><span style=\"font-family:宋体;color:#0066cc;FONT-SIZE: 9pt\">党委委员、副局长：</span><span style=\"font-family:宋体;color:red;FONT-SIZE: 9pt\">谭爱英</span></p></td></tr><tr><td><p><span style=\"font-family: 宋体; font-size: 9pt;\">分管市场推广处、旅行社管理处、组织人事处、机关党委、及其他专项工作，指导广州旅游促进会工作。</span></p></td></tr></tbody></table></td></tr></tbody></table></td></tr></tbody></table><p><span style=\"font-family:\'??\',serif;color:black;FONT-SIZE: 9pt\">&nbsp;</span></p><table cellspacing=\"0\" cellpadding=\"0\" width=\"95%\" border=\"0\"><tbody><tr><td><table cellspacing=\"0\" cellpadding=\"0\" width=\"100%\" border=\"0\"><tbody><tr><td style=\"WIDTH: 127.5pt\"><p><span style=\"font-family:\'??\',serif;color:black;FONT-SIZE: 9pt\"><img height=\"191\" alt=\"\" src=\"http://www.gzly.gov.cn/f/img/201505/1432704438480.jpg\" width=\"144\" /></span></p></td><td><table cellspacing=\"0\" cellpadding=\"0\" width=\"100%\" border=\"0\"><tbody><tr><td><p><span style=\"font-family:宋体;color:#0066cc;FONT-SIZE: 9pt\">党委委员、副局长：</span><span style=\"font-family:宋体;color:red;FONT-SIZE: 9pt\">陈永亮</span></p></td></tr><tr><td><p><span style=\"font-family:宋体;color:black;FONT-SIZE: 9pt\">分管资源开发处、规划发展处、离退休干部工作处、广州城市旅游问询服务中心、及其他专项工作。</span></p></td></tr></tbody></table></td></tr></tbody></table></td></tr></tbody></table><p><span style=\"font-family:\'??\',serif;color:black;FONT-SIZE: 9pt\">&nbsp;</span></p><table cellspacing=\"0\" cellpadding=\"0\" width=\"95%\" border=\"0\"><tbody><tr><td><table cellspacing=\"0\" cellpadding=\"0\" width=\"100%\" border=\"0\"><tbody><tr><td style=\"WIDTH: 127.5pt\"><p><span style=\"font-family:\'??\',serif;color:black;FONT-SIZE: 9pt\"><img height=\"207\" alt=\"\" src=\"http://www.gzly.gov.cn/f/img/201505/1432704470117.jpg\" width=\"143\" /></span></p></td><td><table style=\"WIDTH: 463px; HEIGHT: 33px\" cellspacing=\"0\" cellpadding=\"0\" width=\"463\" border=\"0\"><tbody><tr><td><p><span style=\"font-family:宋体;color:#0066cc;FONT-SIZE: 9pt\">党委委员、副局长：</span><span style=\"font-family:宋体;color:red;FONT-SIZE: 9pt\">吴青松</span></p></td></tr><tr><td><p><span style=\"font-family:宋体;color:black;FONT-SIZE: 9pt\"></span>分管办公室、法规与统计处、旅游饭店管理处、广州市旅游局机关服务中心、及其他专项工作。<span style=\"font-family:宋体;color:black;FONT-SIZE: 9pt\"></span></p></td></tr></tbody></table></td></tr></tbody></table></td></tr></tbody></table><p><span style=\"font-family:\'??\',serif;color:black;FONT-SIZE: 9pt\">&nbsp;</span><span style=\"font-family:\'??\',serif;color:black;FONT-SIZE: 9pt\">&nbsp;</span></p><table cellspacing=\"0\" cellpadding=\"0\" width=\"100%\" border=\"0\"><tbody><tr><td style=\"WIDTH: 127.5pt\"><p><span style=\"font-family:\'??\',serif;color:black;FONT-SIZE: 9pt\"><img height=\"196\" alt=\"\" src=\"http://www.gzly.gov.cn/f/img/201505/1432704643780.jpg\" width=\"145\" /></span></p></td><td><table cellspacing=\"0\" cellpadding=\"0\" width=\"100%\" border=\"0\"><tbody><tr><td><p><span style=\"font-family:宋体;color:#0066cc;FONT-SIZE: 9pt\">副巡视员：</span><span style=\"font-family:宋体;color:red;FONT-SIZE: 9pt\">徐颖</span></p></td></tr><tr><td><p><span style=\"font-family:宋体;color:black;FONT-SIZE: 9pt\">分管行业培训指导处、广州市旅游质量监督管理所、及其他专项工作。</span></p></td></tr></tbody></table></td></tr></tbody></table><p style=\"TEXT-ALIGN: left\"><span style=\"font-family:\'微软雅黑\',sans-serif;color:black;\"><span style=\"font-size:16px;\">&nbsp;</span></span></p>');
