INSERT INTO DS_CMS_CATEGORY (ID, PID, SITEID, STATUS, NAME, FOLDER, SCOPE, URL, SEQ, VIEWSITE, PAGEVIEWSITE, METAKEYWORDS, METADESCRIPTION, SUMMARY, RELEASETIME, RELEASESOURCE, RELEASEUSER, IMG, CONTENT) VALUES (60, 50, 5, 1, '其他', 'othersqt', 0, '/a/othersqt/index.html', '0', 'list.jsp', 'page.jsp', '', '', NULL, NULL, NULL, NULL, '', '');
INSERT INTO DS_CMS_CATEGORY (ID, PID, SITEID, STATUS, NAME, FOLDER, SCOPE, URL, SEQ, VIEWSITE, PAGEVIEWSITE, METAKEYWORDS, METADESCRIPTION, SUMMARY, RELEASETIME, RELEASESOURCE, RELEASEUSER, IMG, CONTENT) VALUES (61, NULL, 5, 1, '公开指南', 'gkzn', 1, '/a/gkzn/index.html', '1', 'show.jsp', '', '', '', NULL, NULL, NULL, NULL, '', '<p align=\"center\"><span style=\"font-family:SimSun;font-size:16px;color:#0020a0;\">广州市旅游局政府信息公开指南</span></p><p><span style=\"font-family:SimSun;font-size:16px;color:#000000;\">　　根据《中华人民共和国政府信息公开条例》（以下简称《条例》），本机关制作和从公民、法人或者其他组织获取并由本机关保存的政府信息，除依法免予公开的外，由本机关负责主动公开或者依公民、法人或者其他组织的申请予以提供。</span></p><p><span lang=\"EN-US\"><o:p><span style=\"font-family:SimSun;font-size:16px;color:#000000;line-height: 150%;\">　　为了更好地提供政府信息公开服务，本机关编制了《广州市旅游局政府信息公开指南》（以下简称《指南》）。需要获得本机关政府信息公开服务的公民、法人或者其他组织，建议阅读本《指南》。</span></o:p></span></p><p><span style=\"font-family: SimSun;\"><span style=\"color:#000000;\"><span style=\"font-size: 11pt;\"><span style=\"font-family:SimSun;font-size:16px;\">　　本《指南》每年更新一次。公民、法人或者其他组织可以在“中国广州政府门户网站”（</span><span style=\"font-family:SimSun;font-size:16px;\"><a href=\"http://www.gz.gov.cn\">http://www.gz.gov.cn</a></span></span><span style=\"font-size: 11pt;\"><span style=\"font-family:SimSun;font-size:16px;\">）和本机关政府网站（<a href=\"http://www.gz.gov.cn\">http://www.gz.gov.cn</a>）上查阅本《指南》，也可以到本《指南》指定发放点广州市旅游局 (地址：广州东风西路140号13楼)领取。</span></span></span></span></p><p><span style=\"font-family:SimSun;font-size:16px;color:#000000;\">一、信息分类和编排体系</span></p><p><span style=\"font-family:SimSun;font-size:16px;color:#000000;\">　　本机关在职责范围内，负责主动或依申请公开下列各类政府信息：</span></p><p><span style=\"font-family:SimSun;font-size:16px;color:#000000;\">（一）机构职能。</span></p><p><span style=\"font-family:SimSun;font-size:16px;color:#000000;\">　　主要包括：本机关机构设置及主要职能情况；机构领导及分工情况；内设机构设置及职能情况；下（直）属单位设置及职能情况等。</span></p><p><span style=\"font-family:SimSun;font-size:16px;color:#000000;\">（二）规章文件。</span></p><p><span style=\"font-family:SimSun;font-size:16px;color:#000000;\">　　主要包括：旅游政策法规；由本市制定地方旅游法规规章；以本机关名义发布或者本机关作为主办部门与其他部门联合发布的规范性文件等。</span></p><p><span style=\"font-family:SimSun;font-size:16px;color:#000000;\">（三）规划计划。</span></p><p><span style=\"font-family:SimSun;font-size:16px;color:#000000;\">　　主要包括：专项旅游规划、阶段性工作计划、工作重点安排等。</span></p><p><span style=\"font-family:SimSun;font-size:16px;color:#000000;\">（四）业务工作。</span></p><p><span style=\"font-family:SimSun;font-size:16px;color:#000000;\">　　主要包括：本部门受上级部门委托的行政许可事项、依据、条件、数量、程序、期限以及申请需要提交的全部材料目录及办理情况；政府集中采购项目的目录、标准及实施情况；重大旅游建设项目建设情况；行业规范与管理、国际与国内市场开发、行业培训指导、质量监督与执法检查情况；突发公共事件的应急预案、预警信息及应对情况等。</span></p><p><span style=\"font-family:SimSun;font-size:16px;color:#000000;\">（五）统计数据。</span></p><p><span style=\"font-family:SimSun;font-size:16px;color:#000000;\">　　主要包括：专项统计报告、年鉴等。</span></p><p><span style=\"font-family:SimSun;font-size:16px;color:#000000;\">（六）其他。</span></p><p><span style=\"font-family:SimSun;font-size:16px;color:#000000;\">　　主要包括：本机关重要会议、活动的主要情况；人事任免事项；本机关公务员及事业单位工作人员招考录用计划、程序、结果等，以及本机关职责范围内依法应当公开的其他信息。</span></p><p><span style=\"font-family:SimSun;font-size:16px;color:#000000;\">　　为方便公民、法人或者其他组织查询本机关主动和依申请公开的政府信息，本机关编制了《广州市旅游局政府信息公开目录》（以下简称《目录》）。本机关在编排以上各类政府信息时，按照业务和信息类别，划分为1-3级类目。公民、法人或者其他组织可以在“中国广州政府门户网站”（<a href=\"http://www.gz.gov.cn/\"><span style=\"font-family:SimSun;font-size:16px;\">http://www.gz.gov.cn</span></a></span><span style=\"font-family:SimSun;font-size:16px;color:#000000;\">）或本机关政府网站（</span><a href=\"http://www.gzly.gov.cn\"><span style=\"font-family:SimSun;font-size:16px;\">http://www.gzly.gov.cn</span></a><span style=\"font-family:SimSun;font-size:16px;color:#000000;\">）上查阅该《目录》，也可以到广州市旅游局（地址：广州市东风西路140号13楼）查阅。</span></p><p><span style=\"font-family:SimSun;font-size:16px;color:#000000;\">二、获取形式</span></p><p><span style=\"font-family:SimSun;font-size:16px;color:#000000;\">（一）主动公开。</span></p><p><span style=\"font-family:SimSun;font-size:16px;color:#000000;\">　　本机关主动公开的政府信息范围，详见《目录》。</span></p><p><span style=\"font-family:SimSun;font-size:16px;color:#000000;\">● 公开形式</span></p><p><span style=\"font-family:SimSun;font-size:16px;color:#000000;\">　　本机关政府信息公开主要采取政府网站网上公开形式。本机关网上信息公开网址为</span><a href=\"http://www.gzly.gov.cn\"><span style=\"font-family:SimSun;font-size:16px;\">http://www.gzly.gov.cn</span></a><span style=\"font-family:SimSun;font-size:16px;color:#000000;\">。</span></p><p><span style=\"font-family:SimSun;font-size:16px;color:#000000;\">　　本机关还将采用以下辅助方式主动公开政府信息：</span></p><p><span style=\"font-family:SimSun;font-size:16px;color:#000000;\">　　1．通过政府公报、新闻发布会等形式和报纸、广播、电视等公共媒体主动公开政府信息；</span></p><p><span style=\"font-family:SimSun;font-size:16px;color:#000000;\">　　2．本机关在广州市东风西路140号13楼设有政府信息公开查阅点，公民、法人或者其他组织可以到该查阅点查阅本机关公开的政府信息；</span></p><p><span style=\"font-family:SimSun;font-size:16px;color:#000000;\">　　3．本机关在广州市东风西路140号13楼大厅设有政府信息公告栏、电子屏幕，主动公开政府信息。</span></p><p><span style=\"font-family:SimSun;font-size:16px;color:#000000;\">　　本机关网上公开的信息，除机构职能类、政策法规类信息以外，网上留存的期限为2年。超过留存期的信息，本机关不再继续通过网上公开，公民、法人或者其他组织可以到本机关设立的公共查阅点（广州市东风西路140号13楼）查阅。&nbsp;</span></p><p><span style=\"font-family:SimSun;font-size:16px;color:#000000;\">● 公开时限</span></p><p><span style=\"font-family:SimSun;font-size:16px;color:#000000;\">　　本机关主动公开的政府信息，自政府信息形成或者变更之日20个工作日内予以公开。法律、法规对政府信息公开的期限另有规定的，从其规定。</span></p><p><span style=\"font-family:SimSun;font-size:16px;color:#000000;\">（二）依申请公开。</span></p><p><span style=\"font-family:SimSun;font-size:16px;color:#000000;\">　　除本机关主动公开的政府信息外，公民、法人或者其他组织可以根据自身生产、生活、科研等特殊需要，向本机关申请获取相关政府信息。</span></p><p><span style=\"font-family:SimSun;font-size:16px;color:#000000;\">　　本机关政府信息公开申请受理机构（见本《指南》第三条），负责受理公民、法人或者其他组织向本机关提出的政府信息公开申请。</span></p><p><span style=\"font-family:SimSun;font-size:16px;color:#000000;\">● 提出申请</span></p><p><span style=\"font-family:SimSun;font-size:16px;color:#000000;\">　　向本机关申请获取政府信息的，应当书面填写《广州市旅游局政府信息公开申请表》（以下简称《申请表》，见附件1）。《申请表》可以在受理机构处领取，也可以在本机关网站上下载电子版，复制有效。</span></p><p><span style=\"font-family:SimSun;font-size:16px;color:#000000;\">　　申请人对申请获取信息的描述请尽量详尽、明确；若有可能，请提供该信息的标题、发布时间、文号或者其他有助于本机关确定信息内容的提示。</span></p><p><span style=\"font-family:SimSun;font-size:16px;color:#000000;\">1. 本机关受理书面提交的政府信息公开申请。</span></p><p><span style=\"font-family:SimSun;font-size:16px;color:#000000;\">　　除申请人当面提交《申请表》外，申请人通过信函方式提出申请的，请在信封左下角注明“政府信息公开申请”的字样；申请人通过电报、传真方式提出申请的，请相应注明“政府信息公开申请”的字样。</span></p><p><span style=\"font-family:SimSun;font-size:16px;color:#000000;\">2. 本机关受理通过互联网提交的政府信息公开申请。</span></p><p><span style=\"font-family:SimSun;font-size:16px;color:#000000;\">　　申请人可通过互联网在本机关网站上填写电子版《申请表》后发送至</span><a href=\"mailto:lyjbgs8200@163.com\"><span style=\"font-family:SimSun;font-size:16px;color:#000000;\">lyjbgs8200@163.com</span></a><span style=\"font-family:SimSun;font-size:16px;color:#000000;\">电子信箱，向本机关提交政府信息公开申请。《申请表》网址为</span><a href=\"http://www.gzly.gov.cn\"><span style=\"font-family:SimSun;font-size:16px;color:#000000;\">http://www.gzly.gov.cn</span></a><span style=\"font-family:SimSun;font-size:16px;color:#000000;\">。</span></p><p><span style=\"font-family:SimSun;font-size:16px;color:#000000;\">　　申请人向本机关申请获取与自身相关的注册登记、社会保障等方面政府信息时，应当出示有效身份证件或证明文件，当面向本机关提交书面申请。</span></p><p><span style=\"font-family:SimSun;font-size:16px;color:#000000;\">本机关不直接受理通过电话、短消息等方式提出的申请，但申请人可以通过电话咨询相应的服务业务。</span></p><p><span style=\"font-family:SimSun;font-size:16px;color:#000000;\">● 申请处理</span></p><p><span style=\"font-family:SimSun;font-size:16px;color:#000000;\">　　本机关收到公民、法人或者其他组织提出的政府信息公开申请后，根据需要，通过相应方式对申请人身份进行核对。</span></p><p><span style=\"font-family:SimSun;font-size:16px;color:#000000;\">　　本机关收到申请后，将从形式上对申请的要件是否完备进行审查，对于要件不完备的申请予以退回，要求申请人补正信息。</span></p><p><span style=\"font-family:SimSun;font-size:16px;color:#000000;\">　　对申请人提出的政府信息公开申请，本机关将根据不同情况分别作出答复，详见本机关处理政府信息公开申请流程图（见附件2）。</span></p><p><span style=\"font-family:SimSun;font-size:16px;color:#000000;\">　　本机关办理申请人政府信息公开申请时，能够当场答复的，将当场答复；不能当场答复的，自收到申请之日起15个工作日内予以答复；确需延长答复期限的，经本单位政府信息公开工作机构负责人同意，延长答复时间不超过15个工作日，并告知申请人。《条例》另有规定的，从其规定。</span></p><p><span style=\"font-family:SimSun;font-size:16px;color:#000000;\">　　本机关依申请提供信息时，除不应当公开的内容外，根据掌握该信息的实际状态进行提供，不对信息进行加工、统计、研究、分析或者其他处理。</span></p><p><span style=\"font-family:SimSun;font-size:16px;color:#000000;\">● 收费标准</span></p><p><span style=\"font-family:SimSun;font-size:16px;color:#000000;\">　　本机关依申请提供政府信息的收费标准依照国家物价与财政部门制定的标准执行，收取的费用全部上缴财政。</span></p><p><span style=\"font-family:SimSun;font-size:16px;color:#000000;\">　　申请公开政府信息的公民，确有经济困难的，本人可向本机关提出减免相关费用的申请，并填写《申请表》相关栏目。</span></p><p><span style=\"font-family:SimSun;font-size:16px;color:#000000;\">三、政府信息公开工作机构</span></p><p><span style=\"font-family:SimSun;font-size:16px;color:#000000;\">　　本机关政府信息公开工作机构为：广州市旅游局办公室</span></p><p><span style=\"font-family:SimSun;font-size:16px;color:#000000;\">　　办公地址：广州市东风西路140号13楼<br />　　邮政编码：510170<br />　　办公时间：8:30-12:00 14:00-17:30（工作日）<br />　　联系电话：020-81078226<br />　　传&nbsp;&nbsp;&nbsp; 真：81078234<br />　　电子信箱：</span><a href=\"mailto:lyjbgs8200@163.com\"><span style=\"font-family:SimSun;font-size:16px;color:#000000;\">lyjbgs8200@163.com</span></a></p><p><span style=\"color:#000000;\"></span><span style=\"font-family:SimSun;font-size:16px;\">&nbsp;</span></p><p><span style=\"color:#000000;\"></span><span style=\"font-family:SimSun;font-size:16px;\">&nbsp;</span></p><p><span style=\"font-family:SimSun;font-size:16px;color:#000000;\">本机关政府信息公开申请受理机构为：</span></p><p><span style=\"font-family:SimSun;font-size:16px;color:#000000;\">1．广州市旅游局规划发展处</span></p><blockquote style=\"margin-right: 0px;\" dir=\"ltr\"><p><span style=\"font-family:SimSun;font-size:16px;color:#000000;\">办公地址：广州市东风西路140号13楼<br />邮政编码：510170<br />办公时间：8:30-12:00 14:00-17:30（工作日）<br />联系电话：020-81078253<br />传&nbsp;&nbsp;&nbsp; 真：81078234<br />电子信箱：</span></p></blockquote><p><span style=\"font-family:SimSun;font-size:16px;color:#000000;\">2．广州市旅游局法规与统计处</span></p><blockquote style=\"margin-right: 0px;\" dir=\"ltr\"><p><span style=\"font-family:SimSun;font-size:16px;color:#000000;\">办公地址：广州市东风西路140号13楼<br />邮政编码：510170<br />办公时间：8:30-12:00 14:00-17:30（工作日）<br />联系电话：020-81078298<br />传&nbsp;&nbsp;&nbsp; 真：81078237<br />电子信箱：</span><a href=\"mailto:A81078237@126.com\"><span style=\"font-family:SimSun;font-size:16px;color:#000000;\">A81078237@126.com</span></a></p></blockquote><p><span style=\"font-family:SimSun;font-size:16px;color:#000000;\">3．广州市旅游局资源开发处</span></p><blockquote style=\"margin-right: 0px;\" dir=\"ltr\"><p><span style=\"font-family:SimSun;font-size:16px;color:#000000;\">办公地址：广州市东风西路140号13楼<br />邮政编码：510170<br />办公时间：8:30-12:00 14:00-17:30（工作日）<br />联系电话：020-81078259<br />传&nbsp;&nbsp;&nbsp; 真：81078254<br />电子信箱：</span><a href=\"mailto:Gzly8270@126.com\"><span style=\"font-family:SimSun;font-size:16px;color:#000000;\">Gzly8270@126.com</span></a></p></blockquote><p><span style=\"font-family:SimSun;font-size:16px;color:#000000;\">4．广州市旅游局市场推广处</span></p><blockquote style=\"margin-right: 0px;\" dir=\"ltr\"><p><span style=\"font-family:SimSun;font-size:16px;color:#000000;\">办公地址：广州市东风西路140号13楼<br />邮政编码：510170<br />办公时间：8:30-12:00 14:00-17:30（工作日）<br />联系电话：020-81078263<br />传&nbsp;&nbsp;&nbsp; 真：81078264<br />电子信箱：</span><a href=\"mailto:Tourism81078258@126.com\"><span style=\"font-family:SimSun;font-size:16px;color:#000000;\">Tourism81078258@126.com</span></a></p></blockquote><p><span style=\"font-family:SimSun;font-size:16px;color:#000000;\">5．广州市旅游局旅游饭店管理处</span></p><blockquote style=\"margin-right: 0px;\" dir=\"ltr\"><p><span style=\"font-family:SimSun;font-size:16px;color:#000000;\">办公地址：广州市东风西路140号13楼<br />邮政编码：510170<br />办公时间：8:30-12:00 14:00-17:30（工作日）<br />联系电话：020-81078246<br />传&nbsp;&nbsp;&nbsp; 真：81078255<br />电子信箱：</span><a href=\"mailto:fdglch@163.com\"><span style=\"font-family:SimSun;font-size:16px;color:#000000;\">fdglch@163.com</span></a></p></blockquote><p><span style=\"font-family:SimSun;font-size:16px;color:#000000;\">6．广州市旅游局旅行社管理处</span></p><blockquote style=\"margin-right: 0px;\" dir=\"ltr\"><p><span style=\"font-family:SimSun;font-size:16px;color:#000000;\">办公地址：广州市东风西路140号13楼<br />邮政编码：510170<br />办公时间：8:30-12:00 14:00-17:30（工作日）<br />联系电话：020-81073276<br />传&nbsp;&nbsp;&nbsp; 真：81078214<br />电子信箱：</span><a href=\"mailto:lxsglc@126.com\"><span style=\"font-family:SimSun;font-size:16px;color:#000000;\">lxsglc@126.com</span></a></p></blockquote><p><span style=\"font-family:SimSun;font-size:16px;color:#000000;\">7．广州市旅游局行业培训指导处</span></p><blockquote style=\"margin-right: 0px;\" dir=\"ltr\"><p><span style=\"font-family:SimSun;font-size:16px;color:#000000;\">办公地址：广州市东风西路140号13楼<br />邮政编码：510170<br />办公时间：8:30-12:00 14:00-17:30（工作日）<br />联系电话：020-81078241<br />传&nbsp;&nbsp;&nbsp; 真：81078241<br />电子信箱：</span><a href=\"mailto:peixunchu8241@126.com\"><span style=\"font-family:SimSun;font-size:16px;color:#000000;\">peixunchu8241@126.com</span></a></p></blockquote><p><span style=\"font-family:SimSun;font-size:16px;color:#000000;\">8．广州市旅游局组织人事处（纪委办公室、检察室、机关党委办公室与其合署办公）</span></p><blockquote style=\"margin-right: 0px;\" dir=\"ltr\"><p><span style=\"font-family:SimSun;font-size:16px;color:#000000;\">办公地址：广州市东风西路140号13楼<br />邮政编码：510170<br />办公时间：8:30-12:00 14:00-17:30（工作日）<br />联系电话：020-81078201<br />传&nbsp;&nbsp;&nbsp; 真：81078203<br />电子信箱：</span><a href=\"mailto:81078203@163.com\"><span style=\"font-family:SimSun;font-size:16px;color:#000000;\">81078203@163.com</span></a></p></blockquote><p><span style=\"font-family:SimSun;font-size:16px;color:#000000;\">四、其他<br />公民、法人或者其他组织认为本机关提供的与其自身相关的政府信息记录不准确的，可以向本机关提出更正申请，并提供证据材料。本机关将根据申请作出相应处理，并告知申请人。</span></p><p><span style=\"font-family:SimSun;font-size:16px;color:#000000;\">公民、法人或者其他组织认为本机关未依法履行政府信息公开义务的，可以向本机关投诉举报（投诉电话：12345，传真：81078234，电子邮箱：</span><a href=\"mailto:lyjbgs8200@163.com\"><span style=\"font-family:SimSun;font-size:16px;\">lyjbgs8200@163.com</span></a><span style=\"font-family:SimSun;font-size:16px;\">，办公地址：广州市东风西路140号13楼，邮政编码：510170，接待投诉时间：工作日8:30-12:00 14:00-17:30）。 公民、法人或其他组织也可以向上级行政机关、监察机关或者政府信息公开工作主管部门举报。</span></p><p><span style=\"font-family:SimSun;font-size:16px;color:#000000;\">公民、法人或者其他组织认为行政机关在政府信息公开工作中的具体行政行为侵犯其合法权益的，公民、法人和其他组织可以依法申请行政复议或提起行政诉讼。</span></p><p><span style=\"font-family:SimSun;font-size:16px;color:#000000;\">附件：<br />1．<a href=\"/f/res/yb.doc\">广州市旅游局政府信息公开申请表（样表）</a><br />2．<a href=\"/f/res/lct.doc\">广州市旅游局处理政府信息公开申请流程图</a></span></p><p align=\"right\"><span style=\"font-family:SimSun;font-size:16px;color:#000000;\">广州市旅游局<br />2014年4月28日</span></p>');
INSERT INTO DS_CMS_CATEGORY (ID, PID, SITEID, STATUS, NAME, FOLDER, SCOPE, URL, SEQ, VIEWSITE, PAGEVIEWSITE, METAKEYWORDS, METADESCRIPTION, SUMMARY, RELEASETIME, RELEASESOURCE, RELEASEUSER, IMG, CONTENT) VALUES (62, NULL, 5, 1, '依申请公开', 'ysqgk', 1, '/a/ysqgk/index.html', '2', 'show_iframe.jsp', '', '', '', '', '2016-12-01 15:58:35', '/zw/zwclient/ysqgk/ysqgk.html', '', '', '本栏目直接点发布即可');
