INSERT INTO DS_CMS_PAGE (ID, SITEID, CATEGORYID, STATUS, TITLE, URL, METAKEYWORDS, METADESCRIPTION, SUMMARY, RELEASETIME, RELEASESOURCE, RELEASEUSER, IMG, IMGTOP, PAGETOP, CONTENT) VALUES (977, 5, 45, 1, '广州市旅游局2012年部门预算', '/a/czys/977.html', '', '', '广州市旅游局2012年部门预算', '2012-02-08 21:04:45', NULL, NULL, '', 0, 0, '<p>详情请看附件</p><p><a href=\"http://www.gzly.gov.cn/f/file/201505/1432645522328.pdf\">广州市旅游局2012年部门预算编制说明.pdf</a></p>');
INSERT INTO DS_CMS_PAGE (ID, SITEID, CATEGORYID, STATUS, TITLE, URL, METAKEYWORDS, METADESCRIPTION, SUMMARY, RELEASETIME, RELEASESOURCE, RELEASEUSER, IMG, IMGTOP, PAGETOP, CONTENT) VALUES (978, 5, 42, 1, '广州市旅游局举办2015年高级导游研修班 ', '/a/gzdt2/978.html', '', '', '广州市旅游局举办2015年高级导游研修班 ', '2015-04-08 10:13:17', NULL, NULL, '/DsEp/html/gzly/html/f/res/index/3.jpg', 1, 1, '<p style=\"TEXT-ALIGN: center\"><span style=\"font-family:宋体;color:black;FONT-SIZE: 9pt\"><img style=\"WIDTH: 225pt; HEIGHT: 149.25pt; VISIBILITY: visible; mso-wrap-style: square\" class=\"wordImage\" alt=\"\" src=\"http://www.gzly.gov.cn/f/img/201505/1432693981803.jpg\" width=\"auto\" height=\"auto\" /></span></p><p style=\"TEXT-ALIGN: center\"><span style=\"font-family:宋体;color:black;FONT-SIZE: 9pt\"><img style=\"WIDTH: 225pt; HEIGHT: 149.25pt; VISIBILITY: visible; mso-wrap-style: square\" class=\"wordImage\" alt=\"\" src=\"http://www.gzly.gov.cn/f/img/201505/1432693991948.jpg\" width=\"auto\" height=\"auto\" /></span></p><p style=\"TEXT-ALIGN: left\"><span style=\"font-family:宋体;color:black;FONT-SIZE: 9pt\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span><span style=\"font-family:宋体;color:black;FONT-SIZE: 9pt\">根据国务院【2014】31号文的精神，为适应广州建设国家旅游综合改革试点城市的发展要求，结合2015年广州市旅游工作会议的工作部署，进一步提高高级导游人员的综合素质，同时有效提高高级导游员在导游队伍中的引领示范作用，并为建立导游人员培训专家库储备师资力量，我局于3月17日在举办了2015年广州市高级导游研修班，共有49名高级导游出席。</span></p><p style=\"TEXT-ALIGN: left\"><span style=\"font-family:宋体;color:black;FONT-SIZE: 9pt\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span><span style=\"font-family:宋体;color:black;FONT-SIZE: 9pt\">此次研修班采取了专题讲座和主题研讨相结合的方式进行。上午邀请了广东省博物馆馆长助理、研究馆员王芳博士讲授《广州与海上丝绸之路》专题，并就如何深度讲解广州历史文化提出了很好的建议和意见。下午来自旅行社、旅游院校、导游管理公司等高级导游代表围绕导游专业素质的提升、认真履行高级导游在旅游行业发展的特殊作用、个人担当以及导游人员培训专家库等议题展开研讨。我局分管培训工作的徐颖副巡视员对研修班做了点评总结，对主题研讨发言给予充分肯定，赞同大家提出关于建立信息平台（微信群、QQ群），开展高级导游景区讲解志愿活动等提议。</span></p><p style=\"TEXT-ALIGN: left\"><span style=\"font-family:宋体;color:black;FONT-SIZE: 9pt\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span><span style=\"font-family:宋体;color:black;FONT-SIZE: 9pt\">为提高高级导游理论水平，我局为此次研修班高级导游发放了《高级导游职业能力发展与服务品牌创建》、《北京路文化核心区导游词》、全国、省、市旅游工作会议材料等资料。</span></p><p style=\"TEXT-ALIGN: left\"><span style=\"font-family:宋体;color:black;FONT-SIZE: 9pt\">&nbsp;</span></p><p><table border=\"1\" cellspacing=\"0\" cellpadding=\"0\" width=\"400\"><tbody><tr><td style=\"WIDTH: 402pt; HEIGHT: 20.25pt\" colspan=\"2\"><p style=\"TEXT-ALIGN: left\"><span style=\"font-family:宋体;FONT-SIZE: 12pt\">出席3月17日高级导游研修班名单（排名不分先后） </span></p></td></tr><tr><td style=\"HEIGHT: 20.25pt\"><p style=\"TEXT-ALIGN: center\"><span style=\"font-family:宋体;FONT-SIZE: 12pt\">序号</span></p></td><td style=\"HEIGHT: 20.25pt\"><p style=\"TEXT-ALIGN: center\"><span style=\"font-family:宋体;FONT-SIZE: 12pt\">姓名</span></p></td><td style=\"HEIGHT: 20.25pt\"><p style=\"TEXT-ALIGN: center\"><span style=\"font-family:宋体;FONT-SIZE: 12pt\">旅行社简称</span></p></td></tr><tr><td style=\"HEIGHT: 18.75pt\"><p style=\"TEXT-ALIGN: center\"><span style=\"font-family:宋体;FONT-SIZE: 12pt\">1</span></p></td><td style=\"HEIGHT: 18.75pt\"><p style=\"TEXT-ALIGN: center\"><span style=\"font-family:宋体;FONT-SIZE: 12pt\">梁洪沛</span></p></td><td style=\"HEIGHT: 18.75pt\"><p style=\"TEXT-ALIGN: center\"><span style=\"font-family:宋体;FONT-SIZE: 12pt\">广东香江旅游</span></p></td></tr><tr><td style=\"HEIGHT: 18.75pt\"><p style=\"TEXT-ALIGN: center\"><span style=\"font-family:宋体;FONT-SIZE: 12pt\">2</span></p></td><td style=\"HEIGHT: 18.75pt\"><p style=\"TEXT-ALIGN: center\"><span style=\"font-family:宋体;FONT-SIZE: 12pt\">郑文丽</span></p></td><td style=\"HEIGHT: 18.75pt\"><p style=\"TEXT-ALIGN: center\"><span style=\"font-family:宋体;FONT-SIZE: 12pt\">广东省中国旅行社</span></p></td></tr><tr><td style=\"HEIGHT: 18.75pt\"><p style=\"TEXT-ALIGN: center\"><span style=\"font-family:宋体;FONT-SIZE: 12pt\">3</span></p></td><td style=\"HEIGHT: 18.75pt\"><p style=\"TEXT-ALIGN: center\"><span style=\"font-family:宋体;FONT-SIZE: 12pt\">江澜</span></p></td><td style=\"HEIGHT: 18.75pt\"><p style=\"TEXT-ALIGN: center\"><span style=\"font-family:宋体;FONT-SIZE: 1