INSERT INTO DS_CMS_PAGE (ID, SITEID, CATEGORYID, STATUS, TITLE, URL, METAKEYWORDS, METADESCRIPTION, SUMMARY, RELEASETIME, RELEASESOURCE, RELEASEUSER, IMG, IMGTOP, PAGETOP, CONTENT) VALUES (980, 5, 42, 1, '千里送教黔南州乡村旅游专题培训顺利结束', '/a/gzdt2/980.html', '', '', '千里送教黔南州乡村旅游专题培训顺利结束', '2015-04-28 10:34:24', NULL, NULL, '/DsEp/html/gzly/html/f/res/index/3.jpg', 1, 1, '<div><table border=\"0\" cellspacing=\"0\" cellpadding=\"0\"><tbody><tr><td><div><table border=\"0\" cellspacing=\"0\" cellpadding=\"0\"><tbody><tr><td><p style=\"TEXT-ALIGN: left\"><span style=\"font-family:宋体;color:black;FONT-SIZE: 9pt\">2015</span><span style=\"font-family:宋体;color:black;FONT-SIZE: 9pt\">年4月13日广州市旅游局邀请广东社科院旅游研究所陈南江研究员、增城市旅游发展中心史寿山主任等专家赴黔南州开展乡村旅游专题培训送教上门活动。 </span></p><p style=\"TEXT-ALIGN: center\"><span style=\"font-family:宋体;color:black;FONT-SIZE: 9pt\"><img style=\"WIDTH: 278.25pt; HEIGHT: 225pt; VISIBILITY: visible; mso-wrap-style: square\" class=\"wordImage\" src=\"http://www.gzly.gov.cn/f/img/201505/1432694224703.png\" width=\"auto\" height=\"auto\" alt=\"\" /></span></p><p style=\"TEXT-ALIGN: left\"><span style=\"font-family:宋体;color:black;FONT-SIZE: 9pt\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-family:宋体;color:black;FONT-SIZE: 9pt\">为期3天的“2015年黔南州乡村旅游专题培训班”在黔南州都匀市五月花酒店开班，黔南州旅游局局长王明亮等领导出席本次培训的开班仪式并讲话。陈南江博士和史寿山主任作为主讲老师。黔南州各县市分管领导、旅游系统及重点旅游乡镇的分管领导和干部150余人参加了培训。</span></p><p style=\"TEXT-ALIGN: center\"><span style=\"font-family:宋体;color:black;FONT-SIZE: 9pt\"><img style=\"WIDTH: 278.25pt; HEIGHT: 225pt; VISIBILITY: visible; mso-wrap-style: square\" class=\"wordImage\" src=\"http://www.gzly.gov.cn/f/img/201505/1432694233926.png\" width=\"auto\" height=\"auto\" alt=\"\" /></span></p><p style=\"TEXT-ALIGN: left\"><span style=\"font-family:宋体;color:black;FONT-SIZE: 9pt\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-family:宋体;color:black;FONT-SIZE: 9pt\">通过此次强化学习，为促进当地乡村旅游发展，提高乡村旅游管理者及从业人员的整体素质，帮助大家理清乡村旅游发展思路，提高驾驭市场、加快推进发展的能力，推动黔南州乡村旅游业持续健康快续发展，以及推进全州“快旅慢游”体系建设，都起到了积极的作用。</span></p><p style=\"TEXT-ALIGN: center\"><span style=\"font-family:宋体;color:black;FONT-SIZE: 9pt\"><img style=\"WIDTH: 278.25pt; HEIGHT: 225pt; VISIBILITY: visible; mso-wrap-style: square\" class=\"wordImage\" src=\"http://www.gzly.gov.cn/f/img/201505/1432694242518.png\" width=\"auto\" height=\"auto\" alt=\"\" /></span></p><p style=\"TEXT-ALIGN: left\"><span style=\"font-family:宋体;color:black;FONT-SIZE: 9pt\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-family:宋体;color:black;FONT-SIZE: 9pt\">培训以现场授课及情景模拟的形式，列举鲜活事例及旅游接待过程中遇到的问题，特别是在福泉市黄丝江边布依寨的现场观摩课，现场点评，帮助群众找到破解发展难题的有效措施和思路。在规划建设、行业管理及市场推广等方面都不同程度上受到了启发，学员们在轻松的氛围中更好地学到专业知识和技能，为进一步提升黔南州的旅游接待水平奠定了良好的基础。</span></p></td></tr></tbody></table></div></td></tr></tbody></table></div>');
INSERT INTO DS_CMS_PAGE (ID, SITEID, CATEGORYID, STATUS, TITLE, URL, METAKEYWORDS, METADESCRIPTION, SUMMARY, RELEASETIME, RELEASESOURCE, RELEASEUSER, IMG, IMGTOP, PAGETOP, CONTENT) VALUES (981, 5, 42, 1, '广州市旅游局组织2015年导游岗前培训工作试点', '/a/gzdt2/981.html', '', '', '广州市旅游局组织2015年导游岗前培训工作试点', '2015-04-08 10:40:48', NULL, NULL, '/DsEp/html/gzly/html/f/res/index/3.jpg', 1, 1, '<div><p style=\"TEXT-ALIGN: left\"><span style=\"font-family:宋体;color:black;FONT-SIZE: 9pt\">根据国家旅游局《导游人员管理实施办法》的有关规定，结合我市建设全国旅游综合改革试点城市的需要，广州市旅游局于3月23日-25日举办了一期导游岗前培训试点。邀请高级导游或金牌导游为教员，对80名我市已通过全国导游人员资格考试的人员进行岗前培训。 </span></p><p style=\"TEXT-ALIGN: left\"><span style=\"font-family:宋体;color:black;FONT-SIZE: 9pt\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span><span style=\"font-family:宋体;color:black;FONT-SIZE: 9pt\">培训内容主要有广州旅游概况、文明旅游、应急和安全、导游技巧等，以导游工作流程模拟操作和旅游景点模拟讲解为主。培训方式为理论授课、经验交流、实地培训、带团实际操作，采用小班教学的方式，每10名学员配1名金牌导游或高级导游分组现场辅导。</span></p><p style=\"TEXT-ALIGN: left\"><span style=\"font-family:宋体;color:black;FONT-SIZE: 9pt\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 3</span><span style=\"font-family:宋体;color:black;FONT-SIZE: 9pt\">月23日上午重点讲授了广州旅游概况（包括历史、文化、经济、社会、美食及商贸情况）、文明旅游及旅游安全等理论内容；下午重点讲授了地接及全陪带团工作流程讲解、导游带团技巧、应急和安全演练等内容，同时穿插有旅游心理学知识点。</span></p><p style=\"TEXT-ALIGN: left\"><span style=\"font-family:宋体;color:black;FONT-SIZE: 9pt\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 3</span><span style=\"font-family:宋体;color:black;FONT-SIZE: 9pt\">月24日，实地培训选择一条广州一日游线路，行程为白云山(雕塑公园)→陈家祠→午餐→黄埔军校旧址(校本部、孙总理纪念碑)→花城广场。具体由辅导老师对沿途风光讲解及景点讲解重点提出要求，并选取部分做示范讲解，随后由学员进行讲解，老师给予点评。</span></p><p style=\"TEXT-ALIGN: left\"><span style=\"font-family:宋体;color:black;FONT-SIZE: 9pt\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 3</span><span style=\"font-family:宋体;color:black;FONT-SIZE: 9pt\">月25日，学员分成8个小组，由现场辅导员进行培训指导、经验交流和讨论，各小组学员代表轮流模拟讲解、并进行随机抽查考核及讲评。</span></p><p style=\"TEXT-ALIGN: left\"><span style=\"font-family:宋体;color:black;FONT-SIZE: 9pt\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span><span style=\"font-family:宋体;color:black;FONT-SIZE: 9pt\">此次培训，树立了受训新导游的职业荣誉感，提高了职业道德素养，提高了他们的带团技巧和实际操作能力，提升了大家的基本素质，获得了参训人员的一致好评。学员们纷纷表示，此次培训想新导游之所想，急新导游之所急，为每位学员配发了《新导游必备手册》（中国旅游出版社）。通过培训，学员进一步了解了广州的历史沿革、改革开放后广州的变化、作用和地位，熟悉了广州的风土人情、岭南文化及重要旅游资源；基本掌握了沿途风光导览及景区定点讲解的方法和技巧，获取了实际操作经验和应急处理能力，并希望市旅游局能继续组织此类培训活动。</span></p><p style=\"TEXT-ALIGN: left\"><span style=\"font-family:宋体;color:black;FONT-SIZE: 9pt\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 3</span><span style=\"font-family:宋体;color:black;FONT-SIZE: 9pt\">月25日下午，在总结研讨会上，广州市旅游局副巡视员徐颖就培训时间、培训课程内容设计和培训方法等方面作了系统阐述。各组辅导老师表示，由市旅游局牵头组织导游岗前培训是我市旅游业界历史上第一次，对提升我市导游从业人员的综合素质、增强新导游适应工作岗位的能力等诸多方面意义重大，建议未来进一步完善培训方法，不断提高导游岗前培训的有效性和针对性。</span></p><p style=\"TEXT-ALIGN: center\"><span style=\"font-family:宋体;color:black;FONT-SIZE: 9pt\"><img style=\"WIDTH: 337.5pt; HEIGHT: 207pt; VISIBILITY: visible; mso-wrap-style: square\" class=\"wordImage\" src=\"http://www.gzly.gov.cn/f/img/201505/1432694537032.png\" width=\"auto\" height=\"auto\" alt=\"\" /></span></p><p style=\"TEXT-ALIGN: center\"><strong><span style=\"font-family:宋体;color:black;FONT-SIZE: 9pt\">1.</span><span style=\"font-family:宋体;color:black;FONT-SIZE: 9pt\">理论授课</span></strong></p><p style=\"TEXT-ALIGN: center\"><strong><span style=\"font-family:宋体;color:black;FONT-SIZE: 9pt\"><img style=\"WIDTH: 337.5pt; HEIGHT: 195pt; VISIBILITY: visible; mso-wrap-style: square\" class=\"wordImage\" src=\"http://www.gzly.gov.cn/f/img/201505/1432694544913.png\" width=\"auto\" height=\"auto\" alt=\"\" /></span></strong></p><p style=\"TEXT-ALIGN: center\"><strong><span style=\"font-family:宋体;color:black;FONT-SIZE: 9pt\">2.</span><span style=\"font-family:宋体;color:black;FONT-SIZE: 9pt\">带团模拟实践</span></strong></p><p style=\"TEXT-ALIGN: center\"><strong><span style=\"font-family:宋体;color:black;FONT-SIZE: 9pt\"><img style=\"WIDTH: 337.5pt; HEIGHT: 189.75pt; VISIBILITY: visible; mso-wrap-style: square\" class=\"wordImage\" src=\"http://www.gzly.gov.cn/f/img/201505/1432694554627.png\" width=\"auto\" height=\"auto\" alt=\"\" /></span></strong></p><p style=\"TEXT-ALIGN: center\"><strong><span style=\"font-family:宋体;color:black;FONT-SIZE: 9pt\">3.</span><span style=\"font-family:宋体;color:black;FONT-SIZE: 9pt\">实地培训</span></strong></p><p style=\"TEXT-ALIGN: center\"><strong><span style=\"font-family:宋体;color:black;FONT-SIZE: 9pt\"><img style=\"WIDTH: 337.5pt; HEIGHT: 202.5pt; VISIBILITY: visible; mso-wrap-style: square\" class=\"wordImage\" src=\"http://www.gzly.gov.cn/f/img/201505/1432694565282.png\" width=\"auto\" height=\"auto\" alt=\"\" /></span></strong></p><p style=\"TEXT-ALIGN: center\"><strong><span style=\"font-family:宋体;color:black;FONT-SIZE: 9pt\">4.</span><span style=\"font-family:宋体;color:black;FONT-SIZE: 9pt\">景点讲解</span></strong></p><p style=\"TEXT-ALIGN: center\"><strong><span style=\"font-family:宋体;color:black;FONT-SIZE: 9pt\"><img style=\"WIDTH: 337.5pt; HEIGHT: 187.5pt; VISIBILITY: visible; mso-wrap-style: square\" class=\"wordImage\" src=\"http://www.gzly.gov.cn/f/img/201505/1432694575717.png\" width=\"auto\" height=\"auto\" alt=\"\" /></span></strong></p><p style=\"TEXT-ALIGN: center\"><strong><span style=\"font-family:宋体;color:black;FONT-SIZE: 9pt\">5.</span><span style=\"font-family:宋体;color:black;FONT-SIZE: 9pt\">分组讨论</span></strong></p><p style=\"TEXT-ALIGN: center\"><strong><span style=\"font-family:宋体;color:black;FONT-SIZE: 9pt\"><img style=\"WIDTH: 337.5pt; HEIGHT: 193.5pt; VISIBILITY: visible; mso-wrap-style: square\" class=\"wordImage\" src=\"http://www.gzly.gov.cn/f/img/201505/1432694589197.png\" width=\"auto\" height=\"auto\" alt=\"\" /></span></strong></p><p style=\"TEXT-ALIGN: center\"><strong><span style=\"font-family:宋体;color:black;FONT-SIZE: 9pt\">6.</span><span style=\"font-family:宋体;color:black;FONT-SIZE: 9pt\">总结研讨</span></strong></p><p style=\"TEXT-ALIGN: left\"><span style=\"font-family:宋体;color:black;FONT-SIZE: 9pt\">&nbsp;</span></p><p style=\"TEXT-ALIGN: left\"><span style=\"font-family:宋体;color:black;FONT-SIZE: 9pt\">&nbsp;</span></p><p style=\"TEXT-ALIGN: left\"><span style=\"font-family:宋体;color:black;FONT-SIZE: 9pt\">&nbsp;</span></p><p style=\"TEXT-ALIGN: center\"><span style=\"font-family:宋体;color:black;FONT-SIZE: 9pt\">附：主要培训老师简介</span></p><p style=\"TEXT-ALIGN: center\"><span style=\"font-family:宋体;color:black;FONT-SIZE: 9pt\">（以下排名不分先后）</span></p><p style=\"TEXT-ALIGN: center\"><span style=\"font-family:宋体;color:black;FONT-SIZE: 9pt\">&nbsp;</span></p><p style=\"TEXT-ALIGN: left\"><span style=\"font-family:宋体;color:black;FONT-SIZE: 9pt\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 1.</span><span style=\"font-family:宋体;color:black;FONT-SIZE: 9pt\">陈蕾：广东省旅游职业技术学校旅游服务与管理专业部副主任，广州中航国旅广东旅校分公司总经理，高级导游、广东省旅游协会导游分会副秘书长。2005年获得“广东省明星导游”称号；2006年获得“广州市首届金牌导游员”荣誉称号；2010年被聘为“亚运志愿者精品课程培训教师”；2010年被授予“南粤优秀导游员”称号。</span></p><p style=\"TEXT-ALIGN: left\"><span style=\"font-family:宋体;color:black;FONT-SIZE: 9pt\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 2.</span><span style=\"font-family:宋体;color:black;FONT-SIZE: 9pt\">冯小飞：广州市旅游商务职业学校导游专业教师，高级导游，优秀领队，广州市导游形象大赛优秀奖，广东省导游技能大赛优秀辅导教师，南湖国旅、广东女子学院等优秀培训师，全国导游教师授课二等奖。</span></p><p style=\"TEXT-ALIGN: left\"><span style=\"font-family:宋体;color:black;FONT-SIZE: 9pt\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 3</span><span style=\"font-family:宋体;color:black;FONT-SIZE: 9pt\">．蔡高明：南鹰假期中心总经理，高级英文导游,中级英文翻译，拥有美国导游证；1975年12月至1996年6月在广东省中国国际旅行社任高级英文导游,经理; 1996年6月至1999年12月在广东省旅游局任科长; 1999年12月至2002年5月在南湖旅游中心任经理、主任;2002年5月至今在广东外贸旅行社、美国加州米尔布雷市一家旅行社分别任总经理和主管。</span></p><p style=\"TEXT-ALIGN: left\"><span style=\"font-family:宋体;color:black;FONT-SIZE: 9pt\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 4.</span><span style=\"font-family:宋体;color:black;FONT-SIZE: 9pt\">江澜：第二届广东省旅游协会导游分会执行会长，广东中旅职业培训学校高级导游，担任各类导游大赛评委，参与导游资格考试系列教材《全国导游基础知识》的编辑工作。</span></p><p style=\"TEXT-ALIGN: left\"><span style=\"font-family:宋体;color:black;FONT-SIZE: 9pt\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 5.</span><span style=\"font-family:宋体;color:black;FONT-SIZE: 9pt\">谢萍：广东青旅推广预订部经理、工会副主席，高级导游。学历本科，广州地区旅行社行业协会副秘书长、广东省旅行社协会副秘书长、广东省导游分会副秘书长。</span></p><p style=\"TEXT-ALIGN: left\"><span style=\"font-family:宋体;color:black;FONT-SIZE: 9pt\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 6.</span><span style=\"font-family:宋体;color:black;FONT-SIZE: 9pt\">张静：广之旅经理助理，2005年获“广之旅金牌导游”称号；2006年获得广州市金牌导游称号；2008获“广之旅鼎级金牌导游”称号；2014年获广之旅优秀员工称号。</span></p><p style=\"TEXT-ALIGN: left\"><span style=\"font-family:宋体;color:black;FONT-SIZE: 9pt\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 7.</span><span style=\"font-family:宋体;color:black;FONT-SIZE: 9pt\">冼少华：现任广之旅部门主任，经济师、学历本科，2007年获颁“广之旅金牌导游”称号；2008年获颁“广之旅鼎级金牌导游”称号；2008年获得广州市金牌导游称号。</span></p><p style=\"TEXT-ALIGN: left\"><span style=\"font-family:宋体;color:black;FONT-SIZE: 9pt\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 8.</span><span style=\"font-family:宋体;color:black;FONT-SIZE: 9pt\">韩宏侠：南湖国旅专业领队，2005年获“陕西省十佳导游”；2006年“陕西省服务明星”；2009年“广州市金牌导游”；2010年南湖国旅“优秀导游”。</span></p><p style=\"TEXT-ALIGN: left\"><span style=\"font-family:宋体;color:black;FONT-SIZE: 9pt\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 9.</span><span style=\"font-family:宋体;color:black;FONT-SIZE: 9pt\">杨媚：国旅专职西班牙语专职导游、翻译，负责接待西班牙语旅行团、商务团队；交易会期间多次担任随身翻译；全国第十批支援西藏导游，获得西藏自治区优秀个人称号；2009年“广州市金牌导游”。</span></p><span style=\"font-family:宋体;color:black;FONT-SIZE: 9pt\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 10.</span><span style=\"font-family:宋体;color:black;FONT-SIZE: 9pt\">龙志锴：岭南国旅中级导游、领队，曾在2009年获“广州市金牌导游”称号。</span></div>');
INSERT INTO DS_CMS_PAGE (ID, SITEID, CATEGORYID, STATUS, TITLE, URL, METAKEYWORDS, METADESCRIPTION, SUMMARY, RELEASETIME, RELEASESOURCE, RELEASEUSER, IMG, IMGTOP, PAGETOP, CONTENT) VALUES (982, 5, 42, 1, '广州市旅游局大事记(2014年四月份) ', '/a/gzdt2/982.html', '', '', '广州市旅游局大事记(2014年四月份) ', '2014-05-27 10:44:17', NULL, NULL, '', 0, 1, '<span style=\"font-family:宋体;color:black;FONT-SIZE: 9pt\">广&nbsp;州&nbsp;市&nbsp;旅&nbsp;游&nbsp;局&nbsp;大&nbsp;事&nbsp;记 <br /><br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 二〇一四年四月 <br />　 <br />9日-14日&nbsp;&nbsp;△派员参加国家旅游局在澳门和香港举行的2014年“美丽中国之旅”港澳地区主题宣传推广活动。 <br /><br />11日&nbsp;&nbsp;△协助昆明市旅游发展委员会来穗举办“山石冠天下，风情醉国人”云南石林旅游推介会。 <br /><br />17日-20日&nbsp;&nbsp;△派员参加湖南省岳阳市人民政府、湖南省旅游局、中国生态文明研究与促进会生态旅游分会在岳阳市举办的“高铁遇上游轮旅游（岳阳）结盟活动”。 <br /><br />22日-25日&nbsp;&nbsp;△派员参加广深珠旅游联盟在贵州黔南州组织的旅游踩线考察活动。 <br /><br />26日　△会同清远市旅游局主办清远旅游（广州）推介会暨广清一体化?微生活2014清远精品嘉年华活动。 <br /><br />29日　△市政协港澳台侨外事委员会会同我局召开“广州国际化营商环境改善”专题调研会议。 <br />29日　△组织局机关、旅游协会及区（县）旅游局参加全省旅游行业服务监管工作视频会议。 <br /><br />30日　△派员参加广州程启招标代理公司组织的“广州‘72小时过境免签政策’海外网站宣传项目”的开标、评标会议。 <br /><br />是月&nbsp;&nbsp;△签订广州白云机场、澳大利亚悉尼机场和新西兰奥克兰机场“72小时过境免签”政策宣传广告发布合同。确定巴黎、温哥华、伦敦机场“72小时过境免签”政策宣传广告招标代理公司。 <br />△派员参加国家及全省旅游规划发展工作电视电话会议。 <br />△组织老字号协会和旅游商品企业参加第六届中国国际旅游商品博览会。 <br />△启动广州旅游集散中心试点工作，赴广州塔实地考察设立旅游集散中心的服务内容和空间布局，召开广州旅游集散中心试点工作协调会。 <br />△继续推进大学城提升计划各项工作，重新设计大学城旅游线路，制作大学城旅游全景图，编印大学城旅游宣传资料。&nbsp; <br />△印制《“360看广州&nbsp;&nbsp;广州一日游”广州旅游导图》15万份，派发给广州地区各大星级酒店、相关旅行社及广州旅游问询中心等单位。 <br />△开展北京路文化核心区导游词编制工作。 <br />△撰写《敢为人先——改革开放广东一千个第一》、《全国第一家合资企业——广州康泰国际旅行社有限公司》等专题史料。 <br />△做好人大代表建议和政协委员提案的接收分办和督办工作。共收到建议和提案38件，办结28件。 <br />△完成2013年度旅行社信息统计审核工作及2014年第一季度旅游统计分析工作。 <br />△批准成立旅行社6家，办理13家旅行社变更事项备案，督促新批5家出境游组团社落实增存旅游服务质量保证金要求。 <br />△完成对广州市安冬邦德国际旅游有限公司不缴纳旅游服务质量保证金案的调查取证，依法吊销该公司旅行社业务经营许可证。 <br />△开展广州旅游市场专项整治联合行动，出动执法36人次，检查企业15家，取缔非法经营旅游业务的经营网点2个，收缴虚假旅游宣传广告6000余份。 <br />△处理各类旅游投诉及咨询举报共129宗，结案68宗，协调旅行社向消费者退（赔）人民币22358元。 <br />△积极推进对口帮扶梅州、清远旅游业发展工作。召开局长办公会议，确定重点帮扶项目，明确任务分工。组织赴清远市协调相关工作，争取市帮扶清远指挥部资金支持。 <br />△督促指导旅游企业做好节前安全检查，本月共对40家旅行社、13家A级景区、2家星级饭店进行安全检查。 </span>');
INSERT INTO DS_CMS_PAGE (ID, SITEID, CATEGORYID, STATUS, TITLE, URL, METAKEYWORDS, METADESCRIPTION, SUMMARY, RELEASETIME, RELEASESOURCE, RELEASEUSER, IMG, IMGTOP, PAGETOP, CONTENT) VALUES (983, 5, 42, 1, '广州市旅游局大事记（2014年八月份）', '/a/gzdt2/983.html', '', '', '广州市旅游局大事记（2014年八月份）', '2014-09-09 10:50:26', NULL, NULL, '', 0, 1, '<span style=\"font-family:宋体;color:black;FONT-SIZE: 9pt\">&nbsp;广&nbsp;州&nbsp;市&nbsp;旅&nbsp;游&nbsp;局&nbsp;大&nbsp;事&nbsp;记 <br /><br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 二〇一四年八月 <br />　 <br />5日&nbsp;&nbsp;△组织召开“广州一日游”工作座谈会，听取广之旅等5家旅行社相关工作情况报告及意见建议，对下一步工作提出具体要求。 <br /><br />5日-6日&nbsp;&nbsp;△会同清远、韶关旅游局，在郑州、西安举办&nbsp;“品花城风韵?享漂流激情?观丹霞世遗”—2014广清韶（西安、郑州）旅游推介会。 <br /><br />6日-8日&nbsp;&nbsp;△派员参加南方航空有限公司广州至纽约国际航线首航团，赴纽约参加旅游相关活动。 <br /><br />11日&nbsp;&nbsp;△在英国伦敦机场刊出“广州72小时过境免签政策”宣传灯箱广告。 <br /><br />12日&nbsp;&nbsp;△会同市农业局，推荐增城市申报国家观光休闲农业示范市，红山村、宝趣玫瑰世界申报国家观光休闲农业示范点。 <br /><br />21日-22日&nbsp;&nbsp;△局领导班子赴市委参加市党纪政纪法纪培训。 <br />26日&nbsp;&nbsp;△派员参加由市发改委召开的《打造华南游艇中心，培育广州经济新平台》和《大力发挥游艇经济，促进先进制造业、商务服务业、休闲旅游业、文化体育产业协同发展的建议》市政协重点提案办理工作座谈会。 <br /><br />27日-29日&nbsp;&nbsp;△派员赴黑龙江参加国家旅游局组织的学习贯彻《国务院关于促进旅游改革发展的若干意见》全国旅游局长研讨班。 <br /><br />是月&nbsp;&nbsp;△推进2014年市战略性主导产业发展资金旅游项目相关工作，委托第三方机构组织专家对15个备选项目进行考察与评审，制定、上报项目安排计划，派员参加由市发改委、财政局共同组织的2015年广州市战略性主导产业发展资金计划工作会议。 <br />△协调研究景区旅游大巴停放问题，组织相关区旅游局、旅游景区召开会议，会同荔湾区旅游局现场考察陈家祠景区，组织交委、交警、各区政府等相关部门以及相关景区召开工作协调会。 <br />△指导北京路文化核心区、香江健康山谷创4A级景区，对珠江琶醍文化创意艺术区、星期八小镇进行创3A级景区现场验收。 <br />△组织8家旅游单位赴济南参加“2014中国?山东国际旅游交易会”，获组委会颁发“最佳组织奖”；组织广州地区酒店行业协会、广州老字号协会等32家单位参加广州博览会；派员参加广东省旅游局举办的“2014中国（广东）国际旅游产业博览会”。 <br />△协助内蒙古自治区兴安盟旅游局在广州举办旅游推介会，协助贵州省黔南州州委、州政府代表团赴广州开展“贵州省黔南州（广州）投资环境暨项目推介会”和“黔南州产业项目招商专题座谈会”。 <br />△组织召开出境游组团社工作会议，制定《关于进一步规范旅行社经营行为的通知》下发全市旅行社，进一步规范旅行社经营行为，促进行业健康发展。 <br />△会同市交委运管局召开旅游集散中心试点建设动员会，指导试点建设单位完善规划建设方案，起草旅游集散中心宣传方案，协调解决广州塔建设旅游集散中心的客运资质问题。 <br />△协调落实大学城旅游接待服务中心场地；设立大学城北驿站和广外大学城驿站旅游问询点；举办大学城亲子修学游首发仪式；印刷大学城旅游线路宣传折页3款4,500份；更新大学城旅游全景图；做好大学城提升计划工作阶段性总结。 <br />△组织旅游专业讲师赴贵州省荔波县，为当地旅游业进行旅游专业知识培训；协调组织景区景点从业人员形象礼仪及讲解服务培训班。 <br />△指导清远市旅游局按计划做好“三连一阳”旅游总体规划、旅游市场营销等工作，做好清远梅州有关旅游扶贫工作。赴对口帮扶增城市正果镇圭湖村对贫困户进行中秋慰问。 <br />△制定《广州市旅游行业防控埃博拉疫情工作方案》，安排部署旅游行业埃博拉疫情联防联控工作，会同广州出入境检验检疫局组织涉外导游、领队旅行健康防护知识专题培训。积极协调、妥善处置8月20日广之旅东莞分社大巴车侧翻事故。本月共对25家旅行社、1家A级景区、6家星级饭店进行安全检查。 <br />△批准成立旅行社5家；办理旅行社变更事项备案1家、旅行社委托代理招徕旅游者业务备案15宗；完成2家旅行社诚信旅行社测评及3家旅行社出境游组团社资质测评；办理导游IC卡业务530张。 <br />△出动旅游市场监督检查32人次，检查涉嫌违法经营旅游业务点11个，处理各类旅游投诉及咨询举报共228宗，结案146宗，协调旅行社向消费者退（赔）人民币24857元。 <br />△组织开展局编制政府部门权责清单工作。组织贯彻中央八项规定严肃财经纪律和“小金库”专项治理自查自纠活动，相关情况书面报市财政局。 <br /><br /></span>');
INSERT INTO DS_CMS_PAGE (ID, SITEID, CATEGORYID, STATUS, TITLE, URL, METAKEYWORDS, METADESCRIPTION, SUMMARY, RELEASETIME, RELEASESOURCE, RELEASEUSER, IMG, IMGTOP, PAGETOP, CONTENT) VALUES (984, 5, 42, 1, '广州市旅游局大事记（2014年九月份） ', '/a/gzdt2/984.html', '', '', '广州市旅游局大事记（2014年九月份） ', '2014-10-09 10:51:36', NULL, NULL, '', 0, 1, '<span style=\"font-family:宋体;color:black;FONT-SIZE: 9pt\">广&nbsp;州&nbsp;市&nbsp;旅&nbsp;游&nbsp;局&nbsp;大&nbsp;事&nbsp;记&nbsp;<br /><br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 二〇一四年九月 <br /><br />5日&nbsp;&nbsp;△派员赴对口帮扶梅州市松口镇铜琶村对贫困户和村干部进行中秋慰问。 <br /><br />5日-6日&nbsp;&nbsp;△王东副市长率领广州旅游业界代表团，赴鞍山开展旅游学习交流活动，与鞍山市签署两地旅游合作协议。 <br /><br />10日&nbsp;&nbsp;△派员参加市人大建议工作座谈会，研究讨论人大代表提出的《创建广州文化航母，推动文化品牌塑造》建议。 <br /><br />10日-16日&nbsp;&nbsp;△局主要领导率队，组织广州地区部分骨干旅行社赴甘孜州考察旅游线路，组织旅游工作交流对接。 <br /><br />19日-21日&nbsp;&nbsp;△组织我市8家主要旅游企业参加“中国西安丝绸之路国际旅游博览会”，获组委会颁发的“最佳人气奖”。 <br /><br />22日&nbsp;&nbsp;△组织召开广州“72小时过境免签”政策中转旅游服务柜台协调会议。 <br /><br />23日&nbsp;&nbsp;△派员参加2014年“市政府工作部门为市政协委员提供知情问政咨询服务活动”。 <br /><br />25日&nbsp;&nbsp;△王东副市长在市政府冯军副秘书长陪同下，到我局调研，听取工作情况汇报，就今后广州旅游业发展提出具体要求。 <br /><br />29日&nbsp;&nbsp;△王东副市长带领市旅游局、安监局、质监局、公安消防局等市直相关部门赴动物园、白云山景区进行安全生产及登革热防控工作检查督查，并召开旅游行业国庆节前安全生产座谈会。 <br /><br />30日&nbsp;&nbsp;△招玉芳副省长率队，赴番禺宝墨园开展国庆节前旅游安全生产检查。 <br /><br />是月&nbsp;&nbsp;△调整广州地区诚信旅行社评定活动组织委员会、广州市旅游饭店星级评定委员会及广州市旅游景区质量等级评定委员会组织成员；参加广州塔创建国家5A级旅游景区研讨会；对珠江琶醍啤酒文化创意艺术区、星期8小镇两家拟通过国家3A级旅游景区验收单位进行公示；指导香江健康山谷等景区开展创A工作。 <br />△在法国巴黎、英国伦敦、俄罗斯莫斯科、德国法兰克福、加拿大温哥华等5个海外国际机场分别投放&nbsp;“72小时过境免签政策”宣传广告。 <br />△“华南五市”（广州、清远、肇庆、桂林、贺州）旅游联盟赴石家庄市和洛阳市，举办“华南五市”山水休闲旅游黄金专线推介会；广州、佛山、肇庆旅游（发展）局联合组织三地旅游业界及媒体代表，赴南宁、贵阳举办旅游推介会；组织广州地区10家旅游企业，参加2014北京国际商务及会奖旅游展(CIBTM&nbsp;&nbsp;2014)，并做广州会奖旅游“目的地演讲”专场推介；协助青岛市旅游局来穗举办“红瓦绿树?碧海蓝天—追梦青岛”旅游专题推介会。 <br />△完成旅游集散中心建设规划设计并呈报王东副市长；协调开通旅游班线，策划旅游集散中心旅游套票产品；邀请景区协会、饭店协会和旅行社协会会员单位进驻集散中心；启动旅游集散中心工程建设。 <br />△研究广州旅游大数据库建设工作方案，召开旅游企业座谈会，开展智慧旅游第二阶段调研。 <br />△召开“十一黄金周”期间住宿、景点抽样调查培训会议。 <br />△下发《广州市旅游局关于印发〈旅行社分支机构备案规范〉的通知》，组织各区（县级市）旅游行政管理部门工作人员培训。 <br />△协助省星评委完成广信江湾酒店、远洋宾馆、广东胜利宾馆、广东大厦、皇家国际饭店、流花宾馆、华厦大酒店、迎宾馆四星级饭店评定性复核工作。 <br />△召开北京路文化核心区导游词评审会。 <br />△组织广州地区2014年度第二次全国导游人员资格考试笔试。 <br />△举办景区从业人员岭南文化艺术欣赏与宣传培训班。 <br />△批准成立旅行社2家；办理旅行社变更事项备案6家、旅行社委托代理招徕旅游者业务备案26宗；完成2家旅行社诚信旅行社测评及3家旅行社出境游组团社资质测评。 <br />△出动旅游市场监督检查40人次，检查经营旅游业务点24个，驱散非法派发旅游宣传广告人员30余名，收缴虚假旅游宣传广告1.3万余份。处理各类旅游投诉及咨询举报共210宗，结案138宗，协调旅行社向消费者退（赔）人民币124567元。 <br />△校核、公示局机关及局下属事业单位机构及人员信息；制定局编制政府部门权责清单工作实施方案，并报王东副市长。 <br />△成立局登革热防控领导小组，制定防控应急预案，及时下发通知，召开旅游行业防控登革热工作会议，邀请市疾控中心专家进行防控登革热培训，加大督导检查力度，全面推进防控工作。 <br />△开展2014年廉洁文化活动。派员参加由市计生局组织的计生工作者培训班，做好迎接2014年度计生目标责任制考核准备工作。 </span>');
INSERT INTO DS_CMS_PAGE (ID, SITEID, CATEGORYID, STATUS, TITLE, URL, METAKEYWORDS, METADESCRIPTION, SUMMARY, RELEASETIME, RELEASESOURCE, RELEASEUSER, IMG, IMGTOP, PAGETOP, CONTENT) VALUES (985, 5, 42, 1, '广州市旅游局大事记（2014年六月份）', '/a/gzdt2/985.html', '', '', '广州市旅游局大事记（2014年六月份）', '2014-07-09 10:52:39', NULL, NULL, '', 0, 1, '<span style=\"font-family:宋体;color:black;FONT-SIZE: 9pt\">广&nbsp;州&nbsp;市&nbsp;旅&nbsp;游&nbsp;局&nbsp;大&nbsp;事&nbsp;记 <br /><br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 二〇一四年六月 <br />　 <br />5日&nbsp;&nbsp;△协助昆明市旅游发展委员会来穗举办2014“春之昆明?避暑胜地”旅游推介洽谈会。 <br /><br />6日&nbsp;&nbsp;△组织各区（县级市）旅游局、A级和重点景区召开旅游资源开发暨规划工作会议。 <br /><br />9日-13日&nbsp;&nbsp;△派员参加广州市公务员考录面试工作。 <br /><br />11日-15日&nbsp;&nbsp;△组织广之旅、广州塔等12家旅游企业赴香港参加第28届香港国际旅游展及第9届商务会奖旅游展（ITE&nbsp;&amp;&nbsp;MICE&nbsp;2014）。 <br /><br />17日&nbsp;&nbsp;△组织2014年度内审员培训，各区（县级市）旅游局及星级酒店约280人参加培训。 <br /><br />19日&nbsp;&nbsp;△派员参加南沙国际邮轮码头综合体项目研讨论证会。 <br /><br />25日-27日&nbsp;&nbsp;△组织乡村旅游景区参加2014年广州市美丽乡村第一期培训班。 <br />26日-30日&nbsp;&nbsp;△派员赴马来西亚参加第六届TPO论坛。 <br /><br />26日-30日&nbsp;&nbsp;△会同清远市旅游局以广清旅游统一形象参加2014北京国际旅游博览会。 <br /><br /><br />是月&nbsp;&nbsp;△认真学习贯彻习近平总书记系列重要讲话精神。组织局党委中心组理论学习；邀请市委党校教授辅导授课；组织参加市委宣传部“培育和践行社会主义核心价值观首场宣讲报告会”。 <br />△组织各区（县级市）旅游局开展广东旅游名镇名村创建及2014年广州战略性主导产业发展资金旅游项目申报工作，指导珠江琶醍啤酒文化创意艺术区创3A景区工作。 <br />△指导增城白水寨、从化流溪河国家森林公园和石门国家森林公园等6家受洪灾影响严重的景区，开展灾后旅游设施修复和安全生产工作。 <br />△在法国巴黎、英国伦敦、俄罗斯莫斯科、德国法兰克福、加拿大温哥华等5个海外国际机场投放“72小时过境免签政策”宣传广告；在广九《直通车月刊》杂志投放广州旅游宣传广告；在广州花城广场投放广州城市形象宣传广告。 <br />△批准成立旅行社7家，指导1家旅行社申评诚信旅行社、3家旅行社申请出境游组团社工作，对&nbsp;11家旅行社2013年度组织接待外国人来穗旅游奖励开展审计。 <br />△组织2014年度《出境旅游领队证》申领和换证培训，共1460人参加培训。 <br />△召开旅游集散中心建设研讨会及试点规划咨询会议，组织旅游企业现场考察集散中心试点选址情况，发放广州旅游集散中心建设问卷，征求各方意见和需求。 <br />△开展旅游大巴在景区景点停放情况调研，组织相关景区、旅行社和专家召开广州旅游景区停车场供需研讨座谈会，研究起草专题报告上报市领导；协调市交委研究在花城广场周边路段设置旅游交通指示牌。 <br />△组织召开“广州智慧旅游公共服务平台建设”咨询工作会议、网站及信息系统运维和安全工作会议；派员参加市委宣传部举办的2014年第一期互联网宣传管理暨政务微信开通建设培训班；完成“市旅游局政府服务网上办理项目”公开招投标前期准备工作；发布商务网站信息296条、政务网站信息264条、微博信息150条。 <br />△组织召开入境游客花费抽样调查培训会议；派员参加“全省旅游质监执法人员视频培训会议”及市法制办举办的“政府合同审查工作培训班”。 <br />△向省旅游局和市质监局推荐我局牵头制定的《旅游景区高峰预警机制工作指引》、《珠江游览船星级划分与评定》和广州动物园起草的《动物园游览安全服务质量规范》作为广东省服务业标准化项目；向省旅游局推荐推荐广东省中国青年旅行社、广州动物园和广州塔作为第三批全国旅游标准化试点单位。 <br />△检查涉嫌违法经营旅游业务单位1个，处理各类旅游投诉及咨询举报共102宗，结案45宗，协调旅行社向消费者退（赔）人民币7880元。 <br />△派员参加市协作办组织召开的黔南州帮扶工作会议；筹备展开对口帮扶贵州省荔波县培养旅游人才支教、来穗培训等工作；派员参加增城市“双到”帮扶工作培训；完成梅州市松口镇铜琶村修复800米机耕路填土打底工程。 <br />△派员参加国家旅游局组织的“安全与应急管理培训班”；完成《广州市旅游突发事件应急预案》及《2014年第二季度突发事件应对工作总结和第三季度隐患分析评估报告》，并报市应急办；督促指导旅游企业做好安全检查，本月共对33家旅行社、6家A级景区、5家星级饭店进行安全检查。 <br />△组织第一批在职干部职工赴市疗养院体检。 </span>');
INSERT INTO DS_CMS_PAGE (ID, SITEID, CATEGORYID, STATUS, TITLE, URL, METAKEYWORDS, METADESCRIPTION, SUMMARY, RELEASETIME, RELEASESOURCE, RELEASEUSER, IMG, IMGTOP, PAGETOP, CONTENT) VALUES (986, 5, 42, 1, '广州市旅游局大事记（2014年七月份）', '/a/gzdt2/986.html', '', '', '广州市旅游局大事记（2014年七月份）', '2014-08-09 10:59:10', NULL, NULL, '', 0, 1, '<span style=\"font-family:宋体;color:black;FONT-SIZE: 9pt\">&nbsp;广&nbsp;州&nbsp;市&nbsp;旅&nbsp;游&nbsp;局&nbsp;大&nbsp;事&nbsp;记 <br /><br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 二〇一四年七月 <br />　 <br />4日&nbsp;&nbsp;△局主要领导率相关处室领导赴广之旅调研，了解上半年经营发展及贯彻《旅游法》情况，对广之旅新领导班子提出具体要求。 <br /><br />7日-8日&nbsp;&nbsp;△市政府王东副市长率我局及东莞市旅游局，组织两地旅游企业赴四川成都举办穗莞旅游（成都）推介会。 <br /><br />9日-12日&nbsp;&nbsp;△王东副市长率我局及市教育局、文广新局、规划设计院等单位赴四川甘孜州开展对口帮扶工作；会同东莞市旅游局组织两地30多家重点旅游企业，到甘孜州开展旅游帮扶踩线考察活动。 <br /><br />14日-17日&nbsp;&nbsp;△指导推进沙湾古镇、天适樱花园、神农草堂、北京路文化核心区等景点创A工作。 <br /><br />18日&nbsp;&nbsp;△召开2014年第二季度旅游投诉案例分析会。 <br /><br />21日-25日&nbsp;&nbsp;△广深珠旅游联盟组织广州塔等11家旅游企业赴福州、杭州举办两场推介会，当地主要旅行社及媒体180人参加。 <br /><br />28日&nbsp;&nbsp;△局主要领导带队，向市人大汇报我市旅游业发展情况，陪同市人大代表检查广之旅、南粤王博物馆等旅游企业。 <br /><br />28日-31日&nbsp;&nbsp;△朱力局长参加省委组织部、省旅游局联合举办的智慧旅游专题培训班。 <br /><br />29日-30日&nbsp;&nbsp;△派员参加市发改委组织的游艇提案座谈会，现场考察太古仓游艇码头建设情况。 <br /><br /><br />是月&nbsp;&nbsp;△组织梳理总结2014年上半年我局工作情况，起草《广州市旅游局2014年上半年工作总结及下半年主要工作安排》并报省旅游局及市政府王东副市长。 <br />△派员参加由陈建华市长主持召开的广州市历史文化名城委员会2014年第一次会议，会议审定通过我局参与编制的《北京路文化核心区总体规划》。 <br />△成立广州市旅游集散中心专项工作小组，制定《广州市旅游集散中心建设试点工作方案》，协调市交委领导共同研究探讨旅游集散中心建设扶持政策，赴试点单位确定旅游问询中心设置地点并协商旅游定线申报意向和旅游企业进驻条件。 <br />△公示《广州市国内旅游组团合同（示范文本）》及《广州市出境旅游组团合同（示范文本）》（征求意见稿），向社会公众征求意见；制订局《国外举办推介活动流程》、《国内举办推介会流程》及《国内参展流程》。 <br />△会见新西兰奥克兰外办主任，就10月广州赴奥克兰旅游推介活动交换意见。 <br />△筹备参加&nbsp;“中国广东国际旅游交易会”、第22届广州博览会、山东旅游交易会、2014年中国（北京）国际商务及会奖旅游展；筹备2014广州大学城亲子修学游活动，设计大学城文化、科技、生态旅游三款宣传折页。 <br />△批准成立旅行社4家；办理旅行社变更事项备案4家、旅行社委托代理招徕旅游者业务备案14宗；公示2013年度组织接待外国人来穗旅游奖励审计结果。 <br />△评定花都区广州国盛大酒店为三星级旅游饭店。 <br />△起草完成《2014年度财政投资信息化建设项目广州智慧旅游统一服务工作平台系统建设方案（报备稿）》，着手编写《广州智慧旅游统一服务工作平台系统》招标方案，向省旅游局报送《抓管理，促服务，大力推动广州旅游电子政务和智慧旅游的建设与发展》经验介绍材料；向世界旅游城市联合会出具信息使用授权书，授权其使用由我局直接采编享有版权的政务和商务网站信息及图片。 <br />△召开广州市旅游市场打假打非专项整治工作联席会议，出动市场监督检查14人次，检查涉嫌违法经营旅游业务点3个，处理各类旅游投诉及咨询举报共226宗，结案131宗，协调旅行社向消费者退（赔）人民币58987元。 <br />△派员参加广州市党政代表团赴新疆喀什疏附县开展对口援建考察暨出席第十届“喀交会”、第二届“喀什?广州商品交易会”。 <br />△派员赴青海西宁参加国家安全生产监督管理总局举办的“建立健全安全生产责任体系，提高应急处置能力建设”培训班；下发广州市旅游行业安全生产检查督查工作方案；指导白水寨等受灾景区做好灾后安全管理工作。本月共对38家旅行社、2家A级景区、6家星级饭店进行安全检查。 </span>');
INSERT INTO DS_CMS_PAGE (ID, SITEID, CATEGORYID, STATUS, TITLE, URL, METAKEYWORDS, METADESCRIPTION, SUMMARY, RELEASETIME, RELEASESOURCE, RELEASEUSER, IMG, IMGTOP, PAGETOP, CONTENT) VALUES (987, 5, 42, 1, '广州市旅游局大事记（2014年十二月份）', '/a/gzdt2/987.html', '', '', '广州市旅游局大事记（2014年十二月份）', '2015-01-30 11:00:40', NULL, NULL, '', 0, 1, '<span style=\"font-family:宋体;color:black;FONT-SIZE: 9pt\">&nbsp;广&nbsp;州&nbsp;市&nbsp;旅&nbsp;游&nbsp;局&nbsp;大&nbsp;事&nbsp;记 <br /><br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 二〇一四年十二月 <br /><br />10日&nbsp;&nbsp;△组织《旅游法》暨文明旅游知识竞赛决赛。 <br /><br />15日&nbsp;&nbsp;△百万葵园被评为国家4A级旅游景区。 <br /><br />16日&nbsp;&nbsp;△民盟广东省委员会领导带队，赴我局进行有关粤北山区旅游发展及自驾游发展情况调研，局分管领导、相关处室人员及5家旅行社代表参加调研座谈。 <br /><br />23日-24日&nbsp;&nbsp;△局领导参加全省旅游局局长座谈会。 <br /><br />24日&nbsp;&nbsp;△首批广州旅游集散中心（海珠站、芳村站及广州南站）正式投入试运营。 <br /><br />26日&nbsp;&nbsp;△局主要领导带相关人员参加珠江游蓝海豚游船公司海豚628号新船首航仪式。 <br /><br />30日-31日&nbsp;&nbsp;△广州市第十四届人民代表大会常务委员会第三十六次会议听取和审议广州市旅游局关于落实《政府工作报告》目标任务情况的报告，局主要领导带相关人员参加会议并作报告。&nbsp; <br /><br />是月&nbsp;&nbsp;△派员参加市规划局召开的从化市莲麻村旅游策划方案及发展规划方案征求意见会；制定下发《关于促进从化市吕田镇莲麻村旅游发展的工作方案》。 <br />△派员参加从化宝趣玫瑰世界创5A方案评审会议。 <br />△协助重庆市、石家庄市、邵阳市及赣州西部旅游联盟来穗举办旅游推介会。 <br />△以“羊年到羊城过大年”为主题，筹备广州迎春花市节系列旅游宣传推介活动，设计编印2015年广州迎春花市旅游宣传册5000册。 <br />△完成在法国巴黎、俄罗斯莫斯科机场投放的“72小时过境免签政策”宣传广告上画、刊出工作。 <br />△启动TPO部分会员间的宣传资源互换推广活动，制订广州-仁川、高阳宣传交换方案。 <br />△印刷广州银发旅游宣传折页三款共计9000份。 <br />△派员参加市人大法工委组织召开的地方性法规修正案征求意见座谈会，听取立法顾问对《广州市旅游条例》等地方性法规的意见。 <br />△派员参加市人大举办的第15期市民议政厅网络在线互动活动，听取网民对我市旅游发展的意见和建议。 <br />△批准成立旅行社8家，评定诚信旅行社2家，办理旅行社委托代理招徕旅游者业务备案56宗；完成5家旅行社出境游组团社申请初审。 <br />△协助国家星评委完成亚洲国际大酒店五星级评定性复核，完成花都合景喜来登酒店申报五星级饭店的检查和上报工作。 <br />△组织北京路文化核心区导游词培训；为梅州市、清远市及新疆、黔南州等对口帮扶地区制作旅游教育培训DVD视频资料。 <br />△发布商务网站信息共457条、政务网站信息共41条，完成微博信息发布155条，处理微博投诉1条，答复粉丝旅游咨询&nbsp;10条。微博粉丝量24.7万,举行微博“广州旅游集散中心启用”主题有奖转发活动，阅读量162万人次。&nbsp; <br />△组织全市旅行社企业迎接全国文明旅游检查；组织从化市和增城市星级酒店开展冬季消防演练；对18家旅行社、8家酒店进行安全检查。 <br />△处理各类旅游投诉及咨询举报共97宗，协调旅行社向消费者退（赔）人民币87307.7元。 </span>');
INSERT INTO DS_CMS_PAGE (ID, SITEID, CATEGORYID, STATUS, TITLE, URL, METAKEYWORDS, METADESCRIPTION, SUMMARY, RELEASETIME, RELEASESOURCE, RELEASEUSER, IMG, IMGTOP, PAGETOP, CONTENT) VALUES (988, 5, 42, 1, '广州市旅游局大事记（2014年十一月份）', '/a/gzdt2/988.html', '', '', '广州市旅游局大事记（2014年十一月份）', '2014-12-09 11:03:16', NULL, NULL, '', 0, 1, '<span style=\"font-family:宋体;color:black;FONT-SIZE: 9pt\">&nbsp;广&nbsp;州&nbsp;市&nbsp;旅&nbsp;游&nbsp;局&nbsp;大&nbsp;事&nbsp;记 <br /><br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 二〇一四年十一月 <br /><br />11日&nbsp;&nbsp;△派员参加TPO第25次执委会。 <br /><br />13日&nbsp;&nbsp;△局主要领导陪同市人大赴珠江琶堤考察，参加市人大满意度测评调研座谈会，向市人大汇报2014年我局落实《政府工作报告》目标任务情况。 <br /><br />15日-16日&nbsp;&nbsp;△局主要领导赴郑州市参加旅游市长论坛大会。 <br /><br />17日&nbsp;&nbsp;△局主要领导在广州富力君悦酒店主持召开广州、奥克兰、洛杉矶三市旅游局长圆桌会议，深入探讨三市旅游合作细节问题。 <br /><br />20日-24日&nbsp;&nbsp;△局主要领导随广州市政府代表团赴新西兰，出席广州市与奥克兰市缔结友好城市关系25周年庆典。&nbsp; <br /><br />24日&nbsp;&nbsp;△与中山、江门旅游局共同签署《广中江联盟旅游合作框架协议》 <br /><br />26日&nbsp;&nbsp;△局领导会见印度驻广州总领事馆塔润?库玛先生一行。 <br /><br />是月&nbsp;&nbsp;△局主要领导率队和组织专家先后赴从化市莲麻村、宝趣玫瑰世界考察，与从化市政府及有关部门、企业召开座谈会议，研究落实市委领导指示精神，推动从化市莲麻村、宝趣玫瑰世界旅游发展建设。 <br />△积极推进海鸥岛旅游开发。先后4次赴海鸥岛实地考察，组织番禺区政府与石楼镇政府赴天津市郭家沟乡村旅游示范点调研，并与南方传媒影视集团、珠江文化研究会、北京巅峰智业、北京博雅方略等文化旅游创意单位对接，指导启动海鸥岛旅游规划招投标工作。 <br />△派员参加市规划局召开的从化市莲麻村规划和改造工作协调会，参加市林业和园林局关于流溪河、石门国家森林公园规划座谈会。 <br />△参加市人大法工委组织召开的地方性法规修正案征求意见座谈会，听取人大代表、政协委员、立法顾问等对《广州市旅游条例》的意见。 <br />△组织各区旅游局及旅游企业赴澳门，参加2014澳门国际旅游（产业）博览会；&nbsp;组织广州新电视塔等企业赴台北，参加第九届海峡两岸台北旅游展；组织各区旅游局、行业协会、酒店、旅行社、旅游公司等19家单位赴上海，参加2014中国国际旅游交易会；会同中山和江门市旅游局，组织旅游企业先后赴大连、长春和哈尔滨三地举办旅游推介会，宣传推介我市春节及花市旅游产品。 <br />△牵头组织召开泰国、广州旅游同业座谈会，泰国旅行商代表团、亚洲航空及广州旅行社代表参加会议；派员参加由法国里昂市政府代表团举办的合作交流座谈会；2014澳门国际旅游（产业）博览会期间，派员参加“中国-澳门-拉美旅游”圆桌对话会议，并与澳门旅游局召开座谈会，就明年共同开展“一程多站”宣传促销活动、加强“144小时便利签证”及“广州72小时过境免签政策”的推广运用等合作内容进行了讨论和交流。 <br />△协助浙江台州市旅游局、杭州市旅游形象推广中心代表团、贵州省黔东南州来穗举办旅游推介会。协助俄罗斯驻广州总领事馆、俄罗斯联邦索契市来穗举办“索契市在旅游医疗康复环境以及关于组织各种各样国际活动能力的推介会”。 <br />△印刷50万份“广州一日游”旅游地图，发至星级宾馆（洒店）、机场、火车站、旅游问询点及相关旅行社，向游客免费派送。 <br />△向省景评委推荐宝墨园申报5A级旅游景区、香江健康山谷申报4A级旅游景区。 <br />△批准成立旅行社3家；办理旅行社变更事项备案3家；完成2家旅行社诚信旅行社测评及7家旅行社出境游组团社资质测评。 <br />△协助国家星评委完成星河湾酒店、丽思卡尔顿酒店、君悦酒店五星级评定性复核，协助省星评委完成科学城华夏国际商务酒店四星级评定性复核。 <br />△组织2014年全国中高级导游员等级考试，与旅行社协会联合举办2014年移动互联时代旅行社创新发展培训班。 <br />△积极推进广州旅游集散中心建设试点，协助试点单位策划具有市场竞争力和吸引力的集散中心专属产品，策划首期开通旅游专线10条，通过编印宣传折页及官方网站、微博、微信等多种渠道，全方位宣传提升广州旅游集散中心知名度。 <br />△派员赴对口帮扶梅州市铜琶村召开现场工作会，解决铜琶村安全饮水问题。&nbsp; <br />△发布商务网站信息共459条、政务网站信息共24条，完成微博信息发布158条，处理微博投诉6条，答复粉丝旅游咨询&nbsp;13条。 <br />△检查经营旅游业务点13个，收缴虚假旅游宣传广告700余份；处理各类旅游投诉及咨询举报共110宗，结案50宗，协调旅行社向消费者退（赔）人民币11917元。 <br />△组织全市旅游行业开展“消防安全宣传月”活动。 </span>');
INSERT INTO DS_CMS_PAGE (ID, SITEID, CATEGORYID, STATUS, TITLE, URL, METAKEYWORDS, METADESCRIPTION, SUMMARY, RELEASETIME, RELEASESOURCE, RELEASEUSER, IMG, IMGTOP, PAGETOP, CONTENT) VALUES (989, 5, 42, 1, '广州市旅游局大事记（2014年十月份） ', '/a/gzdt2/989.html', '', '', '广州市旅游局大事记（2014年十月份） ', '2014-11-09 11:04:25', NULL, NULL, '', 0, 1, '<span style=\"font-family:宋体;color:black;FONT-SIZE: 9pt\">广&nbsp;州&nbsp;市&nbsp;旅&nbsp;游&nbsp;局&nbsp;大&nbsp;事&nbsp;记 <br /><br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 二〇一四年十月 <br /><br />11日&nbsp;&nbsp;△广州市委书记任学锋会见国家旅游局党组书记、局长邵琪伟一行，局主要领导陪同。 <br /><br />13日&nbsp;&nbsp;△派员赴深圳参加国家旅游局调研组座谈会，交流讨论我市贯彻落实国发31号文件情况、粤港澳旅游合作情况、港澳入境旅游政策及房车露营发展等方面情况。 <br /><br />13日-15日&nbsp;&nbsp;△派员赴桂林参加第八届联合国世界旅游组织/亚太旅游协会旅游趋势与展望国际论坛。 <br /><br />14日-17日&nbsp;&nbsp;△派员参加第九届中国邮轮产业大会。 <br /><br />17日&nbsp;&nbsp;△组织赴海鸥岛进行生态乡村旅游开发利用调研考察。 <br /><br />22日&nbsp;&nbsp;△国家旅游局邵琪伟局长视察宝墨园。 <br /><br />30日&nbsp;&nbsp;△组织召开旅行社标准化建设推广会议。 <br /><br />30日&nbsp;&nbsp;△局领导会见新加坡驻广州总领事馆（商务处）一行，就新加坡媒体企业与广州的潜在合作可能性进行探讨。 <br /><br /><br /><br /><br />是月&nbsp;&nbsp;△成功申请国家旅游综合改革试点城市，研究撰写《广州旅游综合改革试点城市实施方案》，组织方案专题研讨会及专家咨询会，并专门向王东副市长汇报。 <br />△珠江琶醍啤酒文化创意艺术区、广州星期8小镇两家景区获评国家AAA级旅游景区；推荐广东天适樱花悠乐园申报广东省休闲农业与乡村旅游示范点；配合省旅游景区质量等级评定委员会评定小组到百万葵园验收创4A工作。 <br />△派员参加广州支持从化事项工作协调会，就我局牵头“支持创建国家级旅游度假区”相关事项提出工作意见。 <br />△派员参加第十届中国国际航空航天博览会广州推介会；在新西兰奥克兰市及新加坡举办主题为“广州——非凡的体验”旅游推介会；组织旅游企业赴东莞参加广东21世纪海上丝绸之路国际博览会；协助中山市旅游局来穗举办旅游推介会；协助广东省旅游局做好第49届香港国际工业品展销会企业动员工作。 <br />△汇总&nbsp;“72小时过境免签”政策宣传工作情况报广州市政府；起草关于“72小时过境免签”政策免费中转旅游服务柜台场地协调事宜的报告，呈王东副市长。 <br />△继续推进智慧旅游相关工作。参加“全国智慧旅游创新发展大会及首期全国智慧旅游建设培训班”，举办第一期信息安全知识培训，开展广州旅游大数据库建设前期调研，向市科信局报送广州智慧旅游公共服务平台建设方案。 <br />△继续推进广州旅游集散中心建设试点。完成部分试点单位项目工程建设及站场压力测试；组织召开旅游线路开通工作协调会，协调相关客运站、交通运输企业与旅游景点对接；指导部分旅游企业对海珠站进行装修布置；向分管市领导报告试点工作进展情况。 <br />△协助省星评委完成了增城宾馆、百花山庄度假村、夏湾拿酒店、碧水湾度假村、祈福酒店、番禺宾馆四星级评定性复核工作；协助国家星评委完成了科尔海悦酒店、铂尔曼大酒店五星级评定性复核工作。 <br />△组织广州地区2014年度第二次全国导游人员资格考试口试；组织举办广州旅游咨询员培训班。 <br />△拟制局2015年对口支援新疆疏附县旅游工作及计划报市援疆办。赴对口帮扶增城正果镇圭湖村与村两委座谈，研究解决实际问题。赴梅州市松口镇铜琶村，与铜琶村党支部开展党支部结对共建活动。指导做好三连一阳旅游规划招标工作。 <br />△发布商务网站信息457条、政务网站信息21条；完成微博信息发布155条&nbsp;；处理微博投诉8条，答复粉丝旅游咨询&nbsp;10条。 <br />△批准成立旅行社3家；办理旅行社变更事项备案11家；完成2家旅行社诚信旅行社测评及3家旅行社出境游组团社资质测评。 <br />△出动旅游市场监督检查18人次，检查经营旅游业务点6个，收缴虚假旅游宣传广告5000余份。处理各类旅游投诉及咨询举报共120宗，结案79宗，协调旅行社向消费者退（赔）人民币121098元。 <br />△组织广州地区旅行社开展&nbsp;“我是广州文明信使”行动。继续抓好埃博拉及登革热疫情联防联控工作，下发宣传资料，按时上报情况。本月共对16家旅行社、4家A级景区、13家星级饭店进行安全检查。 </span>');
INSERT INTO DS_CMS_PAGE (ID, SITEID, CATEGORYID, STATUS, TITLE, URL, METAKEYWORDS, METADESCRIPTION, SUMMARY, RELEASETIME, RELEASESOURCE, RELEASEUSER, IMG, IMGTOP, PAGETOP, CONTENT) VALUES (990, 5, 42, 1, '广州市旅游局大事记（2014年五月份）', '/a/gzdt2/990.html', '', '', '广州市旅游局大事记（2014年五月份）', '2014-06-09 11:05:36', NULL, NULL, '', 0, 1, '<span style=\"font-family:宋体;color:black;FONT-SIZE: 9pt\">&nbsp;广&nbsp;州&nbsp;市&nbsp;旅&nbsp;游&nbsp;局&nbsp;大&nbsp;事&nbsp;记 <br /><br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 二〇一四年五月 <br />　 <br />6日&nbsp;&nbsp;△周耀明副书记带队赴增城正果镇，与市扶贫工作队及镇领导商讨对口帮扶圭湖村事宜。 <br /><br />6日-9日&nbsp;&nbsp;△徐颖副巡视员带队赴番禺区广东女子职业技术学院、广州番禺职业技术学院及花都区广东行政学院等旅游院校调研旅游教育培训资源。 <br /><br />7日&nbsp;&nbsp;△会同市民宗局组织召开宗教文化旅游专题培训班。&nbsp; <br /><br />10日-17日&nbsp;&nbsp;△朱力局长、李志新巡视员带队赴澳大利亚悉尼市和新西兰奥克兰市，举办“72小时过境免签政策”专题推介会。 <br /><br />14日&nbsp;&nbsp;△会同从化市人民政府起草《关于广州从化流溪温泉旅游度假区申报创建国家级旅游度假区的请示》报广东省旅游局。 <br /><br />16日&nbsp;&nbsp;△协助通辽市旅游局来穗举办通辽市航空旅游推介会。 <br /><br />17日-21日&nbsp;&nbsp;△陈永亮副局长带队，组织广州塔等10多家品牌企业共50人参加第七届华中旅游博览会。我局展台获“最佳展台奖”荣誉称号。 <br /><br />18日&nbsp;&nbsp;△谭爱英副局长带相关处室人员参加我局与市科信局联合主办的广州科普一日游启动仪式。 <br /><br />18日-19日&nbsp;&nbsp;△在白云万达广场举办2014年“中国旅游日”广州专题活动。越秀、海珠、天河、白云、番禺等区旅游局、部分旅行社、旅游景区等42个单位参加现场活动。 <br /><br />18日-20日&nbsp;&nbsp;△派员赴武汉参加第七届华中旅游博览会。 <br /><br />19日&nbsp;&nbsp;△派员参加市法制办组织的《广州市农家乐管理办法》研讨会。 <br /><br />21日&nbsp;&nbsp;△考察指导沙湾古镇创4A、宝墨园升5A的工作。 <br /><br />23日&nbsp;&nbsp;△派员参加珠江游专业委员会组织召开的关于研究解决珠江游无序竞争、价格偏低问题座谈会。 <br /><br />23日&nbsp;&nbsp;△及时妥善处置因大雨引发山洪、山泥塌方导致假日通旅行社37名游客被困的突发事件。 <br /><br />24日-27日&nbsp;&nbsp;△组织14家企业选送广州特色旅游商品参加2014中国国际商品博览会暨中国旅游商品大赛。 <br /><br />31日&nbsp;&nbsp;△派员参加&nbsp;“广州宗教文化一日游暨海丝史迹巡礼”启动仪式。 <br /><br />是月&nbsp;&nbsp;△组织市管干部参加市委党校组织的市管干部学习贯彻习近平总书记系列讲话精神集中轮训班，组织开展处级（含）以下干部学习贯彻习近平总书记系列讲话精神集中轮训和专题培训工作。 <br />△积极筹备推进广州旅游集散中心试点工作。根据广州城市发展和旅游产业格局的新变化，更新《广州旅游集散中心规划研究》相关数据，调整建设选点方案，选定广州南旅游集散中心（南站），广州中旅游集散中心（广州塔和花城广场），广州西旅游集散中心（海珠客运站和芳村客运站）为旅游集散中心试点单位。 <br />△起草《广州市旅游条例（修正稿）》报市法制办。 <br />△起草《广州市旅游局关于贯彻执行〈中华人民共和国旅游法〉的情况报告》报广东省旅游局。 <br />△完成《中共广州市委、广州市人民政府关于加快我市旅游业发展建设旅游强市的意见》和《广州市旅游局规范行政处罚自由裁量权暂行规定》的修订工作。 <br />△修改广州“72小时过境免签政策”海外TravelAdvisor网站宣传项目广告发布合同。完成《传奇南粤之叙说广州》宣传片拍摄的续约工作。与广九《直通车月刊》杂志续签合作协议。编印辛亥革命系列宣传折页3款共9000份。 <br />△协助国家旅游局驻伦敦办事处组织英国旅行商广州旅游线路考察。 <br />△完成旅行社日常管理工作。批准成立旅行社1家，办理5家旅行社变更事项备案，办理旅行社委托代理招徕旅游者业务备案44宗，完成42家旅行社降低旅游服务质量保证金50%及2家撤减分社提取旅游服务质量保证金申请的报批工作，完成8家旅行社设立分社缴存旅游服务质量保证金的审核办理工作。 <br />△协调广州地区旅行社行业协会理事会通过成立广州地区旅行社行业协会导游分会方案。 <br />△派员赴云来斯堡酒店、花都喜来登酒店进行申报五星级旅游饭店初检。 <br />△继续推进旅游扶贫工作。指导荔波县修改完善《荔波县旅游发展总体规划》；派员赴梅州与广梅指挥部对接2014年旅游帮扶项目及资金需求工作，协调梅州市松口镇铜琶村投入2.1万元购买化肥发放给贫困户；协调市委新闻宣传处做好对口帮扶宣传策划工作。 <br />△起草《2013年旅游行业服务质量状况分析报告》报送市经贸委。 <br />△出动旅游行政执法检查20人次，检查涉嫌违法经营旅游业务单位4个。处理各类旅游投诉及咨询举报共154宗，结案78宗，协调旅行社向消费者退（赔）人民币13670元。 <br />△督促指导旅游企业做好端午节前安全检查，本月共对33家旅行社、4家A级景区、5家星级饭店进行安全检查。 <br />△召开机关党委委员会会议，审核局第六届机关工会委员会候选委员推荐名单。 <br />△加强局物业管理工作。根据市委办公会议纪要精神，就回民饭店的新一轮承包招租工作，起草《关于落实督办事项工作情况的报告》。召开局长办公会议，研究局物业出租整改有关事项，对局及局属企业出租的物业进行清理和整改。 </span>');
INSERT INTO DS_CMS_PAGE (ID, SITEID, CATEGORYID, STATUS, TITLE, URL, METAKEYWORDS, METADESCRIPTION, SUMMARY, RELEASETIME, RELEASESOURCE, RELEASEUSER, IMG, IMGTOP, PAGETOP, CONTENT) VALUES (991, 5, 42, 1, '广州市旅游局大事记（2015年二月份）', '/a/gzdt2/991.html', '', '', '广州市旅游局大事记（2015年二月份）', '2015-03-09 11:06:14', NULL, NULL, '', 0, 1, '<span style=\"font-family:宋体;color:black;FONT-SIZE: 9pt\">广&nbsp;州&nbsp;市&nbsp;旅&nbsp;游&nbsp;局&nbsp;大&nbsp;事&nbsp;记&nbsp;<br /><br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 二〇一五年二月 <br /><br />8日&nbsp;&nbsp;△派员参加全国旅游市场工作会议。 <br /><br />10日&nbsp;&nbsp;△组织召开2015年旅游工作会议，传达贯彻全国、全省旅游工作会议精神，总结2014年广州旅游工作，部署2015年广州旅游工作任务。市政府王东副市长出席会议并作重要讲话，各区（县级市）政府分管旅游工作的领导和旅游局局长，市直有关单位领导，中央和省驻穗有关单位领导，以及旅游企业、旅游协会代表参加会议。 <br /><br />12日&nbsp;&nbsp;△王东副市长率市旅游局、市安监局、市质监局、市公安消防局、市交委等部门，赴海珠区、白云区对广州旅游集散中心海珠站、广州塔、广州广之旅国际旅行社进行节前安全检查，并在广之旅会议室主持召开广州市春节前旅游安全检查工作座谈会。 <br /><br />26日&nbsp;&nbsp;△派员赴广西桂林参加全国旅游厕所工作现场会。 <br /><br />是月&nbsp;&nbsp;△做好2015广州迎春花市系列活动，会同市委宣传部、花市办在澳门举办花市宣传推介会；印制、发放2015广州迎春花市宣传册1万份。 <br />△制作“羊年羊城过大年，花城花街尝美食”形象视频，在广州广播电视台移动频道播出；在东风路、康王路、北京路、珠江新城等10处市内主干道，以“羊年游羊城三羊开泰”为主题，投放大型专题围墙广告，重点宣传推介广州春节旅游线路和资讯。 <br />△与广州广播电视台新闻资讯广播（FM96.2）合作推出专题旅游栏目，为市民和游客提供广州旅游相关资讯；在白云机场17条巴士线路车载LCD视频联播网投放专题旅游宣传片。 <br />△组织汉诺威米兰展览（中国）有限公司召开2015广州国际旅游展览会（GITF）筹备工作会议，做好旅展相关筹备工作。 <br />△组织召开广清韶旅游联盟2015年工作会议；派员参加广深珠、广佛肇旅游联盟总结会；与港铁、直通车杂志、广州地铁等媒体洽谈旅游宣传合作细节。 <br />△拟订《广州市推进“21世纪海上丝绸之路”三年行动计划》，拟定“做好建设21世纪海上丝绸之路工作”方案。 <br />△开展广州旅游集散中心新春宣传促销活动，广州地区6家旅游景区共提供18,000张景区门票，在广州旅游集散中心各站点开展买一赠一优惠活动。 <br />△派员参加“市政府部门为人大代表和政协委员提供咨询服务活动”。 <br />△修订《广州市旅行社组织与接待外国人来穗旅游奖励办法》，增加奖励项目，提高奖励金额，激活入境旅游市场。 <br />△2家旅行社获国家旅游局批准经营出境旅游业务的许可；批准1家旅行社经营境内旅游和入境旅游业务许可；办理旅行社委托代理招徕旅游者业务备案30宗。 <br />△春节前完成梅州市松口镇铜琶村安全饮水工程主管道铺设，加紧进行管道入户等后续工程，困扰该村几十年的安全饮水问题得到妥善解决。 <br />△继续开展旅游市场文明旅游专项检查和服务网点专项治理抽查行动，对6家旅游景点（区）、11家旅行社、6家酒店进行春节前安全检查。 <br />△发布商务网站信息共369条、政务网站信息共39条，完成微博信息发布112条，答复粉丝旅游咨询40条。微博粉丝量26万,举行微博“羊年羊城过大年&nbsp;花城赏花尝美食”主题有奖转发活动，阅读量229万人次。 <br />△组织召开局2014年度领导干部民主生活会。 <br />△处理各类旅游投诉及咨询举报共117宗，协调旅行社退（赔）人民币6680元。 </span>');
INSERT INTO DS_CMS_PAGE (ID, SITEID, CATEGORYID, STATUS, TITLE, URL, METAKEYWORDS, METADESCRIPTION, SUMMARY, RELEASETIME, RELEASESOURCE, RELEASEUSER, IMG, IMGTOP, PAGETOP, CONTENT) VALUES (992, 5, 42, 1, '广州市旅游局大事记（2015年三月份） ', '/a/gzdt2/992.html', '', '', '广州市旅游局大事记（2015年三月份） ', '2015-04-09 14:07:54', NULL, NULL, '', 0, 1, '<span style=\"font-family:宋体;color:black;FONT-SIZE: 9pt\">广&nbsp;州&nbsp;市&nbsp;旅&nbsp;游&nbsp;局&nbsp;大&nbsp;事&nbsp;记&nbsp;<br /><br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;二〇一五年三月 <br /><br /><br />4日&nbsp;&nbsp;△局主要领导会见香港旅发局、香港旅游业议会、亚洲旅游中心等单位领导，交流探讨旅游合作相关事宜。 <br /><br />6日-8日&nbsp;&nbsp;△成功举办第23届广州国际旅游展览会（GITF），共有43个国家和地区的780家展商参展，参观人数达11.5万人次，参展国际化程度、现场参观总人数、现场销售额均创历史新高。 <br /><br />30日-31日&nbsp;&nbsp;△派员赴合肥参加全国旅游规划发展工作会议。 <br /><br />是月&nbsp;&nbsp;△扎实推进旅游综合改革试点工作。组织市直有关单位、区（县）旅游主管部门、旅游企业领导及旅游专家学者进行座谈，深入研究讨论广州旅游综合改革有关问题，广泛征求旅游综合改革试点实施方案和促进旅游业加快发展的意见建议；组织起草组成员赴北京、天津和秦皇岛进行调研，学习借鉴优秀旅游城市旅游综合改革先进经验和作法；结合座谈会意见及调研成果，对综合改革试点实施方案等材料作进一步修改完善，印发市直相关单位及各区（县级市）征求意见。 <br />△继续做好省、市人大代表建议和政协委员提案接收分办工作。共收到建议和提案共39份，其中省人大代表建议2件、市人大代表建议13件，市政协提案24件，已办结市人大建议9件和市政协提案16件。 <br />△会同深圳市旅游局，先后在英国伦敦市和葡萄牙里斯本市举办旅游推介活动，在爱尔兰都柏林与当地旅游部门及旅行社进行座谈，宣传推介广州“72小时过境免签”政策，以及广州的历史、建筑、文化、节庆民俗等。 <br />△协助斯里兰卡国家旅游局、印度国家旅游局、武汉市旅游局、南宁市旅游局以及贵州贵阳、毕节市旅游局来穗举办旅游推介会，并与贵阳市签订旅游《合作框架协议》。 <br />△局主要领导率队，赴清远参加华南五市品牌推广联席会议，联合商讨2015年旅游联合推广计划。与澳新、东南亚、美国推广中心续签合作协议，进一步加大广州旅游在主要客源市场的宣传推广力度，激活入境游市场。 <br />△与北京路管委会召开工作会议，指导越秀区北京路文化旅游区创4A及广州塔旅游开发工作；赴市城管委协调了解环卫公厕的建设情况，赴市林业和园林局了解市政公园和重要绿道配套公厕的建设情况，赴增城调研和指导“万家旅舍”、自驾车营地（驿站）、旅游厕所和二龙山等建设管理情况，赴黄埔古港古村实地考察景点停车场情况，研究下一步工作计划；制定《广州市部分中心城区旅游大巴停靠实施方案》报市政府。 <br />△不断推进广州旅游集散体系建设。协调指导广州塔办理申办旅游集散中心报批手续；向市政府报送广州旅游集散中心建设情况，以及在广州塔和花城广场设置旅游集散中心的建议；赴正佳广场和广州东站汽车客运站实地考察选点，研究设立旅游集散点和建设旅游集散中心；与广州旅游集散中心、微景旅游和央视网旅游频道等单位，策划“集散中心领红包，广州旅游乐逍遥”活动；与阳江市政府和广东粤运集团等单位商讨合作计划，策划新开旅游专线。 <br />△局领导带队赴新世界大酒店调研，研究探讨酒店现状、存在问题、面临困境及改革思路等问题。 <br />△继续开展旅游市场文明旅游专项检查和服务网点专项治理抽查行动，对2家旅游景点（区）、33家旅行社、11家酒店进行安全检查。 <br />△严格市场执法检查，调解处理各类旅游投诉及咨询举报63宗。 </span>');
INSERT INTO DS_CMS_PAGE (ID, SITEID, CATEGORYID, STATUS, TITLE, URL, METAKEYWORDS, METADESCRIPTION, SUMMARY, RELEASETIME, RELEASESOURCE, RELEASEUSER, IMG, IMGTOP, PAGETOP, CONTENT) VALUES (993, 5, 42, 1, '广州市旅游局大事记（2015年一月份）', '/a/gzdt2/993.html', '', '', '广州市旅游局大事记（2015年一月份）', '2015-02-09 11:09:58', NULL, NULL, '', 0, 1, '<span style=\"font-family:宋体;color:black;FONT-SIZE: 9pt\">广&nbsp;州&nbsp;市&nbsp;旅&nbsp;游&nbsp;局&nbsp;大&nbsp;事&nbsp;记 <br /><br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 二〇一五年一月 <br /><br />15日-16日&nbsp;&nbsp;△局主要领导赴江西南昌参加2015年全国旅游工作会议。 <br /><br />20日&nbsp;&nbsp;△局主要领导参加2015年全省旅游工作会议。 <br /><br />28日&nbsp;&nbsp;△召开新闻媒体通气会，向媒体通报“羊年羊城过大年&nbsp;花城赏花尝美食”活动情况、春节期间各旅游企业推出的主题活动和优惠措施。 <br /><br />30日&nbsp;&nbsp;△局领导会见伊朗伊斯兰共和国驻广州总领事馆领事阿费辛?雷米先生，就两地旅游的共同发展进行友好交流。&nbsp; <br /><br />是月&nbsp;&nbsp;△南沙永乐农庄被评为国家3A级旅游景区；沙湾镇、上九陂村、西和村、塱头荣获2014年广东旅游名镇名村称号；南沙游艇会被评为首批广东省游艇旅游发展示范基地。 <br />△召开广州地区旅游景区最大承载量核定工作宣贯会议及2015年春节假期旅游安全保障工作会议。 <br />△局领导先后会见BBC旅游频道总编、香港旅发局广州办事处代表、TPO秘书长和中国区主管，商讨旅游发展合作事宜。 <br />△派员参加山东旅游推介会、华南五市旅游联盟总结会议、黑龙江冬季旅游产品推介会、桂林市旅游发展委员会举办的“桂林国际旅游胜地——广州推介会”、衢州市旅游委员会举办的&nbsp;“休闲衢州微行动年”年度发布会暨衢州旅游（广州）推介会。协助鞍山市人民政府来穗举办“鞍山经贸交流座谈会暨鞍山投资说明会（暨旅游推介会）”、珠海市文化体育旅游局来穗举办“珠海长隆旅游推介会”。 <br />△拟制“羊年到羊城过大年”旅游宣传推广工作方案，编印、派发《羊年羊城过大年、花城赏花尝美食》宣传册，会同武汉市旅游局联合印制春节宣传册，派员赴北京、天津、贵阳、南宁开展2015年广州迎春花市宣传推介。 <br />△抓紧筹备2015年广州国际旅游展览会（GITF），协调各联盟参展有关事宜。 <br />△组织召开旅游团队港澳名单表网络填报系统培训暨征求意见会议，配合省旅游局做好“外国人144小时免签旅游团信息管理系统”正式启用前各项准备工作。 <br />△协调工商银行、建设银行、农业银行等建立旅游服务质量保证金信息系统，规范旅游服务质量保证金管理。 <br />△协调旅行社落实春节期间“360看广州&nbsp;&nbsp;广州一日游”优惠方案。赴广州市第三公共汽车公司，就与上海春秋旅游公司合作开设广州城市观光巴士公司问题进行调研。 <br />△赴广东国旅等企业调研旅游培训工作情况，赴广东外语外贸大学等院校调研旅游教育资源情况；组织景区协会、旅行社协会、酒店协会座谈开展旅游从业人员培训工作事宜；组织导游公司、广之旅、南湖国旅等企业座谈导游岗前培训事宜。 <br />△编写《2015年“两会”旅游热点问题备忘》，做好为市人大代表和政协委员提供咨询服务活动准备工作。 <br />△参加市人大法工委组织召开的地方性法规修正案征求意见座谈会，听取各区人大法工委对《广州市旅游条例》的意见。 <br />△批准成立旅行社2家，评定诚信旅行社1家，办理旅行社委托代理招徕旅游者业务备案18宗；完成1家旅行社出境游组团社申请初审。 <br />△协助省星评委完成花都合景喜来登酒店申报五星级酒店的初评工作。 <br />△开展旅游市场文明旅游专项检查和服务网点专项治理抽查行动。对26家旅行社、7家酒店进行春节前安全检查。 <br />△发布商务网站信息共507条、政务网站信息共36条，完成微博信息发布158条，处理微博投诉4条，答复粉丝旅游咨询33条。邀请“@中国广州发布”共同举办“羊年到羊城过大年”微博有奖征集活动，阅读量达1200万人次。 <br />△处理各类旅游投诉及咨询举报共77宗，协调旅行社退（赔）人民币14450.2元。 </span>');
INSERT INTO DS_CMS_PAGE (ID, SITEID, CATEGORYID, STATUS, TITLE, URL, METAKEYWORDS, METADESCRIPTION, SUMMARY, RELEASETIME, RELEASESOURCE, RELEASEUSER, IMG, IMGTOP, PAGETOP, CONTENT) VALUES (994, 5, 19, 1, '广州市人民政府关于取消调整保留行政审批备案事项的决定', '/a/lyfggz/994.html', '', '', '广州市人民政府关于取消调整保留行政审批备案事项的决定', '2015-05-27 11:16:06', NULL, NULL, '', 0, 0, '<p>详情见附件</p><p><a href=\"http://www.gzly.gov.cn/f/file/201505/1432696618925.pdf\">广州市人民政府关于取消调整保留行政审批备案事项的决定.pdf</a></p>');
INSERT INTO DS_CMS_PAGE (ID, SITEID, CATEGORYID, STATUS, TITLE, URL, METAKEYWORDS, METADESCRIPTION, SUMMARY, RELEASETIME, RELEASESOURCE, RELEASEUSER, IMG, IMGTOP, PAGETOP, CONTENT) VALUES (996, 5, 19, 1, '中国公民出国旅游管理办法', '/a/lyfggz/996.html', '', '', '中国公民出国旅游管理办法', '2015-07-21 11:23:37', NULL, NULL, '', 0, 0, '<p>2002年5月29日国务院令354号发布</p><p>中国公民出国旅游管理办法:</p><p>　　第一条 为了规范旅行社组织中国公民出国旅游活动，保障出国旅游者和出国旅游经营者的合法权益，制定本办法。</p><p>　　第二条 出国旅游的目的地国家，由国务院旅游行政部门会同国务院有关部门提出，报国务院批准后，由国务院旅游行政部门公布.任何单位和个人不得组织中国公民到国务院旅游行政部门公布的出国旅游的目的地国家以外的国家旅游；组织中国公民到国务院旅游行政部门公布的出国旅游的目的地国家以外的国家进行涉及体育活动、文化活动等临时性专项旅游的，须经国务院旅游行政部门批准。</p><p>　　第三条 旅行社经营出国旅游业务，应当具备下列条件：</p><p>　　（一）取得国际旅行社资格满１年；</p><p>　　（二）经营入境旅游业务有突出业绩；</p><p>　　（三）经营期间无重大违法行为和重大服务质量问题。</p><p>　　第四条 申请经营出国旅游业务的旅行社，应当向省、自治区、直辖市旅游行政部门提出申请。省、自治区、直辖市旅游行政部门应当自受理申请之日起３０个工作日内，依据本办法第三条规定的条件对申请审查完毕，经审查同意的，报国务院旅游行政部门批准；经审查不同意的，应当书面通知申请人并说明理由。国务院旅游行政部门批准旅行社经营出国旅游业务，应当符合旅游业发展规划及合理布局的要求。未经国务院旅游行政部门批准取得出国旅游业务经营资格的，任何单位和个人不得擅自经营或者以商务、考察、培训等方式变相经营出国旅游业务。</p><p>　　第五条 国务院旅游行政部门应当将取得出国旅游业务经营资格的旅行社（以下简称组团社）名单予以公布，并通报国务院有关部门。</p><p>　　第六条 国务院旅游行政部门根据上年度全国入境旅游的业绩、出国旅游目的地的增加情况和出国旅游的发展趋势，在每年的２月底以前确定本年度组织出国旅游的人数安排总量，并下达省、自治区、直辖市旅游行政部门。省、自治区、直辖市旅游行政部门根据本行政区域内各组团社上年度经营入境旅游的业绩、经营能力、服务质量，按照公平、公正、公开的原则，在每年的３月底以前核定各组团社本年度组织出国旅游的人数安排。国务院旅游行政部门应当对省、自治区、直辖市旅游行政部门核定组团社年度出国旅游人数安排及组团社组织公民出国旅游的情况进行监督。</p><p>　　第七条 国务院旅游行政部门统一印制《中国公民出国旅游团队名单表》（以下简称《名单表》），在下达本年度出国旅游人数安排时编号发放给省、自治区、直辖市旅游行政部门，由省、自治区、直辖市旅游行政部门核发给组团社。组团社应当按照核定的出国旅游人数安排组织出国旅游团队，填写《名单表》。旅游者及领队首次出境或者再次出境，均应当填写在《名单表》中，经审核后的《名单表》不得增添人员。</p><p>　　第八条 《名单表》一式四联，分为：出境边防检查专用联、入境边防检查专用联、旅游行政部门审验专用联、旅行社自留专用联。组团社应当按照有关规定，在旅游团队出境、入境时及旅游团队入境后，将《名单表》分别交有关部门查验、留存。出国旅游兑换外汇，由旅游者个人按照国家有关规定办理。</p><p>　　第九条 旅游者持有有效普通护照的，可以直接到组团社办理出国旅游手续；没有有效普通护照的，应当依照《中华人民共和国公民出境入境管理法》的有关规定办理护照后再办理出国旅游手续。组团社应当为旅游者办理前往国签证等出境手续。</p><p>　　第十条 组团社应当为旅游团队安排专职领队。领队应当经省、自治区、直辖市旅游行政部门考核合格，取得领队证。领队在带团时，应当佩戴领队证，并遵守本办法及国务院旅游行政部门的有关规定。</p><p>第十一条 旅游团队应当从国家开放口岸整团出入境。旅游团队出入境时，应当接受边防检查站对护照、签证、《名单表》的查验。经国务院有关部门批准，旅游团队可以到旅游目的地国家按照该国有关规定办理签证或者免签证。旅游团队出境前已确定分团入境的，组团社应当事先向出入境边防检查总站或者省级公安边防部门备案。旅游团队出境后因不可抗力或者其他特殊原因确需分团入境的，领队应当及时通知组团社，组团社应当立即向有关出入境边防检查总站或者省级公安边防部门备案。</p><p>第十二条 组团社应当维护旅游者的合法权益。组团社向旅游者提供的出国旅游服务信息必须真实可靠，不得作虚假宣传，报价不得低于成本。</p><p>　　第十三条 组团社经营出国旅游业务，应当与旅游者订立书面旅游合同。旅游合同应当包括旅游起止时间、行程路线、价格、食宿、交通以及违约责任等内容。旅游合同由组团社和旅游者各持一份。</p><p>　　第十四条 组团社应当按照旅游合同约定的条件，为 旅游者提供服务。组团社应当保证所提供的服务符合保障旅游者人身、财产安全的要求；对可能危及旅游者人身安全的情况，应当向旅游者作出真实说明和明确警示，并采取有效措施，防止危害的发生。</p><p>　　第十五条 组团社组织旅游者出国旅游，应当选择在目的地国家依法设立并具有良好信誉的旅行社（以下简称境外接待社），并与之订立书面合同后，方可委托其承担接待工作。</p><p>　　第十六条 组团社及其旅游团队领队应当要求境外接待社按照约定的团队活动计划安排旅游活动，并要求其不得组织旅游者参与涉及色情、赌博、毒品内容的活动或者危险性活动，不得擅自改变行程、减少旅游项目，不得强迫或者变相强迫旅游者参加额外付费项目。境外接待社违反组团社及其旅游团队领队根据前款规定提出的要求时，组团社及其旅游团队领队应当予以制止。</p><p>　　第十七条 旅游团队领队应当向旅游者介绍旅游目的地国家的相关法律、风俗习惯以及其他有关注意事项，并尊重旅游者的人格尊严、宗教信仰、民族风俗和生活习惯。</p><p>　　第十八条 旅游团队领队在带领旅游者旅行、游览过程中，应当就可能危及旅游者人身安全的情况，向旅游者作出真实说明和明确警示，并按照组团社的要求采取有效措施，防止危害的发生。</p><p>　　第十九条 旅游团队在境外遇到特殊困难和安全问题时，领队应当及时向组团社和中国驻所在国家使领馆报告；组团社应当及时向旅游行政部门和公安机关报告。</p><p>　　第二十条 旅游团队领队不得与境外接待社、导游及为旅游者提供商品或者服务的其他经营者串通欺骗、胁迫旅游者消费，不得向境外接待社、导游及其他为旅游者提供商品或者服务的经营者索要回扣、提成或者收受其财物。</p><p>　　第二十一条 旅游者应当遵守旅游目的地国家的法律，尊重当地的风俗习惯，并服从旅游团队领队的统一管理。</p><p>　　第二十二条 严禁旅游者在境外滞留不归。旅游者在境外滞留不归的，旅游团队领队应当及时向组团社和中国驻所在国家使领馆报告，组团社应当及时向公安机关和旅游行政部门报告。有关部门处理有关事项时，组团社有义务予以协助。</p><p>　　第二十三条 旅游者对组团社或者旅游团队领队违反本办法规定的行为，有权向旅游行政部门投诉。</p><p>　　第二十四条 因组团社或者其委托的境外接待社违约，使旅游者合法权益受到损害的，组团社应当依法对旅游者承担赔偿责任。</p><p>　　第二十五条 组团社有下列情形之一的，旅游行政部门可以暂停其经营出国旅游业务；情节严重的，取消其出国旅游业务经营资格：</p><p>　　（一）入境旅游业绩下降的；</p><p>　　（二）因自身原因，在１年内未能正常开展出国旅游业务的；</p><p>　　（三）因出国旅游服务质量问题被投诉并经查实的；</p><p>　　（四）有逃汇、非法套汇行为的；</p><p>（五）以旅游名义弄虚作假，骗取护照、签证等出入境证件或者送他人出境的；</p><p>（六）国务院旅游行政部门认定的影响中国公民出国旅游秩序的其他行为。</p><p>　　第二十六条 任何单位和个人违反本办法第四条的规定，未经批准擅自经营或者以商务、考察、培训等方式变相经营出国旅游业务的，由旅游行政部门责令停止非法经营，没收违法所得，并处违法所得２倍以上５倍以下的罚款。</p><p>　　第二十七条 组团社违反本办法第十条的规定，不为旅游团队安排专职领队的，由旅游行政部门责令改正，并处５０００元以上２万元以下的罚款，可以暂停其出国旅游业务经营资格；多次不安排专职领队的，并取消其出国旅游业务经营资格。</p><p>　　第二十八条 组团社违反本办法第十二条的规定，向旅游者提供虚假服务信息或者低于成本报价的，由工商行政管理部门依照《中华人民共和国消费者权益保护法》、《中华人民共和国反不正当竞争法》的有关规定给予处罚。</p><p>　　第二十九条 组团社或者旅游团队领队违反本办法第十四条第二款、第十八条的规定，对可能危及人身安全的情况未向旅游者作出真实说明和明确警示，或者未采取防止危害发生的措施的，由旅游行政部门责令改正，给予警告；情节严重的，对组团社暂停其出国旅游业务经营资格，并处５０００元以上２万元以下的罚款，对旅游团队领队可以暂扣直至吊销其领队证；造成人身伤亡事故的，依法追究刑事责任，并承担赔偿责任。</p><p>　　第三十条 组团社或者旅游团队领队违反本办法第十六条的规定，未要求境外接待社不得组织旅游者参与涉及色情、赌博、毒品内容的活动或者危险性活动，未要求其不得擅自改变行程、减少旅游项目、强迫或者变相强迫旅游者参加额外付费项目，或者在境外接待社违反前述要求时未制止的，由旅游行政部门对组团社处组织该旅游团队所收取费用２倍以上５倍以下的罚款，并暂停其出国旅游业务经营资格，对旅游团队领队暂扣其领队证；造成恶劣影响的，对组团社取消其出国旅游业务经营资格，对旅游团队领队吊销其领队证。</p><p>　　第三十一条 旅游团队领队违反本办法第二十条的规定，与境外接待社、导游及为旅游者提供商品或者服务的其他经营者串通欺骗、胁迫旅游者消费或者向境外接待社、导游和其他为旅游者提供商品或者服务的经营者索要回扣、提成或者收受其财物的，由旅游行政部门责令改正，没收索要的回扣、提成或者收受的财物，并处索要的回扣、提成或者收受的财物价值２倍以上５倍以下的罚款；情节严重的，并吊销其领队证。</p><p>　　第三十二条 违反本办法第二十二条的规定，旅游者在境外滞留不归，旅游团队领队不及时向组团社和中国驻所在国家使领馆报告，或者组团社不及时向有关部门报告的，由旅游行政部门给予警告，对旅游团队领队可以暂扣其领队证，对组团社可以暂停其出国旅游业务经营资格。旅游者因滞留不归被遣返回国的，由公安机关吊销其护照。</p><p>　　第三十三条 本办法自２００２年７月１日起施行。</p><p>　　国务院１９９７年３月１７日批准，国家旅游局、公安部１９９７年７月１日发布的《中国公民自费出国旅游管理暂行办法》同时废止。</p><p>&nbsp;</p>');
INSERT INTO DS_CMS_PAGE (ID, SITEID, CATEGORYID, STATUS, TITLE, URL, METAKEYWORDS, METADESCRIPTION, SUMMARY, RELEASETIME, RELEASESOURCE, RELEASEUSER, IMG, IMGTOP, PAGETOP, CONTENT) VALUES (997, 5, 19, 1, '导游人员管理条例', '/a/lyfggz/997.html', '', '', '导游人员管理条例', '2015-07-21 11:25:20', NULL, NULL, '', 0, 0, '<p>1999年5月14日国务院令第263号发布</p><p>　　第一条　为了规范导游活动，保障旅游者和导游人员的合法权益，促进旅游业的健康发展，制定本条例。</p><p>　　第二条　本条例所称导游人员，是指依照本条例的规定取得导游证，接受旅行社委派，为旅游者提供向导、讲解及相关旅游服务的人员。</p><p>　　第三条　国家实行全国统一的导游人员资格考试制度。</p><p>　　具有高级中学、中等专业学校或者以上学历，身体健康，具有适应导游需要的基本知识和语言表达能力的中华人民共和国公民，可以参加导游人员资格考试；经考试合格的，由国务院旅游行政部门或者国务院旅游行政部门委托省、自治区、直辖市人民政府旅游行政部门颁发导游人员资格证书。</p><p>　　第四条　在中华人民共和国境内从事导游活动，必须取得导游证。</p><p>　　取得导游人员资格证书的，经与旅行社订立劳动合同或者在导游服务公司登记，方可持所订立的劳动合同或者登记证明材料，向省、自治区、直辖市人民政府旅游行政部门申请领取导游证。</p><p>　　具有特定语种语言能力的人员，虽未取得导游人员资格证书，旅行社需要聘请临时从事导游活动的，由旅行社向省、自治区、直辖市人民政府旅游行政部门申请领取临时导游证。</p><p>　　导游证和临时导游证的样式规格，由国务院旅游行政部门规定。</p><p>　　第五条　有下列情形之一的，不得颁发导游证：</p><p>　　（一）无民事行为能力或者限制民事行为能力的；</p><p>　　（二）患有传染性疾病的；</p><p>　　（三）受过刑事处罚的，过失犯罪的除外；</p><p>　　（四）被吊销导游证的。</p><p>　　第六条　省、自治区、直辖市人民政府旅游行政部门应当自收到申请领取导游证之日起15日内，颁发导游证；发现有本条例第五条规定情形，不予颁发导游证的，应当书面通知申请人。</p><p>　　第七条　导游人员应当不断提高自身业务素质和职业技能。</p><p>　　国家对导游人员实行等级考核制度。导游人员等级考核标准和考核办法，由国务院旅游行政部门制定。</p><p>　　第八条　导游人员进行导游活动时，应当佩戴导游证。</p><p>　　导游证的有效期限为3年。导游证持有人需要在有效期满后继续从事导游活动的，应当在有效期限届满3个月前，向省、自治区、直辖市人民政府旅游行政部门申请办理换发导游证手续。</p><p>　　临时导游证的有效期限最长不超过3个月，并不得展期。</p><p>　　第九条　导游人员进行导游活动，必须经旅行社委派。</p><p>　　导游人员不得私自承揽或者以其他任何方式直接承揽导游业务，进行导游活动。</p><p>　　第十条　导游人员进行导游活动时，其人格尊严应当受到尊重，其人身安全不受侵犯。</p><p>　　导游人员有权拒绝旅游者提出的侮辱其人格尊严或者违反其职业道德的不合理要求。</p><p>　　第十一条　导游人员进行导游活动时，应当自觉维护国家利益和民族尊严，不得有损害国家利益和民族尊严的言行。</p><p>　　第十二条　导游人员进行导游活动时，应当遵守职业道德，着装整洁，礼貌待人，尊重旅游者的宗教信仰、民族风俗和生活习惯。</p><p>　　导游人员进行导游活动时，应当向旅游者讲解旅游地点的人文和自然情况，介绍风土人情和习俗；但是，不得迎合个别 旅游者的低级趣味，在讲解、介绍中掺杂庸俗下流的内容。</p><p>　　第十三条　导游人员应当严格按照旅行社确定的接待计划，安排旅游者的旅行、游览活动，不得擅自增加、减少旅游项目或者中止导游活动。</p><p>　　导游人员在引导旅游者旅行、游览过程中，遇有可能危及旅游者人身安全的紧急情形时，经征得多数旅游者的同意，可以调整或者变更接待计划，但是应当立即报告旅行社。</p><p>　　第十四条　导游人员在引导旅游者旅行、游览过程中，应当就可能发生危及旅游者人身、财物安全的情况，向旅游者作出真实说明和明确警示，并按照旅行社的要求采取防止危害发生的措施。</p><p>　　第十五条　导游人员进行导游活动，不得向旅游者兜售物品或者购买旅游者的物品，不得以明示或者暗示的方式向旅游者索要小费。</p><p>　　第十六条　导游人员进行导游活动，不得欺骗、胁迫旅游者消费或者与经营者串通欺骗、胁迫旅游者消费。</p><p>　　第十七条　旅游者对导游人员违反本条例规定的行为，有权向旅游行政部门投诉。</p><p>　　第十八条　无导游证进行导游活动的，由旅游行政部门责令改正并予以公告，处1000元以上3万元以下的罚款；有违法所得的，并处没收违法所得。</p><p>　　第十九条　导游人员未经旅行社委派，私自承揽或者以其他任何方式直接承揽导游业务，进行导游活动的，由旅游行政部门责令改正，处1000元以上3万元以下的罚款；有违法所得的，并处没收违法所得；情节严重的，由省、自治区、直辖市人民政府旅游行政部门吊销导游证并予以公告。</p><p>　　第二十条　导游人员进行导游活动时，有损害国家利益和民族尊严的言行的，由旅游行政部门责令改正；情节严重的，由省、自治区、直辖市人民政府旅游行政部门吊销导游证并予以公告；对该导游人员所在的旅行社给予警告直至责令停业整顿。</p><p>　　第二十一条　导游人员进行导游活动时未佩戴导游证的， 由旅游行政部门责令改正；拒不改正的，处500元以下的罚款。</p><p>　　第二十二条　导游人员有下列情形之一的，由旅游行政部门责令改正，暂扣导游证3至6个月；情节严重的，由省、自治区、直辖市人民政府旅游行政部门吊销导游证并予以公告：</p><p>　　（一）擅自增加或者减少旅游项目的；</p><p>　　（二）擅自变更接待计划的；</p><p>　　（三）擅自中止导游活动的。</p><p>　　第二十三条　导游人员进行导游活动，向旅游者兜售物品或者购买旅游者的物品的，或者以明示或者暗示的方式向旅游者索要小费的，由旅游行政部门责令改正，处1000元以上3万元以下的罚款；有违法所得的，并处没收违法所得；情节严重的，由省、自治区、直辖市人民政府旅游行政部门吊销导游证并予以公告；对委派该导游人员的旅行社给予警告直至责令停业整顿。</p><p>第二十四条　导游人员进行导游活动，欺骗、胁迫旅游者消费或者与经营者串通欺骗、胁迫旅游者消费的，由旅游行政部门责令改正，处1000元以上3万元以下的罚款；有违法所得的，并处没收违法所得；情节严重的，由省、自治区、直辖市人民政府旅游行政部门吊销导游证并予以公告；对委派该导游人员的旅行社给予警告直至责令停业整顿；构成犯罪的，依法追究刑事责任。</p><p>　　第二十五条　旅游行政部门工作人员玩忽职守、滥用职权、徇私舞弊，构成犯罪的，依法追究刑事责任；尚不构成犯罪的，依法给予行政处分。</p><p>　　第二十六条　景点景区的导游人员管理办法，由省、自治区、直辖市人民政府参照本条例制定。</p><p>　　第二十七条　本条例自1999年10月1日起施行。1987年11月14日国务院批准、1987年12月1日国家旅游局发布的《导游人员管理暂行规定》同时废止。</p><p>&nbsp;</p>');
INSERT INTO DS_CMS_PAGE (ID, SITEID, CATEGORYID, STATUS, TITLE, URL, METAKEYWORDS, METADESCRIPTION, SUMMARY, RELEASETIME, RELEASESOURCE, RELEASEUSER, IMG, IMGTOP, PAGETOP, CONTENT) VALUES (998, 5, 19, 1, '导游人员管理实施办法', '/a/lyfggz/998.html', '', '', '导游人员管理实施办法', '2015-07-21 11:28:08', NULL, NULL, '', 0, 0, '<p>中华人民共和国国家旅游局令</p><p>　　第15号</p><p>　　《导游人员管理实施办法》已经2001年12月26日国家旅游局局长办公会议讨论通过，现予以公布，自2002年1月1日起施行。</p><p>　　国家旅游局局长：何光</p><p>　　2002年12月27日&nbsp;&nbsp;&nbsp; </p><p>　　导游人员管理实施办法</p><p>　　第一章　　总 则</p><p>　　第一条　　为了加强导游队伍建设，维护旅游市场秩序和旅游者的合法权益，依据《导游人员管理条例》和《旅行社管理条例》，制定本办法。</p><p>　　第二条　　旅游行政管理部门对导游人员实行分级管理。</p><p>　　第三条　　旅游行政管理部门对导游人员实行资格考试制度和等级考核制度。</p><p>　　第四条　　旅游行政管理部门对导游人员实行计分管理制度和年度审核制度。</p><p>　　第二章　　导游资格证和导游证</p><p>　　第五条　　国家实行统一的导游人员资格考试制度。经考试合格者，方可取得导游资格证。</p><p>　　第六条　　国务院旅游行政管理部门负责制定全国导游人员资格考试的政策、标准和对各地考试工作的监督管理。</p><p>　　省级旅游行政管理部门负责组织、实施本行政区域内导游人员资格考试工作。</p><p>　　直辖市、计划单列市、副省级城市负责本地区导游人员的考试工作。</p><p>　　第七条　　坚持考试和培训分开、培训自愿的原则，不得强迫考生参加培训。</p><p>　　第八条　　经考试合格的，由组织考试的旅游行政管理部门在考试结束之日起30个工作日内颁发《导游人员资格证》。</p><p>　　获得资格证3年未从业的，资格证自动失效。</p><p>　　第九条　　获得导游人员资格证、并在一家旅行社或导游管理服务机构注册的，持劳动合同或导游管理服务机构登记证明材料向所在地旅游行政管理部门申请办理导游证。</p><p>　　所在地旅游行政管理部门是指直辖市、计划单列市、副省级旅游行政管理部门以及有相应的导游规模、有相应的导游管理服务机构、有稳定的执法队伍的地市级以上旅游行政管理部门。</p><p>　　第十条　　取得《导游人员资格证》的人员申请办理导游证，须参加颁发导游证的旅游行政管理部门举办的岗前培训考核。</p><p>　　第十一条　　《导游人员资格证》和导游证由国务院旅游行政管理部门统一印制，在中华人民共和国全国范围内使用。</p><p>　　任何单位不得另行颁发其他形式的导游证。</p><p>　　第三章　　导游人员的计分管理</p><p>　　第十二条　　国家对导游人员实行计分管理。</p><p>　　国务院旅游行政管理部门负责制定全国导游人员计分管理政策并组织实施、监督检查。</p><p>　　省级旅游行政管理部门负责本行政区域内导游人员计分管理的组织实施和监督检查。</p><p>　　所在地旅游行政管理部门在本行政区域内负责导游人员计分管理的具体执行。</p><p>　　第十三条　　导游人员计分办法实行年度10分制。</p><p>　　第十四条　　导游人员在导游活动中有下列情形之一的，扣除10分：</p><p>　　(一)　有损害国家利益和民族尊严的言行的;</p><p>　　(二)　诱导或安排旅游者参加黄、赌、毒活动项目的;</p><p>　　(三)　有殴打或谩骂旅游者行为的;</p><p>　　(四)　欺骗、胁迫旅游者消费的;</p><p>　　(五)　未通过年审继续从事导游业务的;</p><p>　　(六)　因自身原因造成旅游团重大危害和损失的。</p><p>　　第十五条　　导游人员在导游活动中有下列情形之一的，扣除8分：</p><p>　　(一)　拒绝、逃避检查，或者欺骗检查人员的;</p><p>　　(二)　擅自增加或者减少旅游项目的;</p><p>　　(三)　擅自终止导游活动的;</p><p>　　(四)　讲解中掺杂庸俗、下流、迷信内容的;</p><p>　　(五)　未经旅行社委派私自承揽或者以其他任何方式直接承揽导游业务的。</p><p>　　第十六条　　导游人员在导游活动中有下列情形之一的，扣除6分：</p><p>　　(一)　向旅游者兜售物品或购买旅游者物品的;</p><p>　　(二)　以明示或者暗示的方式向旅游者索要小费的;</p><p>　　(三)　因自身原因漏接漏送或误接误送旅游团的;</p><p>　　(四)　讲解质量差或不讲解的;</p><p>　　(五)　私自转借导游证供他人使用的;</p><p>　　(六)　发生重大安全事故不积极配合有关部门救助的。</p><p>　　第十七条　　导游人员在导游活动中有下列情形之一的，扣除4分：</p><p>　　(一)　私自带人随团游览的;</p><p>　　(二)　无故不随团活动的;</p><p>　　(三)　在导游活动中未佩带导游证或未携带计分卡;</p><p>　　(四)　不尊重旅游者宗教信仰和民族风俗。</p><p>　　第十八条　　导游人员在导游活动中有下列情形之一的，扣除2分：</p><p>　　(一)　未按规定时间到岗的;</p><p>　　(二)　10人以上团队未打接待社社旗的;</p><p>　　(三)　未携带正规接待计划;</p><p>　　(四)　接站未出示旅行社标识的;</p><p>　　(五)　仪表、着装不整洁的;</p><p>　　(六)　讲解中吸烟、吃东西的。</p><p>　　第十九条　　导游人员10分分值被扣完后，由最后扣分的旅游行政执法单位暂时保留其导游证，并出具保留导游证证明，并于10日内通报导游人员所在地旅游行政管理部门和登记注册单位。正在带团过程中的导游人员，可持旅游执法单位出具的保留证明完成团队剩余行程。</p><p>　　第二十条　　对导游人员的违法、违规行为除扣减其相应分值外，依法应予处罚的，依据有关法律给予处罚。</p><p>　　导游人员通过年审后,年审单位应核消其遗留分值,重新输入初始分值。</p><p>　　第二十一条　　旅游行政执法人员玩忽职守、不按照规定随意进行扣分或处罚的，由上级旅游行政管理部门提出批评和通报，本级旅游行政管理部门给予行政处分。</p><p>　　第四章　　导游人员的年审管理</p><p>　　第二十二条　　国家对导游人员实行年度审核制度。导游人员必须参加年审。</p><p>　　国务院旅游行政管理部门负责制定全国导游人员年审工作政策，组织实施并监督检查。</p><p>　　省级旅游行政管理部门负责组织、指导本行政区域内导游人员年审工作并监督检查。</p><p>　　所在地旅游行政管理部门具体负责组织实施对导游人员的年审工作。</p><p>　　第二十三条　　年审以考评为主，考评的内容应包括：当年从事导游业务情况、扣分情况、接受行政处罚情况、游客反映情况等。考评等级为通过年审、暂缓通过年审和不予通过年审三种。</p><p>　　第二十四条　　一次扣分达到10分，不予通过年审。</p><p>　　累计扣分达到10分的，暂缓通过年审。</p><p>　　一次被扣8分的，全行业通报。</p><p>　　一次被扣6分的，警告批评。</p><p>　　暂缓通过年审的，通过培训和整改后，方可重新上岗。</p><p>　　第二十五条　　导游人员必须参加所在地旅游行政管理部门举办的年审培训。培训时间应根据导游业务需要灵活安排。每年累计培训时间不得少于56小时。</p><p>　　第二十六条　　旅行社或导游管理服务机构应为注册的导游人员建立档案，对导游人员进行工作培训和指导，建立对导游人员工作情况的检查、考核和奖惩的内部管理机制，接受并处理对导游人员的投诉，负责对导游人员年审的初评。</p><p>　　第五章　　导游人员的等级考核</p><p>　　第二十七条　　国家对导游人员实行等级考核制度。导游人员分为初级、中级、高级、特级四个等级。</p><p>　　第二十八条　　初级导游和中级导游考核由省级旅游行政管理部门或其委托的地市级旅游行政管理部门组织评定;高级导游和特级导游由国务院旅游行政管理部门组织评定。</p><p>　　第二十九条　　由省部级以上单位组织导游评比或竞赛获得最佳称号的导游人员，报国务院旅游行政管理部门批准后，可晋升一级导游等级。</p><p>　　导游等级评定标准和办法由国务院旅游行政管理部门另行制定。</p><p>　　第六章　　附 则</p><p>　　第三十条　　本办法自2002年1月1日起施行。</p><p>　　第三十一条　本办法由国家旅游局负责解释。</p><p>　　附:国家旅游局关于修订《导游人员管理实施办法》的决定</p><p>　　为了适应行政审批制度改革后有关导游人员等级考核评定的需要，促进导游员队伍建设，经国家旅游局局长办公会讨论通过，决定对《导游人员管理实施办法》作如下修订，自2005年7月3日起施行：</p><p>　　一、将第二十八条修改为：国家旅游局组织设立全国导游人员等级考核评定委员会。全国导游人员等级考核评定委员会负责全国导游人员等级考核评定工作的组织实施。</p><p>　　省、自治区、直辖市和新疆生产建设兵团旅游行政部门组织设立导游人员等级考核评定办公室，在全国导游人员等级考核评定委员会的授权和指导下开展相应的工作。</p><p>　　二、将第二十九条修改为：参加省部级以上单位组织的导游技能大赛获得最佳名次的导游人员，报全国导游人员等级考核评定委员会批准后，可晋升一级导游人员等级。一人多次获奖只能晋升一次，晋升的最高等级为高级。</p><p><br /><a href=\"http://www.gzly.gov.cn/f/file/201505/1432697412597.doc\">导游人员管理实施办法.doc<br /></a></p>');
INSERT INTO DS_CMS_PAGE (ID, SITEID, CATEGORYID, STATUS, TITLE, URL, METAKEYWORDS, METADESCRIPTION, SUMMARY, RELEASETIME, RELEASESOURCE, RELEASEUSER, IMG, IMGTOP, PAGETOP, CONTENT) VALUES (999, 5, 19, 1, '旅游投诉处理办法', '/a/lyfggz/999.html', '', '', '旅游投诉处理办法', '2015-07-21 11:30:57', NULL, NULL, '', 0, 0, '<p>国家旅游局令</p><p>　　第32号</p><p>　　《旅游投诉处理办法》已经2010年1月4日国家旅游局第1次局长办公会议审议通过。现予公布，自2010年7月1日起施行。</p><p>　　国家旅游局局长：邵琪伟&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </p><p>　　二○一○年五月五日&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </p><p>　　旅游投诉处理办法</p><p>　　第一章 总则</p><p>　　第一条 为了维护旅游者和旅游经营者的合法权益，依法公正处理旅游投诉，依据《中华人民共和国消费者权益保护法》、《旅行社条例》、《导游人员管理条例》和《中国公民出国旅游管理办法》等法律、法规，制定本办法。</p><p>　　第二条 本办法所称旅游投诉，是指旅游者认为旅游经营者损害其合法权益，请求旅游行政管理部门、旅游质量监督管理机构或者旅游执法机构(以下统称“旅游投诉处理机构”)，对双方发生的民事争议进行处理的行为。</p><p>　　第三条 旅游投诉处理机构应当在其职责范围内处理旅游投诉。</p><p>　　地方各级旅游行政主管部门应当在本级人民政府的领导下，建立、健全相关行政管理部门共同处理旅游投诉的工作机制。</p><p>　　第四条 旅游投诉处理机构在处理旅游投诉中，发现被投诉人或者其从业人员有违法或犯罪行为的，应当按照法律、法规和规章的规定，作出行政处罚、向有关行政管理部门提出行政处罚建议或者移送司法机关。</p><p>　　第二章 管辖</p><p>　　第五条 旅游投诉由旅游合同签订地或者被投诉人所在地县级以上地方旅游投诉处理机构管辖。</p><p>　　需要立即制止、纠正被投诉人的损害行为的，应当由损害行为发生地旅游投诉处理机构管辖。</p><p>　　第六条 上级旅游投诉处理机构有权处理下级旅游投诉处理机构管辖的投诉案件。</p><p>　　第七条 发生管辖争议的，旅游投诉处理机构可以协商确定，或者报请共同的上级旅游投诉处理机构指定管辖。</p><p>　　第三章 受理</p><p>　　第八条 投诉人可以就下列事项向旅游投诉处理机构投诉：</p><p>　　(一)认为旅游经营者违反合同约定的;</p><p>　　(二)因旅游经营者的责任致使投诉人人身、财产受到损害的;</p><p>　　(三)因不可抗力、意外事故致使旅游合同不能履行或者不能完全履行，投诉人与被投诉人发生争议的;</p><p>　　(四)其他损害旅游者合法权益的。</p><p>　　第九条 下列情形不予受理：</p><p>　　(一)人民法院、仲裁机构、其他行政管理部门或者社会调解机构已经受理或者处理的;</p><p>　　(二)旅游投诉处理机构已经作出处理，且没有新情况、新理由的;</p><p>　　(三)不属于旅游投诉处理机构职责范围或者管辖范围的;</p><p>　　(四)超过旅游合同结束之日90天的;</p><p>　　(五)不符合本办法第十条规定的旅游投诉条件的;</p><p>　　(六)本办法规定情形之外的其他经济纠纷。</p><p>　　属于前款第(三)项规定的情形的，旅游投诉处理机构应当及时告知投诉人向有管辖权的旅游投诉处理机构或者有关行政管理部门投诉。</p><p>　　第十条 旅游投诉应当符合下列条件：</p><p>　　(一)投诉人与投诉事项有直接利害关系;</p><p>　　(二)有明确的被投诉人、具体的投诉请求、事实和理由。</p><p>　　第十一条 旅游投诉一般应当采取书面形式，一式两份，并载明下列事项：</p><p>　　(一)投诉人的姓名、性别、国籍、通讯地址、邮政编码、联系电话及投诉日期;</p><p>　　(二)被投诉人的名称、所在地;</p><p>　　(三)投诉的要求、理由及相关的事实根据。</p><p>　　第十二条 投诉事项比较简单的，投诉人可以口头投诉，由旅游投诉处理机构进行记录或者登记，并告知被投诉人;对于不符合受理条件的投诉，旅游投诉处理机构可以口头告知投诉人不予受理及其理由，并进行记录或者登记。</p><p>　　第十三条 投诉人委托代理人进行投诉活动的，应当向旅游投诉处理机构提交授权委托书，并载明委托权限。</p><p>　　第十四条 投诉人4人以上，以同一事由投诉同一被投诉人的，为共同投诉。</p><p>　　共同投诉可以由投诉人推选1至3名代表进行投诉。代表人参加旅游投诉处理机构处理投诉过程的行为，对全体投诉人发生效力，但代表人变更、放弃投诉请求或者进行和解，应当经全体投诉人同意。</p><p>　　第十五条 旅游投诉处理机构接到投诉，应当在5个工作日内作出以下处理：</p><p>　　(一)投诉符合本办法的，予以受理;</p><p>　　(二)投诉不符合本办法的，应当向投诉人送达《旅游投诉不予受理通知书》，告知不予受理的理由;</p><p>　　(三)依照有关法律、法规和本办法规定，本机构无管辖权的，应当以《旅游投诉转办通知书》或者《旅游投诉转办函》，将投诉材料转交有管辖权的旅游投诉处理机构或者其他有关行政管理部门，并书面告知投诉人。</p><p>　　第四章 处理</p><p>　　第十六条 旅游投诉处理机构处理旅游投诉，除本办法另有规定外，实行调解制度。</p><p>　　旅游投诉处理机构应当在查明事实的基础上，遵循自愿、合法的原则进行调解，促使投诉人与被投诉人相互谅解，达成协议。</p><p>　　第十七条 旅游投诉处理机构处理旅游投诉，应当立案办理，填写《旅游投诉立案表》，并附有关投诉材料，在受理投诉之日起5个工作日内，将《旅游投诉受理通知书》和投诉书副本送达被投诉人。</p><p>　　对于事实清楚、应当即时制止或者纠正被投诉人损害行为的，可以不填写《旅游投诉立案表》和向被投诉人送达《旅游投诉受理通知书》，但应当对处理情况进行记录存档。</p><p>　　第十八条 被投诉人应当在接到通知之日起10日内作出书面答复，提出答辩的事实、理由和证据。</p><p>　　第十九条 投诉人和被投诉人应当对自己的投诉或者答辩提供证据。</p><p>　　第二十条 旅游投诉处理机构应当对双方当事人提出的事实、理由及证据进行审查。</p><p>　　旅游投诉处理机构认为有必要收集新的证据，可以根据有关法律、法规的规定，自行收集或者召集有关当事人进行调查。</p><p>　　第二十一条 需要委托其他旅游投诉处理机构协助调查、取证的，应当出具《旅游投诉调查取证委托书》，受委托的旅游投诉处理机构应当予以协助。</p><p>　　第二十二条 对专门性事项需要鉴定或者检测的，可以由当事人双方约定的鉴定或者检测部门鉴定。没有约定的，当事人一方可以自行向法定鉴定或者检测机构申请鉴定或者检测。</p><p>　　鉴定、检测费用按双方约定承担。没有约定的，由鉴定、检测申请方先行承担;达成调解协议后，按调解协议承担。</p><p>　　鉴定、检测的时间不计入投诉处理时间。</p><p>　　第二十三条 在投诉处理过程中，投诉人与被投诉人自行和解的，应当将和解结果告知旅游投诉处理机构;旅游投诉处理机构在核实后应当予以记录并由双方当事人、投诉处理人员签名或者盖章。</p><p>　　第二十四条 旅游投诉处理机构受理投诉后，应当积极安排当事双方进行调解，提出调解方案，促成双方达成调解协议。</p><p>　　第二十五条 旅游投诉处理机构应当在受理旅游投诉之日起60日内，作出以下处理：</p><p>　　(一)双方达成调解协议的，应当制作《旅游投诉调解书》，载明投诉请求、查明的事实、处理过程和调解结果，由当事人双方签字并加盖旅游投诉处理机构印章;</p><p>　　(二)调解不成的，终止调解，旅游投诉处理机构应当向双方当事人出具《旅游投诉终止调解书》。</p><p>　　调解不成的，或者调解书生效后没有执行的，投诉人可以按照国家法律、法规的规定，向仲裁机构申请仲裁或者向人民法院提起诉讼。</p><p>　　第二十六条 在下列情形下，经旅游投诉处理机构调解，投诉人与旅行社不能达成调解协议的，旅游投诉处理机构应当做出划拨旅行社质量保证金赔偿的决定，或向旅游行政管理部门提出划拨旅行社质量保证金的建议：</p><p>　　(一)旅行社因解散、破产或者其他原因造成旅游者预交旅游费用损失的;</p><p>　　(二)因旅行社中止履行旅游合同义务、造成旅游者滞留，而实际发生了交通、食宿或返程等必要及合理费用的。</p><p>　　第二十七条 旅游投诉处理机构应当每季度公布旅游者的投诉信息。</p><p>　　第二十八条 旅游投诉处理机构应当使用统一规范的旅游投诉处理信息系统。</p><p>　　第二十九条 旅游投诉处理机构应当为受理的投诉制作档案并妥善保管相关资料。</p><p>　　第三十条 本办法中有关文书式样，由国家旅游局统一制定。</p><p>　　第五章 附则</p><p>　　第三十一条 本办法由国家旅游局负责解释。</p><p>　　第三十二条 本办法自2010年7月1 日起施行。《旅行社质量保证金暂行规定》、《旅行社质量保证金暂行规定实施细则》、《旅行社质量保证金赔偿暂行办法》同时废止。</p><p><br /><a href=\"http://www.gzly.gov.cn/f/file/201505/1432697600296.doc\">旅游投诉处理办法.doc<br /></a></p>');
INSERT INTO DS_CMS_PAGE (ID, SITEID, CATEGORYID, STATUS, TITLE, URL, METAKEYWORDS, METADESCRIPTION, SUMMARY, RELEASETIME, RELEASESOURCE, RELEASEUSER, IMG, IMGTOP, PAGETOP, CONTENT) VALUES (1000, 5, 19, 1, '出境旅游领队人员管理办法', '/a/lyfggz/1000.html', '', '', '出境旅游领队人员管理办法', '2015-07-21 11:33:49', NULL, NULL, '', 0, 0, '<p>中华人民共和国国家旅游局令</p><p>　　第　18　号</p><p>　　《出境旅游领队人员管理办法》已经 2002年10月14日国家旅游局局长办公会议讨论通过，现予公布实施。</p><p>　　局　长 何光&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </p><p>　　二○○二年十月二十八日</p><p>　　出境旅游领队人员管理办法</p><p>　　第一条 　为了加强对出境旅游领队人员的管理，规范其从业行为，维护出境旅游者的合法权益，促进出境旅游的健康发展，根据《中国公民出国旅游管理办法》和有关规定，制定本办法。</p><p>　　第二条 　本办法所称出境旅游领队人员(以下简称“领队人员”)，是指依照本办法规定取得出境旅游领队证(以下简称“领队证”)，接受具有出境旅游业务经营权的国际旅行社(以下简称“组团社”)的委派，从事出境旅游领队业务的人员。</p><p>　　本办法所称领队业务，是指为出境旅游团提供旅途全程陪同和有关服务;作为组团社的代表，协同境外接待旅行社(以下简称“接待社”)完成旅游计划安排;以及协调处理旅游过程中相关事务等活动。</p><p>　　第三条 　申请领队证的人员，应当符合下列条件：</p><p>　　(一)有完全民事行为能力的中华人民共和国公民;</p><p>　　(二)热爱祖国，遵纪守法;</p><p>　　(三)可切实负起领队责任的旅行社人员;</p><p>　　(四)掌握旅游目的地国家或地区的有关情况。</p><p>　　第四条 　组团社要负责做好申请领队证人员的资格审查和业务培训。</p><p>　　业务培训的内容包括：思想道德教育;涉外纪律教育;旅游政策法规;旅游目的地国家的基本情况;领队人员的义务与职责。</p><p>　　对已经领取领队证的人员，组团社要继续加强思想教育和业务培训，建立严格的工作制度和管理制度，并认真贯彻执行。</p><p>　　第五条 　领队证由组团社向所在地的省级或经授权的地市级以上旅游行政管理部门申领，并提交下列材料：申请领队证人员登记表;组团社出具的胜任领队工作的证明;申请领队证人员业务培训证明。</p><p>　　旅游行政管理部门应当自收到申请材料之日起15个工作日内，对符合条件的申请领队证人员颁发领队证，并予以登记备案。</p><p>　　旅游行政管理部门要根据组团社的正当业务需求合理发放领队证。</p><p>　　第六条 　领队证由国家旅游局统一样式并制作，由组团社所在地的省级或经授权的地市级以上旅游行政管理部门发放。</p><p>　　领队证不得伪造、涂改、出借或转让。</p><p>　　领队证的有效期为三年。凡需要在领队证有效期届满后继续从事领队业务的，应当在届满前半年由组团社向旅游行政管理部门申请登记换发领队证。</p><p>　　领队人员遗失领队证的，应当及时报告旅游行政管理部门，并声明作废，然后申请补发;领队证损坏的，应及时申请换发。</p><p>　　被取消领队人员资格的人员，不得再次申请领队登记。</p><p>　　第七条 　领队人员从事领队业务，必须经组团社正式委派。</p><p>　　领队人员从事领队业务时，必须佩带领队证。</p><p>　　未取得领队证的人员，不得从事出境旅游领队业务。</p><p>　　第八条 　领队人员应当履行下列职责：</p><p>　　(一)遵守《中国公民出国旅游管理办法》中的有关规定，维护旅游者的合法权益;</p><p>　　(二)协同接待社实施旅游行程计划，协助处理旅游行程中的突发事件、纠纷及其它问题;</p><p>　　(三)为旅游者提供旅游行程服务;</p><p>　　(四)自觉维护国家利益和民族尊严，并提醒旅游者抵制任何有损国家利益和民族尊严的言行。</p><p>　　第九条 　违反本办法第四条，对申请领队证人员不进行资格审查或业务培训，或审查不严，或对领队人员、领队业务疏于管理，造成领队人员或领队业务发生问题的，由旅游行政管理部门视情节轻重，分别给予组团社警告、取消申领领队证资格、取消组团社资格等处罚。</p><p>　　第十条 　违反本办法第七条第三款规定，未取得领队证从事领队业务的，由旅游行政管理部门责令改正，有违法所得的，没收违法所得，并可处违法所得3倍以下不超过人民币3万元的罚款;没有违法所得的，可处人民币1万元以下罚款。</p><p>　　第十一条 　违反本办法第六条第二款和第七条第二款规定，领队人员伪造、涂改、出借或转让领队证，或者在从事领队业务时未佩带领队证的，由旅游行政管理部门责令改正，处人民币1万元以下的罚款;情节严重的，由旅游行政管理部门暂扣领队证3个月至1年，并不得重新换发领队证。</p><p>　　第十二条 　违反本办法第八条第一项规定的，按《中国公民出国旅游管理办法》的有关规定处罚。</p><p>　　第十三条 　违反本办法第八条第二、三、四项规定的，由旅游行政管理部门责令改正，并可暂扣领队证3个月至1年;造成重大影响或产生严重后果的，由旅游行政管理部门撤消其领队登记，并不得再次申请领队登记，同时要追究组团社责任。</p><p>　　第十四条 　旅游行政管理部门工作人员玩忽职守、滥用职权、徇私舞弊，构成犯罪的，依法追究刑事责任;未构成犯罪的，依法给予行政处分。</p><p>　　第十五条 　本办法由国家旅游局负责解释。</p><p>　　第十六条 　本办法自发布之日起施行。</p><p><br /><a href=\"http://www.gzly.gov.cn/f/file/201505/1432697728020.doc\">出境旅游领队人员管理办法.doc<br /></a></p>');
INSERT INTO DS_CMS_PAGE (ID, SITEID, CATEGORYID, STATUS, TITLE, URL, METAKEYWORDS, METADESCRIPTION, SUMMARY, RELEASETIME, RELEASESOURCE, RELEASEUSER, IMG, IMGTOP, PAGETOP, CONTENT) VALUES (1001, 5, 19, 1, '旅行社责任保险管理办法', '/a/lyfggz/1001.html', '', '', '旅行社责任保险管理办法', '2015-07-21 11:35:38', NULL, NULL, '', 0, 0, '<p>　国 家 旅 游 局 令</p><p>　　中国保险监督管理委员会</p><p>　　第 35 号</p><p>　　《旅行社责任保险管理办法》已经2010年7月29日国家旅游局第9次局长办公会议、2010年11月8日中国保险监督管理委员会主席办公会审议通过，现予公布，自2011年2月1日起施行。</p><p>　　国家旅游局局长：邵琪伟&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </p><p>　　中国保险监督管理委员会主席：吴定富&nbsp;&nbsp; </p><p>　　二〇一〇年十一月二十五日&nbsp;&nbsp;&nbsp; </p><p>　　旅行社责任保险管理办法</p><p>　　第一章 总 则</p><p>　　第一条 为保障旅游者的合法权益，根据《中华人民共和国保险法》和《旅行社条例》，制定本办法。</p><p>　　第二条 在中华人民共和国境内依法设立的旅行社，应当依照《旅行社条例》和本办法的规定，投保旅行社责任保险。</p><p>　　本办法所称旅行社责任保险，是指以旅行社因其组织的旅游活动对旅游者和受其委派并为旅游者提供服务的导游或者领队人员依法应当承担的赔偿责任为保险标的的保险。</p><p>　　第三条 投保旅行社责任保险的旅行社和承保旅行社责任保险的保险公司，应当遵守本办法。</p><p>　　第二章 投 保</p><p>　　第四条 旅行社责任保险的保险责任，应当包括旅行社在组织旅游活动中依法对旅游者的人身伤亡、财产损失承担的赔偿责任和依法对受旅行社委派并为旅游者提供服务的导游或者领队人员的人身伤亡承担的赔偿责任。</p><p>　　具体包括下列情形：</p><p>　　(一)因旅行社疏忽或过失应当承担赔偿责任的;</p><p>　　(二)因发生意外事故旅行社应当承担赔偿责任的;</p><p>　　(三)国家旅游局会同中国保险监督管理委员会(以下简称中国保监会)规定的其他情形。</p><p>　　第五条 中国保监会及其派出机构依法对旅行社责任保险的保险条款和保险费率进行管理。 第六条 旅行社责任保险的保险费率应当遵循市场化原则，并与旅行社经营风险相匹配。</p><p>　　第七条 旅行社投保旅行社责任保险的，应当与保险公司依法订立书面旅行社责任保险合同(以下简称保险合同)。</p><p>　　第八条 旅行社与保险公司订立保险合同时，双方应当依照《中华人民共和国保险法》的有关规定履行告知和说明义务。</p><p>　　第九条 订立保险合同时，保险公司不得强制旅行社投保其他商业保险。</p><p>　　第十条 保险合同成立后，旅行社按照约定交付保险费。保险公司应当及时向旅行社签发保险单或者其他保险凭证，并在保险单或者其他保险凭证中载明当事人双方约定的合同内容，同时按照约定的时间开始承担保险责任。</p><p>　　第十一条 保险合同成立后，除符合《中华人民共和国保险法》规定的情形外，保险公司不得解除保险合同。</p><p>　　第十二条 保险合同成立后，旅行社要解除保险合同的，应当同时订立新的保险合同，并书面通知所在地县级以上旅游行政管理部门，但因旅行社业务经营许可证被依法吊销或注销而解除合同的除外。</p><p>　　第十三条 保险合同解除的，保险公司应当收回保险单，并书面通知旅行社所在地县级以上旅游行政管理部门。</p><p>　　第十四条 旅行社的名称、法定代表人或者业务经营范围等重要事项变更时，应当及时通知保险公司。必要时应当依法办理保险合同变更手续。</p><p>　　第十五条 旅行社责任保险的保险期间为1年。</p><p>　　第十六条 旅行社应当在保险合同期满前及时续保。</p><p>　　第十七条 旅行社投保旅行社责任保险，可以依法自主投保，也可以有组织统一投保。</p><p>　　第三章 赔 偿</p><p>　　第十八条 旅行社在组织旅游活动中发生本办法第四条所列情形的，保险公司依法根据保险合同约定，在旅行社责任保险责任限额内予以赔偿。</p><p>　　责任限额可以根据旅行社业务经营范围、经营规模、风险管控能力、当地经济社会发展水平和旅行社自身需要，由旅行社与保险公司协商确定，但每人人身伤亡责任限额不得低于20万元人民币。</p><p>　　第十九条 旅行社组织的旅游活动中发生保险事故，旅行社或者受害的旅游者、导游、领队人员通知保险公司的，保险公司应当及时告知具体的赔偿程序等有关事项。</p><p>　　第二十条 保险事故发生后，旅行社按照保险合同请求保险公司赔偿保险金时，应当向保险公司提供其所能提供的与确认保险事故的性质、原因、损失程度等有关的证明和资料。</p><p>　　保险公司按照保险合同的约定，认为有关的证明和资料不完整的，应当及时一次性通知旅行社补充提供。</p><p>　　旅行社对旅游者、导游或者领队人员应负的赔偿责任确定的，根据旅行社的请求，保险公司应当直接向受害的旅游者、导游或者领队人员赔偿保险金。旅行社怠于请求的，受害的旅游者、导游或者领队人员有权就其应获赔偿部分直接向保险公司请求赔偿保险金。</p><p>　　第二十一条 保险公司收到赔偿保险金的请求和相关证明、资料后，应当及时做出核定;情形复杂的，应当在30日内作出核定，但合同另有约定的除外。保险公司应当将核定结果通知旅行社以及受害的旅游者、导游、领队人员;对属于保险责任的，在与旅行社达成赔偿保险金的协议后10日内，履行赔偿保险金义务。</p><p>　　第二十二条 因抢救受伤人员需要保险公司先行赔偿保险金用于支付抢救费用的，保险公司在接到旅行社或者受害的旅游者、导游、领队人员通知后，经核对属于保险责任的，可以在责任限额内先向医疗机构支付必要的费用。</p><p>　　第二十三条 因第三者损害而造成保险事故的，保险公司自直接赔偿保险金或者先行支付抢救费用之日起，在赔偿、支付金额范围内代位行使对第三者请求赔偿的权利。旅行社以及受害的旅游者、导游或者领队人员应当向保险公司提供必要的文件和所知道的有关情况。</p><p>　　第二十四条 旅行社与保险公司对赔偿有争议的，可以按照双方的约定申请仲裁，或者依法向人民法院提起诉讼。</p><p>　　第二十五条 保险公司的工作人员对当事人的个人隐私应当保密。</p><p>　　第四章 监督检查</p><p>　　第二十六条 县级以上旅游行政管理部门依法对旅行社投保旅行社责任保险情况实施监督检查。</p><p>　　第二十七条 中国保监会及其派出机构依法对保险公司开展旅行社责任保险业务实施监督管理。</p><p>　　第五章 罚 则</p><p>　　第二十八条 违反本办法第十二条、第十六条、第十八条的规定，旅行社解除保险合同但未同时订立新的保险合同，保险合同期满前未及时续保，或者人身伤亡责任限额低于20万元人民币的，由县级以上旅游行政管理部门依照《旅行社条例》第四十九条的规定处罚。</p><p>　　第二十九条 保险公司经营旅行社责任保险，违反有关保险条款和保险费率管理规定的，由中国保监会或者其派出机构依照《中华人民共和国保险法》和中国保监会的有关规定予以处罚。</p><p>　　第三十条 保险公司拒绝或者妨碍依法检查监督的，由中国保监会或者其派出机构依照《中华人民共和国保险法》的有关规定予以处罚。</p><p>　　第六章 附 则</p><p>　　第三十一条 本办法由国家旅游局和中国保监会负责解释。</p><p>　　第三十二条 本办法自2011年2月1日起施行。国家旅游局2001年5月15日发布的《旅行社投保旅行社责任保险规定》同时废止。</p><p><br /><a href=\"http://www.gzly.gov.cn/f/file/201505/1432697821284.doc\">旅行社责任保险管理办法.doc<br /></a></p>');
INSERT INTO DS_CMS_PAGE (ID, SITEID, CATEGORYID, STATUS, TITLE, URL, METAKEYWORDS, METADESCRIPTION, SUMMARY, RELEASETIME, RELEASESOURCE, RELEASEUSER, IMG, IMGTOP, PAGETOP, CONTENT) VALUES (1004, 5, 19, 1, '旅行社条例实施细则', '/a/lyfggz/1004.html', '', '', '旅行社条例实施细则', '2015-07-21 11:38:25', NULL, NULL, '', 0, 0, '<div align=\"center\"><strong><span style=\"font-size:18px;\">国家旅游局30号令:《旅行社条例实施细则》</span></strong></div><div align=\"center\"><strong><span style=\"font-size:18px;\">自2009年5月3日起施行</span></strong></div><div align=\"center\"><span style=\"font-size:18px;\">国家旅游局令<br />第30号</span></div><div align=\"left\"><span style=\"font-size:18px;\">　　《旅行社条例实施细则》已经2009年4月2日国家旅游局第4次局长办公会议审议通过，现予公布，自2009年5月3日起施行。</span></div><div align=\"right\"><span style=\"font-size:18px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <br />局长：邵琪伟&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></div><div align=\"right\"><span style=\"font-size:18px;\">二○○九年四月三日</span></div><div align=\"left\"><span style=\"font-size:18px;\"></span>&nbsp;</div><div align=\"center\"><span style=\"font-size:18px;\">旅行社条例实施细则</span></div><div align=\"left\"><span style=\"font-size:18px;\">第一章&nbsp; 总&nbsp; 则</span></div><div align=\"left\"><span style=\"font-size:18px;\">　　第一条　根据《旅行社条例》(以下简称《条例》)，制定本实施细则。<br />　　第二条　《条例》第二条所称招徕、组织、接待旅游者提供的相关旅游服务，主要包括：<br />　　（一）安排交通服务；<br />　　（二）安排住宿服务；<br />　　（三）安排餐饮服务；<br />　　（四）安排观光游览、休闲度假等服务；<br />　　（五）导游、领队服务；<br />　　（六）旅游咨询、旅游活动设计服务。<br />　　旅行社还可以接受委托，提供下列旅游服务：<br />　　（一）接受旅游者的委托，代订交通客票、代订住宿和代办出境、入境、签证手续等；<br />　　（二）接受机关、事业单位和社会团体的委托，为其差旅、考察、会议、展览等公务活动，代办交通、住宿、餐饮、会务等事务；<br />　　（三）接受企业委托，为其各类商务活动、奖励旅游等，代办交通、住宿、餐饮、会务、观光游览、休闲度假等事务；<br />　　（四）其他旅游服务。<br />　　前款所列出境、签证手续等服务，应当由具备出境旅游业务经营权的旅行社代办。<br />　　第三条&nbsp; 《条例》第二条所称国内旅游业务，是指旅行社招徕、组织和接待中国内地居民在境内旅游的业务。<br />　　《条例》第二条所称入境旅游业务，是指旅行社招徕、组织、接待外国旅游者来我国旅游，香港特别行政区、澳门特别行政区旅游者来内地旅游，台湾地区居民来大陆旅游，以及招徕、组织、接待在中国内地的外国人，在内地的香港特别行政区、澳门特别行政区居民和在大陆的台湾地区居民在境内旅游的业务。<br />　　《条例》第二条所称出境旅游业务，是指旅行社招徕、组织、接待中国内地居民出国旅游，赴香港特别行政区、澳门特别行政区和台湾地区旅游，以及招徕、组织、接待在中国内地的外国人、在内地的香港特别行政区、澳门特别行政区居民和在大陆的台湾地区居民出境旅游的业务。<br />　　第四条&nbsp; 对旅行社及其分支机构的监督管理，县级以上旅游行政管理部门应当按照《条例》、本细则的规定和职责，实行分级管理和属地管理。<br />　　第五条　鼓励旅行社实行服务质量等级制度；鼓励旅行社向专业化、网络化、品牌化发展。 </span></div><div align=\"left\"><br /><span style=\"font-size:18px;\">第二章&nbsp; 旅行社的设立与变更</span></div><div align=\"left\"><span style=\"font-size:18px;\">　　第六条&nbsp; 《条例》第六条第（一）项规定的经营场所应当符合下列要求：<br />　　（一）申请者拥有产权的营业用房，或者申请者租用的、租期不少于1年的营业用房；<br />　　（二）营业用房应当满足申请者业务经营的需要。<br />　　第七条《条例》第六条第（二）项规定营业设施应当至少包括下列设施、设备：<br />　　（一）2部以上的直线固定电话；&nbsp;<br />　　（二）传真机、复印机；<br />　　（三）具备与旅游行政管理部门及其他旅游经营者联网条件的计算机。<br />　　第八条　申请设立旅行社，应当向省、自治区、直辖市旅游行政管理部门（简称省级旅游行政管理部门，下同）提交下列文件： <br />　　（一）设立申请书。内容包括申请设立的旅行社的中英文名称及英文缩写，设立地址，企业形式、出资人、出资额和出资方式，申请人、受理申请部门的全称、申请书名称和申请的时间；<br />　　（二）法定代表人履历表及身份证明；<br />　　（三）企业章程；<br />　　（四）依法设立的验资机构出具的验资证明；<br />　　（五）经营场所的证明；<br />　　（六）营业设施、设备的证明或者说明；<br />　　（七）工商行政管理部门出具的《企业名称预先核准通知书》。<br />　　省级旅游行政管理部门可以委托设区的市（含州、盟，下同）级旅游行政管理部门，受理当事人的申请并作出许可或者不予许可的决定。<br />　　第九条　受理申请的旅游行政管理部门可以对申请人的经营场所、营业设施、设备进行现场检查，或者委托下级旅游行政管理部门检查。<br />　　第十条&nbsp; 旅行社申请出境旅游业务的，应当向国务院旅游行政主管部门提交原许可的旅游行政管理部门出具的，证明其经营旅行社业务满两年、且连续两年未因侵害旅游者合法权益受到行政机关罚款以上处罚的文件。<br />　　旅行社取得出境旅游经营业务许可的，由国务院旅游行政主管部门换发旅行社业务经营许可证。旅行社持旅行社业务经营许可证向工商行政管理部门办理经营范围变更登记。<br />　　国务院旅游行政主管部门可以委托省级旅游行政管理部门受理旅行社经营出境旅游业务的申请，并作出许可或者不予许可的决定。<br />　　旅行社申请经营边境旅游业务的，适用《边境旅游暂行管理办法》的规定。<br />　　旅行社申请经营赴台湾地区旅游业务的，适用《大陆居民赴台湾地区旅游管理办法》的规定。<br />　　第十一条　旅行社因业务经营需要，可以向原许可的旅游行政管理部门申请核发旅行社业务经营许可证副本。<br />　　旅行社业务经营许可证及副本，由国务院旅游行政主管部门制定统一样式，国务院旅游行政主管部门和省级旅游行政管理部门分别印制。<br />　　旅行社业务经营许可证及副本损毁或者遗失的，旅行社应当向原许可的旅游行政管理部门申请换发或者补发。<br />　　申请补发旅行社业务经营许可证及副本的，旅行社应当通过本省、自治区、直辖市范围内公开发行的报刊，或者省级以上旅游行政管理部门网站，刊登损毁或者遗失作废声明。<br />　　第十二条　旅行社名称、经营场所、出资人、法定代表人等登记事项变更的，应当在办理变更登记后，持已变更的《企业法人营业执照》向原许可的旅游行政管理部门备案。<br />　　旅行社终止经营的，应当在办理注销手续后，持工商行政管理部门出具的注销文件，向原许可的旅游行政管理部门备案。<br />　　外商投资旅行社的，适用《条例》第三章的规定。未经批准，旅行社不得引进外商投资。<br />　　第十三条　国务院旅游行政主管部门指定的作为旅行社存入质量保证金的商业银行，应当提交具有下列内容的书面承诺：<br />&nbsp;&nbsp;&nbsp; 　（一）同意与存入质量保证金的旅行社签订符合本实施细则第十五条规定的协议；<br />&nbsp;&nbsp;&nbsp; 　（二）当县级以上旅游行政管理部门或者人民法院依据《条例》规定，划拨质量保证金后3个工作日内，将划拨情况及其数额，通知旅行社所在地的省级旅游行政管理部门，并提供县级以上旅游行政管理部门出具的划拨文件或者人民法院生效法律文书的复印件；<br />&nbsp;&nbsp;&nbsp; 　（三）非因《条例》规定的情形，出现质量保证金减少时，承担补足义务。<br />&nbsp;&nbsp;&nbsp; 　旅行社应当在国务院旅游行政主管部门指定银行的范围内，选择存入质量保证金的银行。<br />&nbsp;&nbsp;&nbsp; 　第十四条　旅行社在银行存入质量保证金的，应当设立独立账户，存期由旅行社确定，但不得少于1年。账户存期届满，旅行社应当及时办理续存手续。<br />&nbsp;&nbsp;&nbsp; 　第十五条　旅行社存入、续存、增存质量保证金后7个工作日内，应当向作出许可的旅游行政管理部门提交存入、续存、增存质量保证金的证明文件，以及旅行社与银行达成的使用质量保证金的协议。<br />&nbsp;&nbsp;&nbsp; 　前款协议应当包含下列内容：<br />&nbsp;&nbsp;&nbsp; 　（一）旅行社与银行双方同意依照《条例》规定使用质量保证金；<br />&nbsp;&nbsp;&nbsp; 　（二）旅行社与银行双方承诺，除依照县级以上旅游行政管理部门出具的划拨质量保证金，或者省级以上旅游行政管理部门出具的降低、退还质量保证金的文件，以及人民法院作出的认定旅行社损害旅游者合法权益的生效法律文书外，任何单位和个人不得动用质量保证金。<br />&nbsp;&nbsp;&nbsp;　 第十六条　旅行社符合《条例》第十七条降低质量保证金数额规定条件的，原许可的旅游行政管理部门应当根据旅行社的要求，在10个工作日内向其出具降低质量保证金数额的文件。<br />&nbsp;&nbsp;&nbsp; 　第十七条　旅行社按照《条例》第十八条规定补足质量保证金后7个工作日内，应当向原许可的旅游行政管理部门提交补足的证明文件。</span></div><div align=\"left\"><br /><span style=\"font-size:18px;\">第三章&nbsp; 旅行社的分支机构</span></div><div align=\"left\"><span style=\"font-size:18px;\">&nbsp;&nbsp;&nbsp; 　第十八条　旅行社分社（简称分社，下同）及旅行社服务网点（简称服务网点，下同），不具有法人资格，以设立分社、服务网点的旅行社（简称设立社，下同）的名义从事《条例》规定的经营活动，其经营活动的责任和后果，由设立社承担。<br />&nbsp;&nbsp;&nbsp; 　第十九条　设立社向分社所在地工商行政管理部门办理分社设立登记后，应当持下列文件向分社所在地与工商登记同级的旅游行政管理部门备案：<br />&nbsp;&nbsp;&nbsp; 　（一）设立社的旅行社业务经营许可证副本和企业法人营业执照副本； <br />&nbsp;&nbsp;&nbsp; 　（二）分社的《营业执照》；<br />&nbsp;&nbsp;&nbsp; 　（三）分社经理的履历表和身份证明；<br />&nbsp;&nbsp;&nbsp; 　（四）增存质量保证金的证明文件。<br />&nbsp;&nbsp;&nbsp; 　没有同级的旅游行政管理部门的，向上一级旅游行政管理部门备案。<br />&nbsp;&nbsp;&nbsp; 　第二十条　分社的经营场所、营业设施、设备，应当符合《条例》第六条第（一）项、第（二）项及本实施细则第六条、第七条规定的要求。<br />&nbsp;&nbsp;&nbsp; 　分社的名称中应当包含设立社名称、分社所在地地名和“分社”或者“分公司”字样。<br />&nbsp;&nbsp;&nbsp; 　第二十一条　服务网点是指旅行社设立的，为旅行社招徕旅游者，并以旅行社的名义与旅游者签订旅游合同的门市部等机构。<br />&nbsp;&nbsp;&nbsp; 　设立社设立服务网点的区域范围，应当在设立社所在地的设区的市的行政区划内。<br />&nbsp;&nbsp;&nbsp; 　设立社不得在前款规定的区域范围外，设立服务网点。<br />&nbsp;&nbsp;&nbsp; 　第二十二条　服务网点应当设在方便旅游者认识和出入的公众场所。<br />&nbsp;&nbsp;&nbsp; 　服务网点的名称、标牌应当包括设立社名称、服务网点所在地地名等，不得含有使消费者误解为是旅行社或者分社的内容，也不得作易使消费者误解的简称。<br />&nbsp;&nbsp;&nbsp; 　服务网点应当在设立社的经营范围内，招徕旅游者、提供旅游咨询服务。<br />&nbsp;&nbsp;&nbsp; 　第二十三条　设立社向服务网点所在地工商行政管理部门办理服务网点设立登记后，应当在3个工作日内，持下列文件向服务网点所在地与工商登记同级的旅游行政管理部门备案：<br />&nbsp;&nbsp;&nbsp; 　（一）设立社的旅行社业务经营许可证副本和企业法人营业执照副本； <br />&nbsp;&nbsp;&nbsp; 　（二）服务网点的《营业执照》；<br />&nbsp;&nbsp;&nbsp; 　（三）服务网点经理的履历表和身份证明。<br />&nbsp;&nbsp;&nbsp; 　没有同级的旅游行政管理部门的，向上一级旅游行政管理部门备案。<br />&nbsp;&nbsp;&nbsp; 　第二十四条&nbsp; 分社、服务网点备案后，受理备案的旅游行政管理部门应当向旅行社颁发《旅行社分社备案登记证明》或者《旅行社服务网点备案登记证明》。<br />&nbsp;&nbsp;&nbsp; 　第二十五条　设立社应当与分社、服务网点的员工，订立劳动合同。<br />&nbsp;&nbsp;&nbsp; 　设立社应当加强对分社和服务网点的管理，对分社实行统一的人事、财务、招徕、接待制度规范，对服务网点实行统一管理、统一财务、统一招徕和统一咨询服务规范。</span></div><div align=\"left\"><br /><span style=\"font-size:18px;\">第四章&nbsp; 旅行社经营规范</span></div><div align=\"left\"><span style=\"font-size:18px;\">&nbsp;&nbsp;&nbsp; 　第二十六条　旅行社及其分社、服务网点，应当将《旅行社业务经营许可证》、《旅行社分社备案登记证明》或者《旅行社服务网点备案登记证明》，与营业执照一起，悬挂在经营场所的显要位置。<br />&nbsp;&nbsp;&nbsp; 　第二十七条&nbsp; 旅行社业务经营许可证不得转让、出租或者出借。<br />&nbsp;&nbsp;&nbsp; 　旅行社的下列行为属于转让、出租或者出借旅行社业务经营许可证的行为：<br />&nbsp;&nbsp;&nbsp; 　（一）除招徕旅游者和符合本实施细则第三十四条第一款规定的接待旅游者的情形外，准许或者默许其他企业、团体或者个人，以自己的名义从事旅行社业务经营活动的；<br />&nbsp;&nbsp;&nbsp; 　（二）准许其他企业、团体或者个人，以部门或者个人承包、挂靠的形式经营旅行社业务的。<br />&nbsp;&nbsp;&nbsp; 　第二十八条　旅行社设立的办事处、代表处或者联络处等办事机构，不得从事旅行社业务经营活动。<br />&nbsp;&nbsp;&nbsp; 　第二十九条　旅行社以互联网形式经营旅行社业务的，除符合法律、法规规定外，其网站首页应当载明旅行社的名称、法定代表人、许可证编号和业务经营范围，以及原许可的旅游行政管理部门的投诉电话。<br />&nbsp;&nbsp;&nbsp; 　第三十条　《条例》第二十六条规定的旅行社不得安排的活动，主要包括：<br />&nbsp;&nbsp;&nbsp; 　（一）含有损害国家利益和民族尊严内容的；<br />　　（二）含有民族、种族、宗教歧视内容的；<br />　　（三）含有淫秽、赌博、涉毒内容的；<br />　　（四）其他含有违反法律、法规规定内容的。<br />　　第三十一条&nbsp; 《条例》第三十四条所规定的旅行社不得要求导游人员和领队人员承担接待旅游团队的相关费用，主要包括：<br />&nbsp;&nbsp;&nbsp; 　（一）垫付旅游接待费用；<br />&nbsp;&nbsp;&nbsp; 　（二）为接待旅游团队向旅行社支付费用；<br />&nbsp;&nbsp;&nbsp; 　（三）其他不合理费用。<br />&nbsp;&nbsp;&nbsp; 　第三十二条　旅行社招徕、组织、接待旅游者，其选择的交通、住宿、餐饮、景区等企业，应当符合具有合法经营资格和接待服务能力的要求。<br />&nbsp;&nbsp;&nbsp; 　第三十三条　在签订旅游合同时，旅行社不得要求旅游者必须参加旅行社安排的购物活动或者需要旅游者另行付费的旅游项目。<br />&nbsp;&nbsp;&nbsp; 　同一旅游团队中，旅行社不得由于下列因素，提出与其他旅游者不同的合同事项：<br />&nbsp;&nbsp;&nbsp; 　（一）旅游者拒绝参加旅行社安排的购物活动或者需要旅游者另行付费的旅游项目的；<br />&nbsp;&nbsp;&nbsp; 　（二）旅游者存在的年龄或者职业上的差异。但旅行社提供了与其他旅游者相比更多的服务，或者旅游者主动要求的除外。<br />&nbsp;&nbsp;&nbsp; 　第三十四条&nbsp; 旅行社需要将在旅游目的地接待旅游者的业务作出委托的，应当按照《条例》第三十六条的规定，委托给旅游目的地的旅行社并签订委托接待合同。<br />&nbsp;&nbsp;&nbsp; 　旅行社对接待旅游者的业务作出委托的，应当按照《条例》第三十六条的规定，将旅游目的地接受委托的旅行社的名称、地址、联系人和联系电话，告知旅游者。<br />&nbsp;&nbsp;&nbsp;　 第三十五条　旅游行程开始前，当发生约定的解除旅游合同的情形时，经征得旅游者的同意，旅行社可以将旅游者推荐给其他旅行社组织、接待，并由旅游者与被推荐的旅行社签订旅游合同。<br />&nbsp;&nbsp;&nbsp;　 未经旅游者同意的，旅行社不得将旅游者转交给其他旅行社组织、接待。<br />&nbsp;&nbsp;&nbsp; 　第三十六条&nbsp; 旅行社及其委派的导游人员和领队人员的下列行为，属于擅自改变旅游合同安排行程：<br />&nbsp;&nbsp;&nbsp; 　（一）减少游览项目或者缩短游览时间的；<br />&nbsp;&nbsp;&nbsp; 　（二）增加或者变更旅游项目的；<br />&nbsp;&nbsp;&nbsp; 　（三）增加购物次数或者延长购物时间的；<br />&nbsp;&nbsp;&nbsp; 　（四）其他擅自改变旅游合同安排的行为。<br />&nbsp;&nbsp;&nbsp;　 第三十七条&nbsp; 在旅游行程中，当发生不可抗力、危及旅游者人身、财产安全，或者非旅行社责任造成的意外情形，旅行社不得不调整或者变更旅游合同约定的行程安排时,应当在事前向旅游者作出说明；确因客观情况无法在事前说明的，应当在事后作出说明。<br />&nbsp;&nbsp;&nbsp; 　第三十八条&nbsp; 在旅游行程中，旅游者有权拒绝参加旅行社在旅游合同之外安排的购物活动或者需要旅游者另行付费的旅游项目。<br />&nbsp;&nbsp;&nbsp; 　旅行社及其委派的导游人员和领队人员不得因旅游者拒绝参加旅行社安排的购物活动或者需要旅游者另行付费的旅游项目等情形，以任何借口、理由，拒绝继续履行合同、提供服务，或者以拒绝继续履行合同、提供服务相威胁。<br />&nbsp;&nbsp;&nbsp;　 第三十九条&nbsp; 旅行社及其委派的导游人员、领队人员，应当对其提供的服务可能危及旅游者人身、财物安全的事项，向旅游者作出真实的说明和明确的警示。<br />&nbsp;&nbsp;&nbsp;　 在旅游行程中的自由活动时间，旅游者应当选择自己能够控制风险的活动项目，并在自己能够控制风险的范围内活动。<br />&nbsp;&nbsp;&nbsp; 　第四十条&nbsp; 为减少自然灾害等意外风险给旅游者带来的损害，旅行社在招徕、接待旅游者时，可以提示旅游者购买旅游意外保险。<br />&nbsp;&nbsp;&nbsp;　 鼓励旅行社依法取得保险代理资格，并接受保险公司的委托，为旅游者提供购买人身意外伤害保险的服务。<br />&nbsp;&nbsp;&nbsp;　 第四十一条&nbsp; 发生出境旅游者非法滞留境外或者入境旅游者非法滞留境内的，旅行社应当立即向所在地县级以上旅游行政管理部门、公安机关和外事部门报告。<br />&nbsp;&nbsp;&nbsp; 　第四十二条&nbsp; 在旅游行程中，旅行社及其委派的导游人员、领队人员应当提示旅游者遵守文明旅游公约和礼仪。<br />&nbsp;&nbsp;&nbsp; 　第四十三条&nbsp; 旅行社及其委派的导游人员、领队人员在经营、服务中享有下列权利：<br />&nbsp;&nbsp;&nbsp; 　（一）要求旅游者如实提供旅游所必需的个人信息，按时提交相关证明文件；<br />&nbsp;&nbsp;&nbsp; 　（二）要求旅游者遵守旅游合同约定的旅游行程安排，妥善保管随身物品；<br />&nbsp;&nbsp;&nbsp; 　（三）出现突发公共事件或者其他危急情形，以及旅行社因违反旅游合同约定采取补救措施时，要求旅游者配合处理防止扩大损失，以将损失降低到最低程度；<br />&nbsp;&nbsp;&nbsp; 　（四）拒绝旅游者提出的超出旅游合同约定的不合理要求；<br />&nbsp;&nbsp;&nbsp; 　（五）制止旅游者违背旅游目的地的法律、风俗习惯的言行。<br />&nbsp;&nbsp;&nbsp; 　第四十四条&nbsp; 旅行社应当妥善保存《条例》规定的招徕、组织、接待旅游者的各类合同及相关文件、资料，以备县级以上旅游行政管理部门核查。 <br />&nbsp;&nbsp;&nbsp; 　前款所称的合同及文件、资料的保存期，应当不少于两年。<br />&nbsp;&nbsp;&nbsp; 　旅行社不得向其他经营者或者个人，泄露旅游者因签订旅游合同提供的个人信息；超过保存期限的旅游者个人信息资料，应当妥善销毁。</span></div><div align=\"left\"><br /><span style=\"font-size:18px;\">第五章&nbsp; 监督检查</span></div><div align=\"left\"><span style=\"font-size:18px;\">&nbsp;&nbsp;&nbsp; 　第四十五条&nbsp; 根据《条例》和本实施细则规定，受理旅行社申请或者备案的旅游行政管理部门，可以要求申请人或者旅行社，对申请设立旅行社、办理《条例》规定的备案时提交的证明文件、材料的原件，提供复印件并盖章确认，交由旅游行政管理部门留存。<br />&nbsp;&nbsp;&nbsp;　 第四十六条&nbsp; 县级以上旅游行政管理部门对旅行社及其分支机构实施监督检查时，可以进入其经营场所，查阅招徕、组织、接待旅游者的各类合同、相关文件、资料，以及财务账簿、交易记录和业务单据等材料，旅行社及其分支机构应当给予配合。<br />&nbsp;&nbsp;&nbsp; 　县级以上旅游行政管理部门对旅行社及其分支机构监督检查时，应当由两名以上持有旅游行政执法证件的执法人员进行。<br />&nbsp;&nbsp;&nbsp; 　不符合前款规定要求的，旅行社及其分支机构有权拒绝检查。<br />&nbsp;&nbsp;&nbsp; 　第四十七条&nbsp; 旅行社应当按年度将下列经营和财务信息等统计资料，在次年3月底前，报送原许可的旅游行政管理部门：<br />&nbsp;&nbsp;&nbsp; 　（一）旅行社的基本情况，包括企业形式、出资人、员工人数、部门设置、分支机构、网络体系等；<br />&nbsp;&nbsp;&nbsp; 　（二）旅行社的经营情况，包括营业收入、利税等；<br />&nbsp;&nbsp;&nbsp; 　（三）旅行社组织接待情况，包括国内旅游、入境旅游、出境旅游的组织、接待人数等；<br />&nbsp;&nbsp;&nbsp; 　（四）旅行社安全、质量、信誉情况，包括投保旅行社责任保险、认证认可和奖惩等。<br />&nbsp;&nbsp;&nbsp; 　对前款资料中涉及旅行社商业秘密的内容，旅游行政管理部门应当予以保密。<br />&nbsp;&nbsp;&nbsp; 　第四十八条&nbsp; 《条例》第十七条、第四十二条规定的各项公告，县级以上旅游行政管理部门应当通过本部门或者上级旅游行政管理部门的政府网站向社会发布。<br />&nbsp;&nbsp;&nbsp; 　质量保证金存缴数额降低、旅行社业务经营许可证的颁发、变更和注销的，国务院旅游行政主管部门或者省级旅游行政管理部门应当在作出许可决定或者备案后20个工作日内向社会公告。<br />&nbsp;&nbsp;&nbsp; 　旅行社违法经营或者被吊销旅行社业务经营许可证的，由作出行政处罚决定的旅游行政管理部门，在处罚生效后10个工作日内向社会公告。<br />&nbsp;&nbsp;&nbsp; 　旅游者对旅行社的投诉信息，由处理投诉的旅游行政管理部门每季度向社会公告。<br />&nbsp;&nbsp;&nbsp; 　第四十九条&nbsp; 因下列情形之一，给旅游者的合法权益造成损害的，旅游者有权向县级以上旅游行政管理部门投诉： <br />　　（一）旅行社违反《条例》和本实施细则规定的；<br />　　（二）旅行社提供的服务，未达到旅游合同约定的服务标准或者档次的；<br />&nbsp;&nbsp;&nbsp; 　（三）旅行社破产或者其他原因造成旅游者预交旅游费用损失的。<br />&nbsp;&nbsp;&nbsp; 　划拨旅行社质量保证金的决定，应当由旅行社或者其分社所在地处理旅游者投诉的县级以上旅游行政管理部门作出。<br />　　第五十条&nbsp; 县级以上旅游行政管理部门，可以在其法定权限内，委托符合法定条件的同级旅游质监执法机构实施监督检查。</span></div><div align=\"left\"><br /><span style=\"font-size:18px;\">第六章&nbsp; 法律责任</span></div><div align=\"left\"><span style=\"font-size:18px;\">&nbsp;&nbsp;&nbsp; 　第五十一条&nbsp; 违反本实施细则第十二条第三款、第二十三条、第二十六条的规定，擅自引进外商投资、设立服务网点未在规定期限内备案，或者旅行社及其分社、服务网点未悬挂旅行社业务经营许可证、备案登记证明的，由县级以上旅游行政管理部门责令改正，可以处1万元以下的罚款。<br />&nbsp;&nbsp;&nbsp;　 第五十二条&nbsp; 违反本实施细则第二十二条第三款、第二十八条的规定，服务网点超出设立社经营范围招徕旅游者、提供旅游咨询服务，或者旅行社的办事处、联络处、代表处等从事旅行社业务经营活动的，由县级以上旅游行政管理部门依照《条例》第四十六条的规定处罚。<br />&nbsp;&nbsp;&nbsp; 　第五十三条&nbsp; 违反本实施细则第三十二条的规定，旅行社为接待旅游者选择的交通、住宿、餐饮、景区等企业，不具有合法经营资格或者接待服务能力的，由县级以上旅游行政管理部门责令改正，没收违法所得，处违法所得3倍以下但最高不超过3万元的罚款，没有违法所得的，处1万元以下的罚款。<br />&nbsp;&nbsp;&nbsp; 　第五十四条&nbsp; 违反本实施细则第三十三条的规定，要求旅游者必须参加旅行社安排的购物活动、需要旅游者另行付费的旅游项目，或者对同一旅游团队的旅游者提出与其他旅游者不同合同事项的，由县级以上旅游行政管理部门责令改正，处1万元以下的罚款。<br />&nbsp;&nbsp;&nbsp; 　第五十五条&nbsp; 违反本实施细则第三十四条第二款的规定，旅行社未将旅游目的地接待旅行社的情况告知旅游者的，由县级以上旅游行政管理部门依照《条例》第五十五条的规定处罚。<br />&nbsp;&nbsp;&nbsp; 　第五十六条&nbsp; 违反本实施细则第三十五条第二款的规定，旅行社未经旅游者的同意，将旅游者转交给其他旅行社组织、接待的，由县级以上旅游行政管理部门依照《条例》第五十五条的规定处罚。<br />&nbsp;&nbsp;&nbsp; 　第五十七条&nbsp; 违反本实施细则第三十八条第二款的规定，旅行社及其导游人员和领队人员拒绝继续履行合同、提供服务，或者以拒绝继续履行合同、提供服务相威胁的，由县级以上旅游行政管理部门依照《条例》第五十九条的规定处罚。<br />&nbsp;&nbsp;&nbsp;　 第五十八条&nbsp; 违反本实施细则第四十四条的规定，未妥善保存各类旅游合同及相关文件、资料，保存期不够两年，或者泄露旅游者个人信息的，由县级以上旅游行政管理部门责令改正，没收违法所得，处违法所得3倍以下但最高不超过3万元的罚款；没有违法所得的，处1万元以下的罚款。<br />&nbsp;&nbsp;&nbsp;　 第五十九条&nbsp; 吊销旅行社业务经营许可证的行政处罚，由原许可的省级以上旅游行政管理部门作出。<br />&nbsp;&nbsp;　&nbsp; 对旅行社作出停业整顿行政处罚的，旅行社在停业整顿期间，不得招徕旅游者、签订旅游合同；停业整顿期间，不影响已签订的旅游合同的履行。</span></div><div align=\"left\"><br /><span style=\"font-size:18px;\">第七章&nbsp; 附&nbsp; 则</span></div><div align=\"left\"><span style=\"font-size:18px;\">&nbsp;&nbsp;&nbsp;　 第六十条&nbsp; 本实施细则由国务院旅游行政主管部门负责解释。<br />&nbsp;&nbsp;&nbsp; 　第六十一条&nbsp; 本实施细则自2009年5月3日起施行。2001年12月27日国家旅游局公布的《旅行社管理条例实施细则》同时废止。</span></div>');
INSERT INTO DS_CMS_PAGE (ID, SITEID, CATEGORYID, STATUS, TITLE, URL, METAKEYWORDS, METADESCRIPTION, SUMMARY, RELEASETIME, RELEASESOURCE, RELEASEUSER, IMG, IMGTOP, PAGETOP, CONTENT) VALUES (1016, 5, 25, 1, '关于批准设立广州汇昊龙旅行社有限公司的批复', '/a/lxsgl/1016.html', '', '', '关于批准设立广州汇昊龙旅行社有限公司的批复', '2015-01-09 14:19:26', NULL, NULL, '', 0, 0, '<div style=\"top: 0px;\">??</div><span style=\"font-family:宋体;font-size:16px;\"></span><p align=\"center\" style=\"margin: 0cm 0cm 0pt; text-align: center;\"><span lang=\"EN-US\" style=\"color: black; mso-bidi-font-size: 16.0pt;\"><span style=\"font-family:Times New Roman;font-size:24px;\">&nbsp;</span></span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p align=\"right\" style=\"margin: 0cm 0cm 0pt; text-align: right; line-height: 26.5pt; -ms-word-break: break-all; mso-line-height-rule: exactly;\"><span style=\"font-family:宋体;\"><span lang=\"EN-US\" style=\"color: black; mso-bidi-font-size: 16.0pt;\"><span style=\"mso-spacerun: yes;\"><span style=\"font-size:24px;\">&nbsp; </span></span><span style=\"mso-spacerun: yes;\"><span style=\"font-size:24px;\">&nbsp;</span></span></span><span style=\"color: black; mso-bidi-font-size: 16.0pt;\"><span style=\"font-size:24px;\">穗旅发〔</span><span lang=\"EN-US\"><span style=\"font-size:24px;\">2014</span></span><span style=\"font-size:24px;\">〕</span><span lang=\"EN-US\"><span style=\"mso-spacerun: yes;\"><span style=\"font-size:24px;\">&nbsp;</span></span></span><span style=\"font-size:24px;\">号</span></span></span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p align=\"center\" style=\"margin: 0cm 0cm 0pt; text-align: center; line-height: 28.5pt; vertical-align: baseline; mso-line-height-rule: exactly;\"><span lang=\"EN-US\" style=\"font-size: 22pt; mso-hansi-font-family: 宋体; mso-bidi-font-size: 18.0pt;\"><span style=\"font-family:宋体;\">&nbsp;</span></span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p align=\"center\" style=\"margin: 0cm 0cm 0pt; text-align: center; line-height: 28.5pt; vertical-align: baseline; mso-line-height-rule: exactly;\"><span style=\"font-size: 22pt; mso-hansi-font-family: 宋体; mso-bidi-font-size: 18.0pt;\"><span style=\"font-family:宋体;\">广州市旅游局关于批准设立</span></span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p align=\"center\" style=\"margin: 0cm 0cm 0pt; text-align: center; line-height: 28.5pt; vertical-align: baseline; mso-line-height-rule: exactly;\"><span style=\"font-size: 22pt; mso-hansi-font-family: 宋体; mso-bidi-font-size: 18.0pt;\"><span style=\"font-family:宋体;\">广州汇昊龙旅行社有限公司的批<span style=\"mso-font-width: 85%;\">复</span></span></span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p style=\"margin: 0cm 0cm 0pt; line-height: 22pt; vertical-align: baseline; mso-line-height-rule: exactly;\"><span lang=\"EN-US\" style=\"mso-hansi-font-family: 仿宋_GB2312;\"><span style=\"font-family:宋体;font-size:24px;\">&nbsp;</span></span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p style=\"margin: 0cm 0cm 0pt; line-height: 28.5pt; vertical-align: baseline; mso-line-height-rule: exactly;\"><span style=\"font-family:宋体;\"><span style=\"font-size:24px;\">何雪琼：</span><span lang=\"EN-US\"><span style=\"font-size:24px;\"> </span></span></span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p style=\"margin: 0cm 0cm 0pt; line-height: 28.5pt; text-indent: 31.7pt; vertical-align: baseline; mso-line-height-rule: exactly; mso-char-indent-count: 1.98;\"><span style=\"font-family:宋体;\"><span style=\"font-size:24px;\">您提交的设立旅行社申请材料收悉。</span><span style=\"font-size:24px;\">经审核，您投资的</span><span style=\"font-size:24px;\">广州汇昊龙旅行社有限公司</span><span style=\"font-size:24px;\">符合</span><span style=\"font-size:24px;\">《中华人民共和国旅游法》、《旅行社条例》及《旅行社条例实施细则》规定的条件，我局批准设立。现将有关事项核定如下：</span></span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p style=\"margin: 0cm 0cm 0pt; line-height: 28.5pt; text-indent: 31.35pt; vertical-align: baseline; mso-line-height-rule: exactly; mso-char-indent-count: 1.96;\"><span style=\"font-family: 黑体; mso-hansi-font-family: 仿宋_GB2312;\"><span style=\"font-size:24px;\">一、旅行社名称：</span></span><span style=\"font-family:宋体;font-size:24px;\">广州汇昊龙旅行社有限公司</span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p style=\"margin: 0cm 0cm 0pt; line-height: 28.5pt; text-indent: 31.35pt; vertical-align: baseline; mso-line-height-rule: exactly; mso-char-indent-count: 1.96;\"><span style=\"font-family: 黑体; mso-hansi-font-family: 仿宋_GB2312;\"><span style=\"font-size:24px;\">二、英文名称：</span></span><span style=\"font-family:宋体;\"><span lang=\"EN-US\" style=\"font-family: \'仿宋_GB2312\',\'serif\';\"><span style=\"font-size:24px;\">GUANGZHOU HUIHAOLONG TRAVEL SERVICE</span></span><span lang=\"EN-US\" style=\"mso-hansi-font-family: 仿宋_GB2312;\"><span style=\"font-size:24px;\">CO.,LTD</span></span></span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p style=\"margin: 0cm 0cm 0pt; line-height: 28.5pt; text-indent: 31.35pt; vertical-align: baseline; mso-line-height-rule: exactly; mso-char-indent-count: 1.96;\"><span style=\"font-family: 黑体; mso-hansi-font-family: 仿宋_GB2312;\"><span style=\"font-size:24px;\">三、许可经营业务：</span></span><span style=\"font-family:宋体;font-size:24px;\">境内旅游与入境旅游</span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p style=\"margin: 0cm 0cm 0pt; line-height: 28.5pt; text-indent: 31.35pt; vertical-align: baseline; mso-line-height-rule: exactly; mso-char-indent-count: 1.96;\"><span style=\"font-family: 黑体; mso-hansi-font-family: 仿宋_GB2312;\"><span style=\"font-size:24px;\">四、出资人、出资额及其出资比例：</span></span><span style=\"font-family:宋体;\"><span style=\"font-size:24px;\">何雪琼独资</span><span lang=\"EN-US\"><span style=\"font-size:24px;\">30</span></span><span style=\"font-size:24px;\">万元人民币，占</span><span lang=\"EN-US\"><span style=\"font-size:24px;\">100%</span></span><span style=\"font-size:24px;\">。</span></span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p style=\"margin: 0cm 0cm 0pt; line-height: 28.5pt; text-indent: 31.35pt; vertical-align: baseline; mso-line-height-rule: exactly; mso-char-indent-count: 1.96;\"><span style=\"font-family: 黑体; mso-hansi-font-family: 仿宋_GB2312;\"><span style=\"font-size:24px;\">五、法定代表人</span></span><span style=\"font-family:宋体;font-size:24px;\">：何雪琼</span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p style=\"margin: 0cm 0cm 0pt; line-height: 28.5pt; text-indent: 31.35pt; vertical-align: baseline; mso-line-height-rule: exactly; mso-char-indent-count: 1.96;\"><span style=\"font-family: 黑体; mso-hansi-font-family: 仿宋_GB2312;\"><span style=\"font-size:24px;\">六、企业形式：</span></span><span style=\"font-family:宋体;font-size:24px;\">有限责任公司</span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p style=\"margin: 0cm 0cm 0pt; line-height: 28.5pt; text-indent: 31.35pt; vertical-align: baseline; mso-line-height-rule: exactly; mso-char-indent-count: 1.96;\"><span style=\"font-family: 黑体; mso-hansi-font-family: 仿宋_GB2312;\"><span style=\"font-size:24px;\">七、经营场所：</span></span><span style=\"font-family:宋体;\"><span style=\"font-size:24px;\">广州市海珠区江南大道中宝和巷</span><span lang=\"EN-US\"><span style=\"font-size:24px;\">19</span></span><span style=\"font-size:24px;\">号</span><span lang=\"EN-US\"><span style=\"font-size:24px;\">101</span></span><span style=\"font-size:24px;\">房</span></span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p style=\"margin: 0cm 0cm 0pt; line-height: 28.5pt; text-indent: 31.35pt; vertical-align: baseline; mso-line-height-rule: exactly; mso-char-indent-count: 1.96;\"><span style=\"font-family: 黑体; mso-hansi-font-family: 仿宋_GB2312;\"><span style=\"font-size:24px;\">八、许可证编号：</span></span><span lang=\"EN-US\" style=\"font-family: \'仿宋_GB2312\',\'serif\'; mso-hansi-font-family: 宋体; mso-font-kerning: 0pt; mso-bidi-font-family: 宋体;\"><span style=\"font-family:宋体;font-size:24px;\">L-GD0</span></span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p style=\"margin: 0cm 0cm 0pt; line-height: 28.5pt; text-indent: 31.35pt; vertical-align: baseline; mso-line-height-rule: exactly; mso-char-indent-count: 1.96;\"><span style=\"font-family:宋体;\"><span style=\"font-size:24px;\">请持该批准文件及旅行社业务经营许可证（副本）在</span><span lang=\"EN-US\"><span style=\"font-size:24px;\">1</span></span><span style=\"font-size:24px;\">个月内办妥税务登记、组织机构代码证及公章备案。</span><span style=\"font-size:24px;\">根据《中华人民共和国旅游法》第三十一条、《旅行社条例》第十三条，</span><span style=\"font-size:24px;\">广州汇昊龙旅行社有限公司</span><span style=\"font-size:24px;\">应当在办妥上述事项后</span><span lang=\"EN-US\"><span style=\"font-size:24px;\">3</span></span><span style=\"font-size:24px;\">个工作日内，</span><span style=\"color: black; mso-ascii-font-family: \'Times New Roman\';\"><span style=\"font-size:24px;\">在</span></span><span style=\"color: black;\"><span style=\"font-size:24px;\">国家旅游局指定的银行</span></span><span style=\"color: black; mso-ascii-font-family: \'Times New Roman\';\"><span style=\"font-size:24px;\">开设专门的旅游服务质量保证金账户，</span></span><span style=\"color: black;\"><span style=\"font-size:24px;\">存入</span><span lang=\"EN-US\"><span style=\"font-size:24px;\">20</span></span><span style=\"font-size:24px;\">万元旅游服务质量保证金或者办理同等额度的银行担保，并在此后</span></span><span lang=\"EN-US\" style=\"mso-hansi-font-family: 仿宋_GB2312;\"><span style=\"font-size:24px;\">7</span></span><span style=\"font-size:24px;\">个工作日内将相关的证明文件交回我局备案。您可以</span><span style=\"font-size:24px;\">通过广州市商事主体信息公示平台（</span><span lang=\"EN-US\"><span style=\"font-size:24px;\">http://cri.gz.gov.cn</span></span><span style=\"font-size:24px;\">）查询许可审批的内容。</span></span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p style=\"margin: 0cm 0cm 0pt; line-height: 28.5pt; text-indent: 31.35pt; vertical-align: baseline; mso-line-height-rule: exactly; mso-char-indent-count: 1.96; tab-stops: 376.25pt;\"><span style=\"font-family:宋体;font-size:24px;\">旅行社开业后，应当遵守《中华人民共和国旅游法》、《旅行社条例》和《旅行社条例实施细则》及其它有关法律、法规和规章，确保守法经营、优质服务。</span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p style=\"margin: 0cm 0cm 0pt; line-height: 28.5pt; text-indent: 31.35pt; vertical-align: baseline; mso-line-height-rule: exactly; mso-char-indent-count: 1.96;\"><span style=\"font-family:宋体;\"><span style=\"font-size:24px;\">专此批复</span><span lang=\"EN-US\"><span style=\"mso-spacerun: yes;\"><span style=\"font-size:24px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span></span></span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p style=\"margin: 0cm 0cm 0pt; line-height: 28.5pt; text-indent: 31.35pt; vertical-align: baseline; mso-line-height-rule: exactly; mso-char-indent-count: 1.96;\"><span lang=\"EN-US\" style=\"mso-hansi-font-family: 仿宋_GB2312;\"><span style=\"font-family:宋体;font-size:24px;\">&nbsp;</span></span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p style=\"margin: 0cm 0cm 0pt; line-height: 28.5pt; text-indent: 31.35pt; vertical-align: baseline; mso-line-height-rule: exactly; mso-char-indent-count: 1.96;\"><span lang=\"EN-US\" style=\"mso-hansi-font-family: 仿宋_GB2312;\"><span style=\"font-family:宋体;font-size:24px;\">&nbsp;</span></span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p style=\"margin: 0cm 0cm 0pt; line-height: 28.5pt; text-indent: 31.35pt; vertical-align: baseline; mso-line-height-rule: exactly; mso-char-indent-count: 1.96;\"><span lang=\"EN-US\" style=\"mso-hansi-font-family: 仿宋_GB2312;\"><span style=\"font-family:宋体;font-size:24px;\">&nbsp;</span></span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p style=\"margin: 0cm 0cm 0pt; line-height: 28.5pt; text-indent: 31.35pt; vertical-align: baseline; mso-line-height-rule: exactly; mso-char-indent-count: 1.96;\"><span lang=\"EN-US\" style=\"mso-hansi-font-family: 仿宋_GB2312;\"><span style=\"font-family:宋体;font-size:24px;\">&nbsp;</span></span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p style=\"margin: 0cm 0cm 0pt; line-height: 28.5pt; text-indent: 31.35pt; vertical-align: baseline; mso-line-height-rule: exactly; mso-char-indent-count: 1.96;\"><span lang=\"EN-US\" style=\"mso-hansi-font-family: 仿宋_GB2312;\"><span style=\"font-family:宋体;font-size:24px;\">&nbsp;</span></span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p style=\"margin: 0cm 0cm 0pt; line-height: 28.5pt; text-indent: 294.25pt; vertical-align: baseline; mso-line-height-rule: exactly; mso-char-indent-count: 18.39; tab-stops: 376.25pt;\"><span style=\"font-family:宋体;font-size:24px;\">广州市旅游局</span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p style=\"margin: 0cm 0cm 0pt; line-height: 28.5pt; mso-line-height-rule: exactly; tab-stops: 375.65pt 391.3pt;\"><span lang=\"EN-US\"><span style=\"font-family:宋体;\"><span style=\"mso-spacerun: yes;\"><span style=\"font-size:24px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span><st1:chsdate w:st=\"on\" year=\"2014\" month=\"12\" day=\"23\" islunardate=\"False\" isrocdate=\"False\"><span style=\"font-size:24px;\">2014</span><span lang=\"EN-US\"><span lang=\"EN-US\"><span style=\"font-size:24px;\">年12</span></span></span><span lang=\"EN-US\"><span lang=\"EN-US\"><span style=\"font-size:24px;\">月23</span></span></span><span lang=\"EN-US\"><span lang=\"EN-US\"><span style=\"font-size:24px;\">日</span></span></span></st1:chsdate></span></span></p><span style=\"font-family:宋体;font-size:16px;\"></span>');
INSERT INTO DS_CMS_PAGE (ID, SITEID, CATEGORYID, STATUS, TITLE, URL, METAKEYWORDS, METADESCRIPTION, SUMMARY, RELEASETIME, RELEASESOURCE, RELEASEUSER, IMG, IMGTOP, PAGETOP, CONTENT) VALUES (1017, 5, 25, 1, '关于批准设立广州候季鸟国际旅行社有限公司的批复', '/a/lxsgl/1017.html', '', '', '关于批准设立广州候季鸟国际旅行社有限公司的批复', '2015-01-09 14:20:18', NULL, NULL, '', 0, 0, '<div style=\"top: 0px;\">??</div><span style=\"font-family:宋体;font-size:16px;\"></span><p align=\"center\" style=\"margin: 0cm 0cm 0pt; text-align: center;\"><span lang=\"EN-US\" style=\"color:black;mso-bidi-font-size: 16.0pt;\"><span style=\"font-family:Times New Roman;font-size:24px;\">&nbsp;</span></span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p align=\"right\" style=\"margin: 0cm 0cm 0pt; text-align: right; line-height: 26.5pt; -ms-word-break: break-all; mso-line-height-rule: exactly;\"><span style=\"font-family: 宋体;\"><span lang=\"EN-US\" style=\"color:black;mso-bidi-font-size: 16.0pt;\"><span style=\"mso-spacerun: yes;\"><span style=\"font-size:24px;\">&nbsp; </span></span><span style=\"mso-spacerun: yes;\"><span style=\"font-size:24px;\">&nbsp;</span></span></span><span style=\"color: black; mso-bidi-font-size: 16.0pt;\"><span style=\"font-size:24px;\">穗旅发〔</span><span lang=\"EN-US\"><span style=\"font-size:24px;\">2014</span></span><span style=\"font-size:24px;\">〕</span><span lang=\"EN-US\"><span style=\"mso-spacerun: yes;\"><span style=\"font-size:24px;\">&nbsp;</span></span></span><span style=\"font-size:24px;\">号</span></span></span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p align=\"center\" style=\"margin: 0cm 0cm 0pt; text-align: center; line-height: 28.5pt; vertical-align: baseline; mso-line-height-rule: exactly;\"><span lang=\"EN-US\" style=\"font-family:宋体;font-size: 22pt; mso-hansi-font-family: 宋体; mso-bidi-font-size: 18.0pt;\"><span style=\"font-family:宋体;\">&nbsp;</span></span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p align=\"center\" style=\"margin: 0cm 0cm 0pt; text-align: center; line-height: 28.5pt; vertical-align: baseline; mso-line-height-rule: exactly;\"><span style=\"font-family:宋体;font-size: 22pt; mso-hansi-font-family: 宋体; mso-bidi-font-size: 18.0pt;\"><span style=\"font-family:宋体;\">广州市旅游局关于批准设立</span></span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p align=\"center\" style=\"margin: 0cm 0cm 0pt; text-align: center; line-height: 28.5pt; vertical-align: baseline; mso-line-height-rule: exactly;\"><span style=\"font-family:宋体;font-size: 22pt; mso-hansi-font-family: 宋体; mso-bidi-font-size: 18.0pt;\"><span style=\"font-family:宋体;\">广州候季鸟国际旅行社有限公司的批<span style=\"mso-font-width: 85%;\">复</span></span></span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p style=\"margin: 0cm 0cm 0pt; line-height: 22pt; vertical-align: baseline; mso-line-height-rule: exactly;\"><span lang=\"EN-US\" style=\"mso-hansi-font-family: 仿宋_GB2312;\"><span style=\"font-family:宋体;font-size:24px;\">&nbsp;</span></span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p style=\"margin: 0cm 0cm 0pt; line-height: 28.5pt; vertical-align: baseline; mso-line-height-rule: exactly;\"><span style=\"font-family:宋体;\"><span style=\"font-size:24px;\">刘新丰：</span><span lang=\"EN-US\"><span style=\"font-size:24px;\"> </span></span></span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p style=\"margin: 0cm 0cm 0pt; line-height: 28.5pt; text-indent: 31.7pt; vertical-align: baseline; mso-line-height-rule: exactly; mso-char-indent-count: 1.98;\"><span style=\"font-family:宋体;\"><span style=\"font-size:24px;\">您提交的设立旅行社申请材料收悉。</span><span style=\"font-size:24px;\">经审核，您和衡洁茹、封洁梅投资的</span><span style=\"font-size:24px;\">广州候季鸟国际旅行社有限公司</span><span style=\"font-size:24px;\">符合</span><span style=\"font-size:24px;\">《中华人民共和国旅游法》、《旅行社条例》及《旅行社条例实施细则》规定的条件，我局批准设立。现将有关事项核定如下：</span></span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p style=\"margin: 0cm 0cm 0pt; line-height: 28.5pt; text-indent: 31.35pt; vertical-align: baseline; mso-line-height-rule: exactly; mso-char-indent-count: 1.96;\"><span style=\"font-family:黑体;mso-hansi-font-family: 仿宋_GB2312;\"><span style=\"font-size:24px;\">一、旅行社名称：</span></span><span style=\"font-family:宋体;font-size:24px;\">广州候季鸟国际风旅行社有限公司</span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p style=\"margin: 0cm 0cm 0pt; line-height: 28.5pt; text-indent: 31.35pt; vertical-align: baseline; mso-line-height-rule: exactly; mso-char-indent-count: 1.96;\"><span style=\"font-family:黑体;mso-hansi-font-family: 仿宋_GB2312;\"><span style=\"font-size:24px;\">二、英文名称：</span></span><span style=\"font-family:宋体;\"><span lang=\"EN-US\" style=\"font-family:\'仿宋_GB2312\',\'serif\';\"><span style=\"font-size:24px;\">GUANGZHOU VISITOR INTERNATIONALTRAVEL SERVICE</span></span><span lang=\"EN-US\" style=\"mso-hansi-font-family: 仿宋_GB2312;\"><span style=\"font-size:24px;\"> CO.,LTD</span></span></span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p style=\"margin: 0cm 0cm 0pt; line-height: 28.5pt; text-indent: 31.35pt; vertical-align: baseline; mso-line-height-rule: exactly; mso-char-indent-count: 1.96;\"><span style=\"font-family:黑体;mso-hansi-font-family: 仿宋_GB2312;\"><span style=\"font-size:24px;\">三、许可经营业务：</span></span><span style=\"font-family:宋体;font-size:24px;\">境内旅游与入境旅游</span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p style=\"margin: 0cm 0cm 0pt; line-height: 28.5pt; text-indent: 31.35pt; vertical-align: baseline; mso-line-height-rule: exactly; mso-char-indent-count: 1.96;\"><span style=\"font-family:黑体;mso-hansi-font-family: 仿宋_GB2312;\"><span style=\"font-size:24px;\">四、出资人、出资额及其出资比例：</span></span><span style=\"font-family:宋体;\"><span style=\"font-size:24px;\">出资总额</span><span lang=\"EN-US\"><span style=\"font-size:24px;\">50</span></span><span style=\"font-size:24px;\">万元人民币。其中，刘新丰出资</span><span lang=\"EN-US\"><span style=\"font-size:24px;\">25</span></span><span style=\"font-size:24px;\">万元，占</span><span lang=\"EN-US\"><span style=\"font-size:24px;\">50%</span></span><span style=\"font-size:24px;\">；衡洁茹出资</span><span lang=\"EN-US\"><span style=\"font-size:24px;\">15</span></span><span style=\"font-size:24px;\">万元，占</span><span lang=\"EN-US\"><span style=\"font-size:24px;\">30%</span></span><span style=\"font-size:24px;\">；封洁梅出资</span><span lang=\"EN-US\"><span style=\"font-size:24px;\">10</span></span><span style=\"font-size:24px;\">万元，占</span><span lang=\"EN-US\"><span style=\"font-size:24px;\">20%</span></span><span style=\"font-size:24px;\">。</span></span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p style=\"margin: 0cm 0cm 0pt; line-height: 28.5pt; text-indent: 31.35pt; vertical-align: baseline; mso-line-height-rule: exactly; mso-char-indent-count: 1.96;\"><span style=\"font-family:黑体;mso-hansi-font-family: 仿宋_GB2312;\"><span style=\"font-size:24px;\">五、法定代表人</span></span><span style=\"font-family:宋体;font-size:24px;\">：刘新丰</span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p style=\"margin: 0cm 0cm 0pt; line-height: 28.5pt; text-indent: 31.35pt; vertical-align: baseline; mso-line-height-rule: exactly; mso-char-indent-count: 1.96;\"><span style=\"font-family:黑体;mso-hansi-font-family: 仿宋_GB2312;\"><span style=\"font-size:24px;\">六、企业形式：</span></span><span style=\"font-family:宋体;font-size:24px;\">有限责任公司</span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p style=\"margin: 0cm 0cm 0pt; line-height: 28.5pt; text-indent: 31.35pt; vertical-align: baseline; mso-line-height-rule: exactly; mso-char-indent-count: 1.96;\"><span style=\"font-family:黑体;mso-hansi-font-family: 仿宋_GB2312;\"><span style=\"font-size:24px;\">七、经营场所：</span></span><span style=\"font-family:宋体;\"><span style=\"font-size:24px;\">广州市越秀区东风中路</span><span lang=\"EN-US\"><span style=\"font-size:24px;\">268</span></span><span style=\"font-size:24px;\">号广州交易广场</span><span lang=\"EN-US\"><span style=\"font-size:24px;\">22</span></span><span style=\"font-size:24px;\">层</span><span lang=\"EN-US\"><span style=\"font-size:24px;\">09</span></span><span style=\"font-size:24px;\">自编</span><span lang=\"EN-US\"><span style=\"font-size:24px;\">B</span></span><span style=\"font-size:24px;\">单元（仅限办公用途）</span></span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p style=\"margin: 0cm 0cm 0pt; line-height: 28.5pt; text-indent: 31.35pt; vertical-align: baseline; mso-line-height-rule: exactly; mso-char-indent-count: 1.96;\"><span style=\"font-family:黑体;mso-hansi-font-family: 仿宋_GB2312;\"><span style=\"font-size:24px;\">八、许可证编号：</span></span><span lang=\"EN-US\" style=\"font-family:\'仿宋_GB2312\',\'serif\';mso-hansi-font-family: 宋体; mso-font-kerning: 0pt; mso-bidi-font-family: 宋体;\"><span style=\"font-family:宋体;font-size:24px;\">L-GD0</span></span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p style=\"margin: 0cm 0cm 0pt; line-height: 28.5pt; text-indent: 31.35pt; vertical-align: baseline; mso-line-height-rule: exactly; mso-char-indent-count: 1.96;\"><span style=\"font-family: 宋体;\"><span style=\"font-size:24px;\">请持该批准文件及旅行社业务经营许可证（副本）在</span><span lang=\"EN-US\"><span style=\"font-size:24px;\">1</span></span><span style=\"font-size:24px;\">个月内办妥税务登记、组织机构代码证及公章备案。</span><span style=\"font-size:24px;\">根据《中华人民共和国旅游法》第三十一条、《旅行社条例》第十三条，</span><span style=\"font-size:24px;\">广州候季鸟国际旅行社有限公司</span><span style=\"font-size:24px;\">应当在办妥上述事项后</span><span lang=\"EN-US\"><span style=\"font-size:24px;\">3</span></span><span style=\"font-size:24px;\">个工作日内，</span><span style=\"font-family:\'Times New Roman\';color:black;mso-ascii-font-family: \'Times New Roman\';\"><span style=\"font-size:24px;\">在</span></span><span style=\"color:black;\"><span style=\"font-size:24px;\">国家旅游局指定的银行</span></span><span style=\"font-family:\'Times New Roman\';color:black;mso-ascii-font-family: \'Times New Roman\';\"><span style=\"font-size:24px;\">开设专门的旅游服务质量保证金账户，</span></span><span style=\"color:black;\"><span style=\"font-size:24px;\">存入</span><span lang=\"EN-US\"><span style=\"font-size:24px;\">20</span></span><span style=\"font-size:24px;\">万元旅游服务质量保证金或者办理同等额度的银行担保，并在此后</span></span><span lang=\"EN-US\" style=\"mso-hansi-font-family: 仿宋_GB2312;\"><span style=\"font-size:24px;\">7</span></span><span style=\"font-size:24px;\">个工作日内将相关的证明文件交回我局备案。您可以</span><span style=\"font-size:24px;\">通过广州市商事主体信息公示平台（</span><span lang=\"EN-US\"><span style=\"font-size:24px;\">http://cri.gz.gov.cn</span></span><span style=\"font-size:24px;\">）查询许可审批的内容。</span></span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p style=\"margin: 0cm 0cm 0pt; line-height: 28.5pt; text-indent: 31.35pt; vertical-align: baseline; mso-line-height-rule: exactly; mso-char-indent-count: 1.96; tab-stops: 376.25pt;\"><span style=\"font-family:宋体;font-size:24px;\">旅行社开业后，应当遵守《中华人民共和国旅游法》、《旅行社条例》和《旅行社条例实施细则》及其它有关法律、法规和规章，确保守法经营、优质服务。</span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p style=\"margin: 0cm 0cm 0pt; line-height: 28.5pt; text-indent: 31.35pt; vertical-align: baseline; mso-line-height-rule: exactly; mso-char-indent-count: 1.96;\"><span style=\"font-family: 宋体;\"><span style=\"font-size:24px;\">专此批复</span><span lang=\"EN-US\"><span style=\"mso-spacerun: yes;\"><span style=\"font-size:24px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span></span></span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p style=\"margin: 0cm 0cm 0pt; line-height: 28.5pt; text-indent: 31.35pt; vertical-align: baseline; mso-line-height-rule: exactly; mso-char-indent-count: 1.96;\"><span lang=\"EN-US\" style=\"mso-hansi-font-family: 仿宋_GB2312;\"><span style=\"font-family:宋体;font-size:24px;\">&nbsp;</span></span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p style=\"margin: 0cm 0cm 0pt; line-height: 28.5pt; text-indent: 31.35pt; vertical-align: baseline; mso-line-height-rule: exactly; mso-char-indent-count: 1.96;\"><span lang=\"EN-US\" style=\"mso-hansi-font-family: 仿宋_GB2312;\"><span style=\"font-family:宋体;font-size:24px;\">&nbsp;</span></span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p style=\"margin: 0cm 0cm 0pt; line-height: 28.5pt; text-indent: 294.25pt; vertical-align: baseline; mso-line-height-rule: exactly; mso-char-indent-count: 18.39; tab-stops: 376.25pt;\"><span style=\"font-family:宋体;font-size:24px;\">广州市旅游局</span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p style=\"margin: 0cm 0cm 0pt; line-height: 28.5pt; mso-line-height-rule: exactly; tab-stops: 375.65pt 391.3pt;\"><span lang=\"EN-US\"><span style=\"font-family: 宋体;\"><span style=\"mso-spacerun: yes;\"><span style=\"font-size:24px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span><st1:chsdate w:st=\"on\" year=\"2014\" month=\"12\" day=\"26\" islunardate=\"False\" isrocdate=\"False\"><span style=\"font-size:24px;\">2014</span><span lang=\"EN-US\"><span lang=\"EN-US\"><span style=\"font-size:24px;\">年12</span></span></span><span lang=\"EN-US\"><span lang=\"EN-US\"><span style=\"font-size:24px;\">月26</span></span></span><span lang=\"EN-US\"><span lang=\"EN-US\"><span style=\"font-size:24px;\">日</span></span></span></st1:chsdate></span></span></p><span style=\"font-family:宋体;font-size:16px;\"></span>');
INSERT INTO DS_CMS_PAGE (ID, SITEID, CATEGORYID, STATUS, TITLE, URL, METAKEYWORDS, METADESCRIPTION, SUMMARY, RELEASETIME, RELEASESOURCE, RELEASEUSER, IMG, IMGTOP, PAGETOP, CONTENT) VALUES (1018, 5, 25, 1, '关于对征求2015年度《羊城论坛》选题意见的复函', '/a/lxsgl/1018.html', '', '', '关于对征求2015年度《羊城论坛》选题意见的复函', '2015-01-09 14:21:34', NULL, NULL, '', 0, 0, '<div style=\"top: 0px;\">??</div><span style=\"font-family:宋体;font-size:16px;\"></span><p align=\"center\" style=\"margin: 0cm 0cm 0pt; text-align: center; line-height: 28.5pt; mso-line-height-rule: exactly;\"><span lang=\"EN-US\" style=\"color: black; mso-bidi-font-size: 16.0pt;\"><span style=\"font-family:宋体;font-size:24px;\">&nbsp;</span></span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p align=\"center\" style=\"margin: 0cm 0cm 0pt; text-align: center; line-height: 28.5pt; mso-line-height-rule: exactly;\"><span style=\"font-size: 22pt; mso-hansi-font-family: 宋体;\"><span style=\"font-family:宋体;\">广州市旅游局关于对征求<span lang=\"EN-US\">2015</span>年度</span></span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p align=\"center\" style=\"margin: 0cm 0cm 0pt; text-align: center; line-height: 28.5pt; mso-line-height-rule: exactly;\"><span style=\"font-size: 22pt; mso-hansi-font-family: 宋体;\"><span style=\"font-family:宋体;\">《羊城论坛》选题意见的复函</span></span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p style=\"margin: 0cm 0cm 0pt; line-height: 28.5pt; mso-line-height-rule: exactly;\"><span lang=\"EN-US\" style=\"font-family: 宋体; font-size: 22pt;\">&nbsp;</span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p style=\"margin: 0cm 0cm 0pt; line-height: 28.5pt; mso-line-height-rule: exactly;\"><span style=\"font-family:宋体;font-size:24px;\">市人大常委会研究室：</span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p style=\"margin: 0cm 0cm 0pt; line-height: 28.5pt; text-indent: 30pt; mso-line-height-rule: exactly;\"><span style=\"font-family:宋体;\"><span style=\"font-size:24px;\">你室的《关于征求</span><span lang=\"EN-US\"><span style=\"font-size:24px;\">2015</span></span><span style=\"font-size:24px;\">年度〈羊城论坛〉选题意见的函》已收悉，经研究，我局无意见。</span></span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p style=\"margin: 0cm 0cm 0pt; line-height: 28.5pt; text-indent: 30pt; mso-line-height-rule: exactly;\"><span lang=\"EN-US\"><span style=\"font-family:宋体;font-size:24px;\">&nbsp;</span></span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p style=\"margin: 0cm 0cm 0pt; line-height: 28.5pt; text-indent: 30pt; mso-line-height-rule: exactly;\"><span lang=\"EN-US\"><span style=\"font-family:宋体;font-size:24px;\">&nbsp;</span></span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p style=\"margin: 0cm 0cm 0pt; line-height: 28.5pt; text-indent: 30pt; mso-line-height-rule: exactly;\"><span lang=\"EN-US\"><span style=\"font-family:宋体;font-size:24px;\">&nbsp;</span></span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p style=\"margin: 0cm 0cm 0pt; line-height: 28.5pt; text-indent: 30pt; mso-line-height-rule: exactly;\"><span style=\"font-family:宋体;\"><span lang=\"EN-US\"><span style=\"mso-spacerun: yes;\"><span style=\"font-size:24px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span><span style=\"mso-spacerun: yes;\"><span style=\"font-size:24px;\">&nbsp;&nbsp;&nbsp;&nbsp;</span></span><span style=\"mso-spacerun: yes;\"><span style=\"font-size:24px;\">&nbsp;</span></span></span><span style=\"font-size:24px;\">广州市旅游局</span></span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p style=\"margin: 0cm 0cm 0pt; line-height: 28.5pt; text-indent: 30pt; mso-line-height-rule: exactly; tab-stops: 378.3pt;\"><span style=\"font-family:宋体;\"><span lang=\"EN-US\"><span style=\"mso-spacerun: yes;\"><span style=\"font-size:24px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span><span style=\"mso-spacerun: yes;\"><span style=\"font-size:24px;\">&nbsp;&nbsp;&nbsp;&nbsp;</span></span><span style=\"mso-spacerun: yes;\"><span style=\"font-size:24px;\">&nbsp;</span></span><span style=\"font-size:24px;\">2014</span></span><span style=\"font-size:24px;\">年</span><span lang=\"EN-US\"><span style=\"font-size:24px;\">12</span></span><span style=\"font-size:24px;\">月</span><span lang=\"EN-US\"><span style=\"font-size:24px;\">12</span></span><span style=\"font-size:24px;\">日</span></span></p><span style=\"font-family:宋体;font-size:16px;\"></span>');
INSERT INTO DS_CMS_PAGE (ID, SITEID, CATEGORYID, STATUS, TITLE, URL, METAKEYWORDS, METADESCRIPTION, SUMMARY, RELEASETIME, RELEASESOURCE, RELEASEUSER, IMG, IMGTOP, PAGETOP, CONTENT) VALUES (1019, 5, 25, 1, '关于批准设立广州海之国国际旅行社有限公司的批复', '/a/lxsgl/1019.html', '', '', '关于批准设立广州海之国国际旅行社有限公司的批复', '2015-01-09 14:22:37', NULL, NULL, '', 0, 0, '<div style=\"top: 0px;\">??</div><span style=\"font-family:宋体;font-size:16px;\"></span><p align=\"center\" style=\"margin: 0cm 0cm 0pt; text-align: center; line-height: 28.5pt; vertical-align: baseline; mso-line-height-rule: exactly;\"><span lang=\"EN-US\" style=\"font-size: 22pt; mso-hansi-font-family: 宋体; mso-bidi-font-size: 18.0pt;\"><span style=\"font-family:宋体;\">&nbsp;</span></span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p align=\"center\" style=\"margin: 0cm 0cm 0pt; text-align: center; line-height: 28.5pt; vertical-align: baseline; mso-line-height-rule: exactly;\"><span style=\"font-size: 22pt; mso-hansi-font-family: 宋体; mso-bidi-font-size: 18.0pt;\"><span style=\"font-family:宋体;\">广州市旅游局关于批准设立</span></span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p align=\"center\" style=\"margin: 0cm 0cm 0pt; text-align: center; line-height: 28.5pt; vertical-align: baseline; mso-line-height-rule: exactly;\"><span style=\"font-size: 22pt; mso-hansi-font-family: 宋体; mso-bidi-font-size: 18.0pt;\"><span style=\"font-family:宋体;\">广州海之国国际旅行社有限公司的批<span style=\"mso-font-width: 85%;\">复</span></span></span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p style=\"margin: 0cm 0cm 0pt; line-height: 22pt; vertical-align: baseline; mso-line-height-rule: exactly;\"><span lang=\"EN-US\" style=\"mso-hansi-font-family: 仿宋_GB2312;\"><span style=\"font-family:宋体;font-size:24px;\">&nbsp;</span></span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p style=\"margin: 0cm 0cm 0pt; line-height: 28.5pt; vertical-align: baseline; mso-line-height-rule: exactly;\"><span style=\"font-family:宋体;\"><span style=\"font-size:24px;\">张世海：</span><span lang=\"EN-US\"><span style=\"font-size:24px;\"> </span></span></span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p style=\"margin: 0cm 0cm 0pt; line-height: 28.5pt; text-indent: 31.7pt; vertical-align: baseline; mso-line-height-rule: exactly; mso-char-indent-count: 1.98;\"><span style=\"font-family:宋体;\"><span style=\"font-size:24px;\">您提交的设立旅行社申请材料收悉。</span><span style=\"font-size:24px;\">经审核，您投资的</span><span style=\"font-size:24px;\">广州海之国国际旅行社有限公司</span><span style=\"font-size:24px;\">符合</span><span style=\"font-size:24px;\">《中华人民共和国旅游法》、《旅行社条例》及《旅行社条例实施细则》规定的条件，我局批准设立。现将有关事项核定如下：</span></span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p style=\"margin: 0cm 0cm 0pt; line-height: 28.5pt; text-indent: 31.35pt; vertical-align: baseline; mso-line-height-rule: exactly; mso-char-indent-count: 1.96;\"><span style=\"font-family: 黑体; mso-hansi-font-family: 仿宋_GB2312;\"><span style=\"font-size:24px;\">一、旅行社名称：</span></span><span style=\"font-family:宋体;font-size:24px;\">广州海之国国际旅行社有限公司</span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p style=\"margin: 0cm 0cm 0pt; line-height: 28.5pt; text-indent: 31.35pt; vertical-align: baseline; mso-line-height-rule: exactly; mso-char-indent-count: 1.96;\"><span style=\"font-family: 黑体; mso-hansi-font-family: 仿宋_GB2312;\"><span style=\"font-size:24px;\">二、英文名称：</span></span><span style=\"font-family:宋体;\"><span lang=\"EN-US\" style=\"font-family: \'仿宋_GB2312\',\'serif\';\"><span style=\"font-size:24px;\">GUANGZHOU SER STATEINTERNATIONAL TRAVEL SERVICE</span></span><span lang=\"EN-US\" style=\"mso-hansi-font-family: 仿宋_GB2312;\"><span style=\"font-size:24px;\"> CO.,LTD</span></span></span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p style=\"margin: 0cm 0cm 0pt; line-height: 28.5pt; text-indent: 31.35pt; vertical-align: baseline; mso-line-height-rule: exactly; mso-char-indent-count: 1.96;\"><span style=\"font-family: 黑体; mso-hansi-font-family: 仿宋_GB2312;\"><span style=\"font-size:24px;\">三、许可经营业务：</span></span><span style=\"font-family:宋体;font-size:24px;\">境内旅游与入境旅游</span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p style=\"margin: 0cm 0cm 0pt; line-height: 28.5pt; text-indent: 31.35pt; vertical-align: baseline; mso-line-height-rule: exactly; mso-char-indent-count: 1.96;\"><span style=\"font-family: 黑体; mso-hansi-font-family: 仿宋_GB2312;\"><span style=\"font-size:24px;\">四、出资人、出资额及其出资比例：</span></span><span style=\"font-family:宋体;\"><span style=\"font-size:24px;\">张世海独自认缴出资</span><span lang=\"EN-US\"><span style=\"font-size:24px;\">100</span></span><span style=\"font-size:24px;\">万元人民币，占</span><span lang=\"EN-US\"><span style=\"font-size:24px;\">100%</span></span><span style=\"font-size:24px;\">；实缴出资</span><span lang=\"EN-US\"><span style=\"font-size:24px;\">30</span></span><span style=\"font-size:24px;\">万元。</span></span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p style=\"margin: 0cm 0cm 0pt; line-height: 28.5pt; text-indent: 31.35pt; vertical-align: baseline; mso-line-height-rule: exactly; mso-char-indent-count: 1.96;\"><span style=\"font-family: 黑体; mso-hansi-font-family: 仿宋_GB2312;\"><span style=\"font-size:24px;\">五、法定代表人</span></span><span style=\"font-family:宋体;font-size:24px;\">：张世海</span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p style=\"margin: 0cm 0cm 0pt; line-height: 28.5pt; text-indent: 31.35pt; vertical-align: baseline; mso-line-height-rule: exactly; mso-char-indent-count: 1.96;\"><span style=\"font-family: 黑体; mso-hansi-font-family: 仿宋_GB2312;\"><span style=\"font-size:24px;\">六、企业形式：</span></span><span style=\"font-family:宋体;font-size:24px;\">有限责任公司</span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p style=\"margin: 0cm 0cm 0pt; line-height: 28.5pt; text-indent: 31.35pt; vertical-align: baseline; mso-line-height-rule: exactly; mso-char-indent-count: 1.96;\"><span style=\"font-family: 黑体; mso-hansi-font-family: 仿宋_GB2312;\"><span style=\"font-size:24px;\">七、经营场所：</span></span><span style=\"font-family:宋体;\"><span style=\"font-size:24px;\">广州市白云区棠景街三元里大道</span><span lang=\"EN-US\"><span style=\"font-size:24px;\">1070</span></span><span style=\"font-size:24px;\">号</span><span lang=\"EN-US\"><span style=\"font-size:24px;\">A902</span></span><span style=\"font-size:24px;\">房（仅限办公用途）</span></span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p style=\"margin: 0cm 0cm 0pt; line-height: 28.5pt; text-indent: 31.35pt; vertical-align: baseline; mso-line-height-rule: exactly; mso-char-indent-count: 1.96;\"><span style=\"font-family: 黑体; mso-hansi-font-family: 仿宋_GB2312;\"><span style=\"font-size:24px;\">八、许可证编号：</span></span><span lang=\"EN-US\" style=\"font-family: \'仿宋_GB2312\',\'serif\'; mso-hansi-font-family: 宋体; mso-font-kerning: 0pt; mso-bidi-font-family: 宋体;\"><span style=\"font-family:宋体;font-size:24px;\">L-GD0</span></span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p style=\"margin: 0cm 0cm 0pt; line-height: 28.5pt; text-indent: 31.35pt; vertical-align: baseline; mso-line-height-rule: exactly; mso-char-indent-count: 1.96;\"><span style=\"font-family:宋体;\"><span style=\"font-size:24px;\">请持该批准文件及旅行社业务经营许可证（副本）在</span><span lang=\"EN-US\"><span style=\"font-size:24px;\">1</span></span><span style=\"font-size:24px;\">个月内办妥税务登记、组织机构代码证及公章备案。</span><span style=\"font-size:24px;\">根据《中华人民共和国旅游法》第三十一条、《旅行社条例》第十三条，</span><span style=\"font-size:24px;\">广州海之国国际旅行社有限公司</span><span style=\"font-size:24px;\">应当在办妥上述事项后</span><span lang=\"EN-US\"><span style=\"font-size:24px;\">3</span></span><span style=\"font-size:24px;\">个工作日内，</span><span style=\"color: black; mso-ascii-font-family: \'Times New Roman\';\"><span style=\"font-size:24px;\">在</span></span><span style=\"color: black;\"><span style=\"font-size:24px;\">国家旅游局指定的银行</span></span><span style=\"color: black; mso-ascii-font-family: \'Times New Roman\';\"><span style=\"font-size:24px;\">开设专门的旅游服务质量保证金账户，</span></span><span style=\"color: black;\"><span style=\"font-size:24px;\">存入</span><span lang=\"EN-US\"><span style=\"font-size:24px;\">20</span></span><span style=\"font-size:24px;\">万元旅游服务质量保证金或者办理同等额度的银行担保，并在此后</span></span><span lang=\"EN-US\" style=\"mso-hansi-font-family: 仿宋_GB2312;\"><span style=\"font-size:24px;\">7</span></span><span style=\"font-size:24px;\">个工作日内将相关的证明文件交回我局备案。您可以</span><span style=\"font-size:24px;\">通过广州市商事主体信息公示平台（</span><span lang=\"EN-US\"><span style=\"font-size:24px;\">http://cri.gz.gov.cn</span></span><span style=\"font-size:24px;\">）查询许可审批的内容。</span></span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p style=\"margin: 0cm 0cm 0pt; line-height: 28.5pt; text-indent: 31.35pt; vertical-align: baseline; mso-line-height-rule: exactly; mso-char-indent-count: 1.96; tab-stops: 376.25pt;\"><span style=\"font-family:宋体;font-size:24px;\">旅行社开业后，应当遵守《中华人民共和国旅游法》、《旅行社条例》和《旅行社条例实施细则》及其它有关法律、法规和规章，确保守法经营、优质服务。</span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p style=\"margin: 0cm 0cm 0pt; line-height: 28.5pt; text-indent: 31.35pt; vertical-align: baseline; mso-line-height-rule: exactly; mso-char-indent-count: 1.96;\"><span style=\"font-family:宋体;\"><span style=\"font-size:24px;\">专此批复</span><span lang=\"EN-US\"><span style=\"mso-spacerun: yes;\"><span style=\"font-size:24px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span></span></span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p style=\"margin: 0cm 0cm 0pt; line-height: 28.5pt; text-indent: 31.35pt; vertical-align: baseline; mso-line-height-rule: exactly; mso-char-indent-count: 1.96;\"><span lang=\"EN-US\" style=\"mso-hansi-font-family: 仿宋_GB2312;\"><span style=\"font-family:宋体;font-size:24px;\">&nbsp;</span></span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p style=\"margin: 0cm 0cm 0pt; line-height: 28.5pt; text-indent: 31.35pt; vertical-align: baseline; mso-line-height-rule: exactly; mso-char-indent-count: 1.96;\"><span lang=\"EN-US\" style=\"mso-hansi-font-family: 仿宋_GB2312;\"><span style=\"font-family:宋体;font-size:24px;\">&nbsp;</span></span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p style=\"margin: 0cm 0cm 0pt; line-height: 28.5pt; text-indent: 31.35pt; vertical-align: baseline; mso-line-height-rule: exactly; mso-char-indent-count: 1.96;\"><span lang=\"EN-US\" style=\"mso-hansi-font-family: 仿宋_GB2312;\"><span style=\"font-family:宋体;font-size:24px;\">&nbsp;</span></span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p style=\"margin: 0cm 0cm 0pt; line-height: 28.5pt; text-indent: 294.25pt; vertical-align: baseline; mso-line-height-rule: exactly; mso-char-indent-count: 18.39; tab-stops: 376.25pt;\"><span style=\"font-family:宋体;font-size:24px;\">广州市旅游局</span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p style=\"margin: 0cm 0cm 0pt; line-height: 28.5pt; mso-line-height-rule: exactly; tab-stops: 375.65pt 391.3pt;\"><span lang=\"EN-US\"><span style=\"font-family:宋体;\"><span style=\"mso-spacerun: yes;\"><span style=\"font-size:24px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span><st1:chsdate w:st=\"on\" year=\"2014\" month=\"12\" day=\"23\" islunardate=\"False\" isrocdate=\"False\"><span style=\"font-size:24px;\">2014</span><span lang=\"EN-US\"><span lang=\"EN-US\"><span style=\"font-size:24px;\">年12</span></span></span><span lang=\"EN-US\"><span lang=\"EN-US\"><span style=\"font-size:24px;\">月23</span></span></span><span lang=\"EN-US\"><span lang=\"EN-US\"><span style=\"font-size:24px;\">日</span></span></span></st1:chsdate></span></span></p><span style=\"font-family:宋体;font-size:16px;\"></span>');
INSERT INTO DS_CMS_PAGE (ID, SITEID, CATEGORYID, STATUS, TITLE, URL, METAKEYWORDS, METADESCRIPTION, SUMMARY, RELEASETIME, RELEASESOURCE, RELEASEUSER, IMG, IMGTOP, PAGETOP, CONTENT) VALUES (1020, 5, 25, 1, '关于批准设立广州增悦国际旅行社有限公司的批复', '/a/lxsgl/1020.html', '', '', '关于批准设立广州增悦国际旅行社有限公司的批复', '2015-01-09 05:23:33', NULL, NULL, '', 0, 0, '<div style=\"top: 0px;\">??</div><span style=\"font-family:宋体;font-size:16px;\"></span><p align=\"center\" style=\"margin: 0cm 0cm 0pt; text-align: center;\"><span lang=\"EN-US\" style=\"color: black; mso-bidi-font-size: 16.0pt;\"><span style=\"font-family:Times New Roman;font-size:24px;\">&nbsp;</span></span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p align=\"right\" style=\"margin: 0cm 0cm 0pt; text-align: right; line-height: 26.5pt; -ms-word-break: break-all; mso-line-height-rule: exactly;\"><span style=\"font-family:宋体;\"><span lang=\"EN-US\" style=\"color: black; mso-bidi-font-size: 16.0pt;\"><span style=\"mso-spacerun: yes;\"><span style=\"font-size:24px;\">&nbsp; </span></span><span style=\"mso-spacerun: yes;\"><span style=\"font-size:24px;\">&nbsp;</span></span></span><span style=\"color: black; mso-bidi-font-size: 16.0pt;\"><span style=\"font-size:24px;\">穗旅发〔</span><span lang=\"EN-US\"><span style=\"font-size:24px;\">2014</span></span><span style=\"font-size:24px;\">〕</span><span lang=\"EN-US\"><span style=\"mso-spacerun: yes;\"><span style=\"font-size:24px;\">&nbsp;</span></span></span><span style=\"font-size:24px;\">号</span></span></span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p align=\"center\" style=\"margin: 0cm 0cm 0pt; text-align: center; line-height: 28.5pt; vertical-align: baseline; mso-line-height-rule: exactly;\"><span lang=\"EN-US\" style=\"font-size: 22pt; mso-hansi-font-family: 宋体; mso-bidi-font-size: 18.0pt;\"><span style=\"font-family:宋体;\">&nbsp;</span></span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p align=\"center\" style=\"margin: 0cm 0cm 0pt; text-align: center; line-height: 28.5pt; vertical-align: baseline; mso-line-height-rule: exactly;\"><span style=\"font-size: 22pt; mso-hansi-font-family: 宋体; mso-bidi-font-size: 18.0pt;\"><span style=\"font-family:宋体;\">广州市旅游局关于批准设立</span></span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p align=\"center\" style=\"margin: 0cm 0cm 0pt; text-align: center; line-height: 28.5pt; vertical-align: baseline; mso-line-height-rule: exactly;\"><span style=\"font-size: 22pt; mso-hansi-font-family: 宋体; mso-bidi-font-size: 18.0pt;\"><span style=\"font-family:宋体;\">广州增悦国际旅行社有限公司的批<span style=\"mso-font-width: 85%;\">复</span></span></span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p style=\"margin: 0cm 0cm 0pt; line-height: 22pt; vertical-align: baseline; mso-line-height-rule: exactly;\"><span lang=\"EN-US\" style=\"mso-hansi-font-family: 仿宋_GB2312;\"><span style=\"font-family:宋体;font-size:24px;\">&nbsp;</span></span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p style=\"margin: 0cm 0cm 0pt; line-height: 28.5pt; vertical-align: baseline; mso-line-height-rule: exactly;\"><span style=\"font-family:宋体;\"><span style=\"font-size:24px;\">徐沛欢：</span><span lang=\"EN-US\"><span style=\"font-size:24px;\"> </span></span></span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p style=\"margin: 0cm 0cm 0pt; line-height: 28.5pt; text-indent: 31.7pt; vertical-align: baseline; mso-line-height-rule: exactly; mso-char-indent-count: 1.98;\"><span style=\"font-family:宋体;\"><span style=\"font-size:24px;\">您提交的设立旅行社申请材料收悉。</span><span style=\"font-size:24px;\">经审核，您和欧阳正章、钟健威、徐玲转、李柯投资的</span><span style=\"font-size:24px;\">广州增悦国际旅行社有限公司</span><span style=\"font-size:24px;\">符合</span><span style=\"font-size:24px;\">《中华人民共和国旅游法》、《旅行社条例》及《旅行社条例实施细则》规定的条件，我局批准设立。现将有关事项核定如下：</span></span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p style=\"margin: 0cm 0cm 0pt; line-height: 28.5pt; text-indent: 31.35pt; vertical-align: baseline; mso-line-height-rule: exactly; mso-char-indent-count: 1.96;\"><span style=\"font-family: 黑体; mso-hansi-font-family: 仿宋_GB2312;\"><span style=\"font-size:24px;\">一、旅行社名称：</span></span><span style=\"font-family:宋体;font-size:24px;\">广州增悦国际风旅行社有限公司</span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p style=\"margin: 0cm 0cm 0pt; line-height: 28.5pt; text-indent: 31.35pt; vertical-align: baseline; mso-line-height-rule: exactly; mso-char-indent-count: 1.96;\"><span style=\"font-family: 黑体; mso-hansi-font-family: 仿宋_GB2312;\"><span style=\"font-size:24px;\">二、英文名称：</span></span><span style=\"font-family:宋体;\"><span lang=\"EN-US\" style=\"font-family: \'仿宋_GB2312\',\'serif\';\"><span style=\"font-size:24px;\">GUANGZHOU ZENGYUE INTERNATIONALTRAVEL SERVICE</span></span><span lang=\"EN-US\" style=\"mso-hansi-font-family: 仿宋_GB2312;\"><span style=\"font-size:24px;\"> CO.,LTD</span></span></span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p style=\"margin: 0cm 0cm 0pt; line-height: 28.5pt; text-indent: 31.35pt; vertical-align: baseline; mso-line-height-rule: exactly; mso-char-indent-count: 1.96;\"><span style=\"font-family: 黑体; mso-hansi-font-family: 仿宋_GB2312;\"><span style=\"font-size:24px;\">三、许可经营业务：</span></span><span style=\"font-family:宋体;font-size:24px;\">境内旅游与入境旅游</span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p style=\"margin: 0cm 0cm 0pt; line-height: 28.5pt; text-indent: 31.35pt; vertical-align: baseline; mso-line-height-rule: exactly; mso-char-indent-count: 1.96;\"><span style=\"font-family: 黑体; mso-hansi-font-family: 仿宋_GB2312;\"><span style=\"font-size:24px;\">四、出资人、出资额及其出资比例：</span></span><span style=\"font-family:宋体;\"><span style=\"font-size:24px;\">出资总额</span><span lang=\"EN-US\"><span style=\"font-size:24px;\">30</span></span><span style=\"font-size:24px;\">万元人民币。其中，徐沛欢出资</span><span lang=\"EN-US\"><span style=\"font-size:24px;\">15.3</span></span><span style=\"font-size:24px;\">万元，占</span><span lang=\"EN-US\"><span style=\"font-size:24px;\">51%</span></span><span style=\"font-size:24px;\">；欧阳正章出资</span><span lang=\"EN-US\"><span style=\"font-size:24px;\">8.7</span></span><span style=\"font-size:24px;\">万元，占</span><span lang=\"EN-US\"><span style=\"font-size:24px;\">29%</span></span><span style=\"font-size:24px;\">；钟健威出资</span><span lang=\"EN-US\"><span style=\"font-size:24px;\">3</span></span><span style=\"font-size:24px;\">万元，占</span><span lang=\"EN-US\"><span style=\"font-size:24px;\">10%</span></span><span style=\"font-size:24px;\">；徐玲转和李柯各出资</span><span lang=\"EN-US\"><span style=\"font-size:24px;\">1.5</span></span><span style=\"font-size:24px;\">万元，各占</span><span lang=\"EN-US\"><span style=\"font-size:24px;\">5%</span></span><span style=\"font-size:24px;\">。</span></span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p style=\"margin: 0cm 0cm 0pt; line-height: 28.5pt; text-indent: 31.35pt; vertical-align: baseline; mso-line-height-rule: exactly; mso-char-indent-count: 1.96;\"><span style=\"font-family: 黑体; mso-hansi-font-family: 仿宋_GB2312;\"><span style=\"font-size:24px;\">五、法定代表人</span></span><span style=\"font-family:宋体;font-size:24px;\">：徐沛欢</span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p style=\"margin: 0cm 0cm 0pt; line-height: 28.5pt; text-indent: 31.35pt; vertical-align: baseline; mso-line-height-rule: exactly; mso-char-indent-count: 1.96;\"><span style=\"font-family: 黑体; mso-hansi-font-family: 仿宋_GB2312;\"><span style=\"font-size:24px;\">六、企业形式：</span></span><span style=\"font-family:宋体;font-size:24px;\">有限责任公司</span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p style=\"margin: 0cm 0cm 0pt; line-height: 28.5pt; text-indent: 31.35pt; vertical-align: baseline; mso-line-height-rule: exactly; mso-char-indent-count: 1.96;\"><span style=\"font-family: 黑体; mso-hansi-font-family: 仿宋_GB2312;\"><span style=\"font-size:24px;\">七、经营场所：</span></span><span style=\"font-family:宋体;\"><span style=\"font-size:24px;\">广州市增城永宁街长岗村凤凰开发区汽车城东路</span><span lang=\"EN-US\"><span style=\"font-size:24px;\">2</span></span><span style=\"font-size:24px;\">号</span><span lang=\"EN-US\"><span style=\"font-size:24px;\">105</span></span><span style=\"font-size:24px;\">号商铺二层</span><span lang=\"EN-US\"><span style=\"font-size:24px;\">B4</span></span><span style=\"font-size:24px;\">、</span><span lang=\"EN-US\"><span style=\"font-size:24px;\">B5</span></span></span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p style=\"margin: 0cm 0cm 0pt; line-height: 28.5pt; text-indent: 31.35pt; vertical-align: baseline; mso-line-height-rule: exactly; mso-char-indent-count: 1.96;\"><span style=\"font-family: 黑体; mso-hansi-font-family: 仿宋_GB2312;\"><span style=\"font-size:24px;\">八、许可证编号：</span></span><span lang=\"EN-US\" style=\"font-family: \'仿宋_GB2312\',\'serif\'; mso-hansi-font-family: 宋体; mso-font-kerning: 0pt; mso-bidi-font-family: 宋体;\"><span style=\"font-family:宋体;font-size:24px;\">L-GD0</span></span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p style=\"margin: 0cm 0cm 0pt; line-height: 28.5pt; text-indent: 31.35pt; vertical-align: baseline; mso-line-height-rule: exactly; mso-char-indent-count: 1.96;\"><span style=\"font-family:宋体;\"><span style=\"font-size:24px;\">请持该批准文件及旅行社业务经营许可证（副本）在</span><span lang=\"EN-US\"><span style=\"font-size:24px;\">1</span></span><span style=\"font-size:24px;\">个月内办妥税务登记、组织机构代码证及公章备案。</span><span style=\"font-size:24px;\">根据《中华人民共和国旅游法》第三十一条、《旅行社条例》第十三条，</span><span style=\"font-size:24px;\">广州增悦国际旅行社有限公司</span><span style=\"font-size:24px;\">应当在办妥上述事项后</span><span lang=\"EN-US\"><span style=\"font-size:24px;\">3</span></span><span style=\"font-size:24px;\">个工作日内，</span><span style=\"color: black; mso-ascii-font-family: \'Times New Roman\';\"><span style=\"font-size:24px;\">在</span></span><span style=\"color: black;\"><span style=\"font-size:24px;\">国家旅游局指定的银行</span></span><span style=\"color: black; mso-ascii-font-family: \'Times New Roman\';\"><span style=\"font-size:24px;\">开设专门的旅游服务质量保证金账户，</span></span><span style=\"color: black;\"><span style=\"font-size:24px;\">存入</span><span lang=\"EN-US\"><span style=\"font-size:24px;\">20</span></span><span style=\"font-size:24px;\">万元旅游服务质量保证金或者办理同等额度的银行担保，并在此后</span></span><span lang=\"EN-US\" style=\"mso-hansi-font-family: 仿宋_GB2312;\"><span style=\"font-size:24px;\">7</span></span><span style=\"font-size:24px;\">个工作日内将相关的证明文件交回我局备案。您可以</span><span style=\"font-size:24px;\">通过广州市商事主体信息公示平台（</span><span lang=\"EN-US\"><span style=\"font-size:24px;\">http://cri.gz.gov.cn</span></span><span style=\"font-size:24px;\">）查询许可审批的内容。</span></span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p style=\"margin: 0cm 0cm 0pt; line-height: 28.5pt; text-indent: 31.35pt; vertical-align: baseline; mso-line-height-rule: exactly; mso-char-indent-count: 1.96; tab-stops: 376.25pt;\"><span style=\"font-family:宋体;font-size:24px;\">旅行社开业后，应当遵守《中华人民共和国旅游法》、《旅行社条例》和《旅行社条例实施细则》及其它有关法律、法规和规章，确保守法经营、优质服务。</span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p style=\"margin: 0cm 0cm 0pt; line-height: 28.5pt; text-indent: 31.35pt; vertical-align: baseline; mso-line-height-rule: exactly; mso-char-indent-count: 1.96;\"><span style=\"font-family:宋体;\"><span style=\"font-size:24px;\">专此批复</span><span lang=\"EN-US\"><span style=\"mso-spacerun: yes;\"><span style=\"font-size:24px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span></span></span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p style=\"margin: 0cm 0cm 0pt; line-height: 28.5pt; text-indent: 31.35pt; vertical-align: baseline; mso-line-height-rule: exactly; mso-char-indent-count: 1.96;\"><span lang=\"EN-US\" style=\"mso-hansi-font-family: 仿宋_GB2312;\"><span style=\"font-family:宋体;font-size:24px;\">&nbsp;</span></span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p style=\"margin: 0cm 0cm 0pt; line-height: 28.5pt; text-indent: 294.25pt; vertical-align: baseline; mso-line-height-rule: exactly; mso-char-indent-count: 18.39; tab-stops: 376.25pt;\"><span style=\"font-family:宋体;font-size:24px;\">广州市旅游局</span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p style=\"margin: 0cm 0cm 0pt; line-height: 28.5pt; mso-line-height-rule: exactly; tab-stops: 375.65pt 391.3pt;\"><span lang=\"EN-US\"><span style=\"font-family:宋体;\"><span style=\"mso-spacerun: yes;\"><span style=\"font-size:24px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span><st1:chsdate w:st=\"on\" year=\"2014\" month=\"12\" day=\"26\" islunardate=\"False\" isrocdate=\"False\"><span style=\"font-size:24px;\">2014</span><span lang=\"EN-US\"><span lang=\"EN-US\"><span style=\"font-size:24px;\">年12</span></span></span><span lang=\"EN-US\"><span lang=\"EN-US\"><span style=\"font-size:24px;\">月26</span></span></span><span lang=\"EN-US\"><span lang=\"EN-US\"><span style=\"font-size:24px;\">日</span></span></span></st1:chsdate></span></span></p><span style=\"font-family:宋体;font-size:16px;\"></span>');
