INSERT INTO DS_CMS_PAGE (ID, SITEID, CATEGORYID, STATUS, TITLE, URL, METAKEYWORDS, METADESCRIPTION, SUMMARY, RELEASETIME, RELEASESOURCE, RELEASEUSER, IMG, IMGTOP, PAGETOP, CONTENT) VALUES (1047, 5, 25, 1, '广州市旅游局关于开展旅行社服务网点规范经营专项治理的情况通报', '/a/lxsgl/1047.html', '', '', '广州市旅游局关于开展旅行社服务网点规范经营专项治理的情况通报', '2015-04-30 15:24:01', NULL, NULL, '', 0, 0, '<div style=\"top: 0px;\">??</div><span style=\"font-family:宋体;font-size:16px;\"></span><div style=\"mso-layout-grid-char-alt: -1675;\"><span style=\"font-family:宋体;font-size:16px;\"></span><p align=\"center\" style=\"margin: 0cm 0cm 0pt; text-align: center; line-height: 26pt; mso-line-height-rule: exactly;\"><span lang=\"EN-US\" style=\"font-size: 22pt;\"><span style=\"font-family:宋体;\">&nbsp;</span></span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p align=\"center\" style=\"margin: 0cm 0cm 0pt; text-align: center; line-height: 28.5pt; mso-line-height-rule: exactly;\"><span style=\"font-size: 22pt;\"><span style=\"font-family:宋体;\">广州市旅游局关于开展旅行社服务网点</span></span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p align=\"center\" style=\"margin: 0cm 0cm 0pt; text-align: center; line-height: 28.5pt; mso-line-height-rule: exactly;\"><span style=\"font-size: 22pt;\"><span style=\"font-family:宋体;\">规范经营专项治理的情况通报</span></span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p style=\"margin: 0cm 0cm 0pt; line-height: 26pt; mso-line-height-rule: exactly;\"><span lang=\"EN-US\"><span style=\"font-family:宋体;font-size:24px;\">&nbsp;</span></span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p style=\"margin: 0cm 0cm 0pt; line-height: 26pt; text-indent: 32.25pt; mso-line-height-rule: exactly;\"><span style=\"font-family:宋体;\"><st1:chsdate w:st=\"on\" isrocdate=\"False\" islunardate=\"False\" day=\"1\" month=\"12\" year=\"2014\"><span lang=\"EN-US\"><span style=\"font-size:24px;\">2014</span></span><span style=\"font-size:24px;\">年</span><span lang=\"EN-US\"><span style=\"font-size:24px;\">12</span></span><span style=\"font-size:24px;\">月</span><span lang=\"EN-US\"><span style=\"font-size:24px;\">1</span></span><span style=\"font-size:24px;\">日</span></st1:chsdate><span style=\"font-size:24px;\">至</span><st1:chsdate w:st=\"on\" isrocdate=\"False\" islunardate=\"False\" day=\"31\" month=\"3\" year=\"2015\"><span lang=\"EN-US\"><span style=\"font-size:24px;\">2015</span></span><span style=\"font-size:24px;\">年</span><span lang=\"EN-US\"><span style=\"font-size:24px;\">3</span></span><span style=\"font-size:24px;\">月</span><span lang=\"EN-US\"><span style=\"font-size:24px;\">31</span></span><span style=\"font-size:24px;\">日</span></st1:chsdate><span style=\"font-size:24px;\">，我局在全市范围内开展了旅行社服务网点经营专项治理行动。经过旅行社自查、执法部门抽查、总结提高等三个阶段，治理行动基本结束，现将执行情况予以通报。</span></span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p style=\"margin: 0cm 0cm 0pt; line-height: 26pt; text-indent: 32.25pt; mso-line-height-rule: exactly;\"><span style=\"font-family:宋体;font-size:24px;\">一、基本情况</span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p style=\"margin: 0cm 0cm 0pt; line-height: 26pt; text-indent: 32.25pt; mso-line-height-rule: exactly;\"><span style=\"font-family:宋体;\"><span style=\"font-size:24px;\">我市设有服务网点的旅行社共</span><span lang=\"EN-US\"><span style=\"font-size:24px;\">69</span></span><span style=\"font-size:24px;\">家，设有服务网点共</span><span lang=\"EN-US\"><span style=\"font-size:24px;\">540</span></span><span style=\"font-size:24px;\">个，其中设立服务网点数量居前的旅行社是广之旅、南湖国旅、广东国旅、广东中旅、康辉、广东青旅、和平、假日通、远景、金马，服务网点主要分布在越秀、天河、海珠、番禺</span><span lang=\"EN-US\"><span style=\"font-size:24px;\">4</span></span><span style=\"font-size:24px;\">个区。</span></span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p style=\"margin: 0cm 0cm 0pt; line-height: 26pt; text-indent: 32.25pt; mso-line-height-rule: exactly;\"><span style=\"font-family:宋体;\"><span style=\"font-size:24px;\">服务网点</span><span style=\"font-size:24px;\">总体运营情况较为规范，主要表现在：</span></span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p style=\"margin: 0cm 0cm 0pt; line-height: 26pt; text-indent: 32.25pt; mso-line-height-rule: exactly;\"><span style=\"font-family:宋体;font-size:24px;\">（一）基本能够及时到区、县级市旅游行政主管部门备案，营业执照和总社证照等基本材料齐全并能亮照经营，装修装饰能够使用设立社统一的标识和品牌。</span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p style=\"margin: 0cm 0cm 0pt; line-height: 26pt; text-indent: 32.25pt; mso-line-height-rule: exactly;\"><span style=\"font-family:宋体;font-size:24px;\">（二）服务网点能够按照自身的经营范围为顾客提供招徕、咨询服务，接受设立社的统一管理，发票、印章、资金结算、广告宣传、旅游合同等管理基本规范，暂无发现虚假宣传、出租出借许可证等违法行为。</span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p style=\"margin: 0cm 0cm 0pt; line-height: 26pt; text-indent: 32.25pt; mso-line-height-rule: exactly;\"><span style=\"font-family:宋体;font-size:24px;\">（三）服务网点的员工基本由设立社统一聘用、培训、考核、任用，能够统一签订劳动合同，购买社保。</span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p style=\"margin: 0cm 0cm 0pt; line-height: 26pt; text-indent: 32.25pt; mso-line-height-rule: exactly;\"><span style=\"font-family:宋体;font-size:24px;\">二、存在问题</span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p style=\"margin: 0cm 0cm 0pt; line-height: 26pt; text-indent: 32.25pt; mso-line-height-rule: exactly;\"><span style=\"font-family:宋体;font-size:24px;\">（一）个别服务网点负责人不能在总社参加购买社保。</span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p style=\"margin: 0cm 0cm 0pt; line-height: 26pt; text-indent: 32.25pt; mso-line-height-rule: exactly;\"><span style=\"font-family:宋体;font-size:24px;\">（二）因条件限制或业务量不够大，部分旅行社对服务网点未实行电脑联网管理。</span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p style=\"margin: 0cm 0cm 0pt; line-height: 26pt; text-indent: 32.25pt; mso-line-height-rule: exactly;\"><span style=\"font-family:宋体;font-size:24px;\">（三）个别服务网点未能及时办理备案登记证或备案登记证遗失。</span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p style=\"margin: 0cm 0cm 0pt; line-height: 26pt; text-indent: 32.25pt; mso-line-height-rule: exactly;\"><span style=\"font-family:宋体;font-size:24px;\">（四）个别服务网点未使用设立社统一的标识与品牌，擅自设计策划旅游产品，未使用总社制定的旅游产品价格和宣传资料，在营业场所未设置投诉电话及提醒旅游者签订旅游合同给与索取正式发票的提示牌。</span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p style=\"margin: 0cm 0cm 0pt; line-height: 26pt; text-indent: 32.25pt; mso-line-height-rule: exactly;\"><span style=\"font-family:宋体;font-size:24px;\">（五）个别服务网点未按要求在场所悬挂备案登记证、营业执照。</span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p style=\"margin: 0cm 0cm 0pt; line-height: 26pt; text-indent: 32.25pt; mso-line-height-rule: exactly;\"><span style=\"font-family:宋体;font-size:24px;\">对存在问题的旅行社，我局均在检查登记表中予以记录并告知；问题较为严重的，我局已发出整改通知书并约谈相关负责人。目前均已整改完毕。</span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p style=\"margin: 0cm 0cm 0pt; line-height: 26pt; text-indent: 32.25pt; mso-line-height-rule: exactly;\"><span style=\"font-family:宋体;font-size:24px;\">各旅行社在今后的工作中要进一步加强服务网点的“四统一”（统一管理、统一财务、统一招徕、统一咨询）管理，遵守《广州市旅行社内部管理工作规范》，不断提高服务质量，共同维护我市旅游市场秩序。</span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p style=\"margin: 0cm 0cm 0pt; line-height: 26pt; text-indent: 32.25pt; mso-line-height-rule: exactly;\"><span style=\"font-family:宋体;font-size:24px;\">专此通报。</span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p style=\"margin: 0cm 0cm 0pt; line-height: 20pt; text-indent: 32.3pt; mso-line-height-rule: exactly;\"><span lang=\"EN-US\"><span style=\"font-family:宋体;font-size:24px;\">&nbsp;</span></span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p style=\"margin: 0cm 0cm 0pt; line-height: 20pt; text-indent: 32.3pt; mso-line-height-rule: exactly;\"><span lang=\"EN-US\"><span style=\"font-family:宋体;font-size:24px;\">&nbsp;</span></span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p style=\"margin: 0cm 0cm 0pt; line-height: 20pt; text-indent: 32.3pt; mso-line-height-rule: exactly;\"><span lang=\"EN-US\"><span style=\"font-family:宋体;font-size:24px;\">&nbsp;</span></span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p style=\"margin: 0cm 0cm 0pt; line-height: 26pt; text-indent: 275.8pt; mso-line-height-rule: exactly; mso-char-indent-count: 17.69;\"><span style=\"font-family:宋体;font-size:24px;\">广州市旅游局</span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p style=\"margin: 0cm 0cm 0pt; line-height: 26pt; text-indent: 259.75pt; mso-line-height-rule: exactly; mso-char-indent-count: 16.66;\"><st1:chsdate w:st=\"on\" isrocdate=\"False\" islunardate=\"False\" day=\"22\" month=\"4\" year=\"2015\"><span style=\"font-family:宋体;\"><span lang=\"EN-US\"><span style=\"font-size:24px;\">2015</span></span><span style=\"font-size:24px;\">年</span><span lang=\"EN-US\"><span style=\"font-size:24px;\">4</span></span><span style=\"font-size:24px;\">月</span><span lang=\"EN-US\"><span style=\"font-size:24px;\">22</span></span><span style=\"font-size:24px;\">日</span></span></st1:chsdate></p><span style=\"font-family:宋体;font-size:16px;\"></span></div><span style=\"font-family:宋体;font-size:16px;\"></span><span lang=\"EN-US\" style=\"font-family: \'仿宋_GB2312\',\'serif\'; font-size: 16pt; mso-font-kerning: 1.0pt; mso-bidi-font-family: \'Times New Roman\'; mso-hansi-font-family: \'Times New Roman\'; mso-fareast-language: ZH-CN; mso-ansi-language: EN-US; mso-bidi-language: AR-SA;\"><br clear=\"all\" style=\"page-break-before: always; mso-break-type: section-break;\" /><span style=\"font-family:宋体;\"></span></span>');
INSERT INTO DS_CMS_PAGE (ID, SITEID, CATEGORYID, STATUS, TITLE, URL, METAKEYWORDS, METADESCRIPTION, SUMMARY, RELEASETIME, RELEASESOURCE, RELEASEUSER, IMG, IMGTOP, PAGETOP, CONTENT) VALUES (1073, 5, 25, 1, '旅游服务质量保证金降低交存比例公告', '/a/lxsgl/1073.html', '', '', '旅游服务质量保证金降低交存比例公告', '2015-03-26 16:05:12', NULL, NULL, '', 0, 0, '<div style=\"top: 0px;\">??</div><span style=\"font-family:宋体;font-size:16px;\"></span><p align=\"center\" style=\"margin: 0cm 0cm 0pt; text-align: center; line-height: 28.5pt; mso-line-height-rule: exactly;\"><span lang=\"EN-US\" style=\"color: black; mso-bidi-font-size: 16.0pt;\"><span style=\"font-family:Times New Roman;font-size:24px;\">&nbsp;</span></span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p align=\"center\" style=\"margin: 0cm 0cm 0pt; text-align: center; line-height: 28.5pt; mso-line-height-rule: exactly;\"><span lang=\"EN-US\" style=\"color: black; mso-bidi-font-size: 16.0pt;\"><span style=\"font-family:Times New Roman;font-size:24px;\">&nbsp;</span></span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p align=\"center\" style=\"margin: 0cm 0cm 0pt; text-align: center; line-height: 28.5pt; mso-line-height-rule: exactly;\"><span style=\"font-size: 22pt; mso-bidi-font-family: 宋体-18030; mso-hansi-font-family: 宋体;\"><span style=\"font-family:宋体;\">旅游服务质量保证金降低交存比例公告</span></span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p align=\"center\" style=\"margin: 0cm 0cm 0pt; text-align: center; line-height: 28.5pt; mso-line-height-rule: exactly;\"><span lang=\"EN-US\" style=\"font-size: 18pt; mso-bidi-font-family: 宋体-18030; mso-hansi-font-family: 宋体;\"><span style=\"font-family:宋体;\">&nbsp;</span></span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p align=\"center\" style=\"margin: 0cm 0cm 0pt; text-align: center; line-height: 28.5pt; mso-line-height-rule: exactly;\"><span style=\"font-family:宋体;\"><span lang=\"EN-US\" style=\"font-size: 18pt; mso-bidi-font-family: 宋体-18030; mso-hansi-font-family: 宋体;\">2015</span><span style=\"font-size: 18pt; mso-bidi-font-family: 宋体-18030; mso-hansi-font-family: 宋体;\">年第<span lang=\"EN-US\">1</span>号（总第<span lang=\"EN-US\">8</span>号）</span></span></p><span style=\"font-family:宋体;font-size:16px;\"></span><p style=\"margin: 0cm 0cm 0pt; line-height: 28.5pt; text-indent: 32pt; mso-line-height-rule: exactly; mso-char-indent-count: 2.0;\"><span style=\"font-family:宋体;\"><span style=\"font-size:24px;\">按照《旅游法》和《旅行社条例》，以下</span><span lang=\"EN-US\"><span style=\"font-size:24px;\">9</span></span><span style=\"font-size:24px;\">家旅行社旅游服务质量保证金降低交存比例</span><span lang=\"EN-US\"><span style=\"font-size:24px;\">50%</span></span><span style=\"font-size:24px;\">，现公告如下：</span></span></p><span style=\"font-family:宋体;font-size:16px;\"></span><table width=\"634\" style=\"margin: auto auto auto 10.35pt; border-collapse: collapse; mso-table-layout-alt: fixed; mso-padding-alt: 0cm 5.4pt 0cm 5.4pt;\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\"><span style=\"font-family:宋体;\"> </span><tbody><tr style=\"height: 30.25pt; mso-yfti-irow: 0; mso-yfti-firstrow: yes;\"><span style=\"font-family:宋体;\">  </span><td width=\"61\" style=\"border-width: 1pt; border-style: solid; border-color: windowtext windowtext black; padding: 0cm 5.4pt; border-image: none; width: 45.85pt; height: 30.25pt; background-color: transparent;\" rowspan=\"3\"><span style=\"font-family:宋体;font-size:16px;\">  </span><p align=\"center\" style=\"margin: 0cm 0cm 0pt; text-align: center; line-height: 28.5pt; mso-line-height-rule: exactly;\"><strong><span style=\"font-size: 14pt;\"><span style=\"font-family:宋体;\">序号</span></span></strong></p><span style=\"font-family:宋体;font-size:16px;\">  </span></td><span style=\"font-family:宋体;\">  </span><td width=\"315\" style=\"border-width: 1pt 1pt 1pt 0px; border-style: solid solid solid none; border-color: windowtext windowtext black rgb(73, 73, 73); padding: 0cm 5.4pt; width: 236.3pt; height: 30.25pt; background-color: transparent