mapper.select:
select  from DS_CMS_CATEGORY
where ID=#{id} and SITEID=#{siteid}
order by SEQ

select  from DS_CMS_PAGE
where SITEID=#{siteid} 
	 and CATEGORYID in (${idArray}) 
	 and STATUS=#{status} 
	 and IMGTOP=1 
	 and PAGETOP=1 
	 and RELEASETIME<=#{releasetime} 
	 and (TITLE like #{keyvalue, typeHandler=LikeTypeHandler} or SUMMARY like #{keyvalue, typeHandler=LikeTypeHandler} or CONTENT like #{keyvalue, typeHandler=LikeTypeHandler}) 
order by RELEASETIME ${order}

select count(1) from DS_CMS_PAGE
where SITEID=#{siteid} 
	 and CATEGORYID in (${idArray}) 
	 and STATUS=#{status} 
	 and IMGTOP=1 
	 and PAGETOP=1 
	 and RELEASETIME<=#{releasetime} 
	 and (TITLE like #{keyvalue, typeHandler=LikeTypeHandler} or SUMMARY like #{keyvalue, typeHandler=LikeTypeHandler} or CONTENT like #{keyvalue, typeHandler=LikeTypeHandler})
