sql.where:
SITEID=#{siteid}
 and CATEGORYID=#{categoryid} 
 and STATUS=#{status} 

	 and TITLE like concat('%',#{keyvalue},'%') 
	 and METAKEYWORDS like concat('%',#{keyvalue},'%') 
	 and METADESCRIPTION like concat('%',#{keyvalue},'%') 
	 and SUMMARY like concat('%',#{keyvalue},'%') 
	 and CONTENT like concat('%',#{keyvalue},'%') 

 and RELEASETIME<=#{releasetime}
