mapper.select:
select  from DS_CMS_CATEGORY
where ID=#{id} and SITEID=#{siteid}
order by SEQ

select  from DS_CMS_PAGE
where SITEID=#{siteid} and CATEGORYID in (${idArray})
	 and IMGTOP=1 
	 and PAGETOP=1 
	 and RELEASETIME<=#{releasetime} 
order by RELEASETIME ${order}

select  from DS_CMS_CATEGORY
where SITEID=#{siteid}
	 and PID=#{pid} 
	 and PID is null 
order by SEQ
