mapper.select:
select id, qybm, name, folder, url, img, viewsite, viewapp
from DS_CMS_SITE
where ID=#{id}

select id, siteid, categoryid, title, url, keywords, summary, content, releasetime, img
from DS_CMS_PAGE
where ID=#{id} and SITEID=#{siteid}

select id, pid, siteid, name, folder, status, url, img, keywords, content, viewsite, viewapp, pageviewsite, pageviewapp
from DS_CMS_CATEGORY
where SITEID=#{siteid}
	 and PID=#{pid} 
	 and PID is null 
order by SEQ
