sql.where:
QYBM=#{qybm}
 and CATEGORYID=#{categoryid} 
 and TITLE like concat('%',#{title},'%') 
 and KEYWORDS like concat('%',#{keywords},'%') 
 and SUMMARY like concat('%',#{summary},'%') 
 and CONTENT like concat('%',#{content},'%') 
 and CREATETIME like concat('%',#{createtime},'%') 
 and IMGTOP=#{imgtop} 
 and PAGETOP=#{pagetop}
